/**
 * AI Assistant Page
 * AI鍔╂墜椤甸潰
 * 
 * Features:
 * - Chat interface with AI
 * - Code explanation
 * - Refactoring suggestions
 * - Security analysis
 */

import React, { useState, useRef, useEffect } from 'react';
import {
  Card,
  Typography,
  Space,
  Input,
  Button,
  Avatar,
  List,
  Tag,
  Tooltip,
  Select,
  message,
  Spin,
} from 'antd';
import {
  RobotOutlined,
  UserOutlined,
  SendOutlined,
  CodeOutlined,
  BulbOutlined,
  SafetyCertificateOutlined,
  ThunderboltOutlined,
  CopyOutlined,
  DeleteOutlined,
  PlusOutlined,
  BookOutlined,
  ToolOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';

const { Text, Paragraph } = Typography;
const { TextArea } = Input;

interface Message {
  id: string;
  role: 'user' | 'assistant';
  content: string;
  timestamp: Date;
  codeBlocks?: { language: string; code: string }[];
}

interface Conversation {
  id: string;
  title: string;
  lastMessage: string;
  timestamp: Date;
}

const quickPrompts = [
  { icon: <CodeOutlined />, label: 'Explain this code', prompt: 'Explain what this code does:' },
  { icon: <BulbOutlined />, label: 'Suggest improvements', prompt: 'How can I improve this code?' },
  { icon: <SafetyCertificateOutlined />, label: 'Security check', prompt: 'Check this code for security vulnerabilities:' },
  { icon: <ThunderboltOutlined />, label: 'Optimize performance', prompt: 'Optimize this code for better performance:' },
  { icon: <ToolOutlined />, label: 'Refactor', prompt: 'Refactor this code to be more maintainable:' },
  { icon: <BookOutlined />, label: 'Add documentation', prompt: 'Add documentation to this code:' },
];

const mockConversations: Conversation[] = [
  { id: '1', title: 'SQL Injection Fix', lastMessage: 'The parameterized query looks good...', timestamp: new Date(Date.now() - 30 * 60 * 1000) },
  { id: '2', title: 'React Hook Optimization', lastMessage: 'Consider using useMemo for...', timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000) },
  { id: '3', title: 'API Design Review', lastMessage: 'The REST endpoints follow...', timestamp: new Date(Date.now() - 24 * 60 * 60 * 1000) },
];

export const AIAssistant: React.FC = () => {
  const { t: _t } = useTranslation();
  const [messages, setMessages] = useState<Message[]>([
    {
      id: '1',
      role: 'assistant',
      content: "Hello! I'm your AI coding assistant. I can help you with:\n\n鈥?**Code explanation** - Understand complex code\n鈥?**Security analysis** - Find vulnerabilities\n鈥?**Refactoring** - Improve code quality\n鈥?**Performance optimization** - Make code faster\n\nPaste your code or ask me anything about your project!",
      timestamp: new Date(),
    },
  ]);
  const [inputValue, setInputValue] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [selectedModel, setSelectedModel] = useState('gpt-4-turbo');
  const messagesEndRef = useRef<HTMLDivElement>(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSend = async () => {
    if (!inputValue.trim()) return;

    const userMessage: Message = {
      id: Date.now().toString(),
      role: 'user',
      content: inputValue,
      timestamp: new Date(),
    };

    setMessages(prev => [...prev, userMessage]);
    setInputValue('');
    setIsLoading(true);

    // Simulate AI response
    setTimeout(() => {
      const aiResponse: Message = {
        id: (Date.now() + 1).toString(),
        role: 'assistant',
        content: generateMockResponse(inputValue),
        timestamp: new Date(),
        codeBlocks: inputValue.toLowerCase().includes('fix') ? [
          {
            language: 'python',
            code: `# Secure parameterized query
def get_user(user_id: str) -> User:
    query = "SELECT * FROM users WHERE id = %s"
    cursor.execute(query, (user_id,))
    return cursor.fetchone()`,
          },
        ] : undefined,
      };
      setMessages(prev => [...prev, aiResponse]);
      setIsLoading(false);
    }, 1500);
  };

  const generateMockResponse = (input: string): string => {
    if (input.toLowerCase().includes('security') || input.toLowerCase().includes('vulnerability')) {
      return "I've analyzed the code for security vulnerabilities. Here's what I found:\n\n**馃敶 Critical: SQL Injection**\nThe query uses string interpolation which is vulnerable to SQL injection attacks.\n\n**Recommendation:**\nUse parameterized queries instead of string formatting. See the code example below:";
    }
    if (input.toLowerCase().includes('explain')) {
      return "Here's an explanation of the code:\n\n**Purpose:**\nThis function handles user authentication by validating credentials against the database.\n\n**Flow:**\n1. Receives username and password\n2. Queries the database for matching user\n3. Verifies password hash\n4. Returns JWT token on success\n\n**Key points:**\n鈥?Uses bcrypt for password hashing\n鈥?Implements rate limiting for security\n鈥?Returns standardized error responses";
    }
    if (input.toLowerCase().includes('improve') || input.toLowerCase().includes('refactor')) {
      return "Here are my suggestions for improving this code:\n\n**1. Add Type Hints**\nAdding type hints improves code readability and enables better IDE support.\n\n**2. Extract Magic Numbers**\nReplace hardcoded values with named constants.\n\n**3. Add Error Handling**\nWrap database operations in try-catch blocks.\n\n**4. Use Dependency Injection**\nThis makes the code more testable.";
    }
    return "I understand your question. Based on my analysis of your code and best practices, here are my thoughts:\n\n鈥?The code structure follows good patterns\n鈥?Consider adding more unit tests\n鈥?Documentation could be improved\n\nWould you like me to elaborate on any of these points?";
  };

  const handleQuickPrompt = (prompt: string) => {
    setInputValue(prompt + '\n\n```\n// Paste your code here\n```');
  };

  const copyToClipboard = (text: string) => {
    navigator.clipboard.writeText(text);
    message.success('Copied to clipboard');
  };

  return (
    <div className="ai-assistant-page" style={{ height: 'calc(100vh - 120px)', display: 'flex', gap: 16 }}>
      {/* Sidebar - Conversation History */}
      <Card
        style={{ width: 280, borderRadius: 12, flexShrink: 0 }}
        bodyStyle={{ padding: 0 }}
      >
        <div style={{ padding: 16, borderBottom: '1px solid #f1f5f9' }}>
          <Button type="primary" block icon={<PlusOutlined />}>
            New Chat
          </Button>
        </div>
        <div style={{ padding: '8px 16px' }}>
          <Text type="secondary" style={{ fontSize: 12 }}>RECENT CONVERSATIONS</Text>
        </div>
        <List
          dataSource={mockConversations}
          renderItem={conv => (
            <List.Item
              style={{ padding: '12px 16px', cursor: 'pointer', borderBottom: '1px solid #f8fafc' }}
              className="conversation-item"
            >
              <div style={{ width: '100%' }}>
                <Text strong ellipsis style={{ display: 'block' }}>{conv.title}</Text>
                <Text type="secondary" style={{ fontSize: 12 }} ellipsis>
                  {conv.lastMessage}
                </Text>
              </div>
            </List.Item>
          )}
        />
      </Card>

      {/* Main Chat Area */}
      <Card
        style={{ flex: 1, borderRadius: 12, display: 'flex', flexDirection: 'column' }}
        bodyStyle={{ flex: 1, display: 'flex', flexDirection: 'column', padding: 0 }}
      >
        {/* Header */}
        <div style={{ padding: '12px 24px', borderBottom: '1px solid #f1f5f9', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <Space>
            <Avatar style={{ background: '#2563eb' }} icon={<RobotOutlined />} />
            <div>
              <Text strong>AI Code Assistant</Text>
              <div>
                <Tag color="green" style={{ fontSize: 11 }}>Online</Tag>
              </div>
            </div>
          </Space>
          <Space>
            <Select
              value={selectedModel}
              onChange={setSelectedModel}
              style={{ width: 150 }}
              size="small"
              options={[
                { value: 'gpt-4-turbo', label: 'GPT-4 Turbo' },
                { value: 'gpt-4', label: 'GPT-4' },
                { value: 'claude-3', label: 'Claude 3' },
              ]}
            />
            <Tooltip title="Clear conversation">
              <Button size="small" icon={<DeleteOutlined />} />
            </Tooltip>
          </Space>
        </div>

        {/* Messages */}
        <div style={{ flex: 1, overflow: 'auto', padding: 24 }}>
          {messages.map(msg => (
            <div
              key={msg.id}
              style={{
                display: 'flex',
                gap: 12,
                marginBottom: 24,
                flexDirection: msg.role === 'user' ? 'row-reverse' : 'row',
              }}
            >
              <Avatar
                style={{
                  background: msg.role === 'user' ? '#64748b' : '#2563eb',
                  flexShrink: 0,
                }}
                icon={msg.role === 'user' ? <UserOutlined /> : <RobotOutlined />}
              />
              <div
                style={{
                  maxWidth: '70%',
                  padding: 16,
                  borderRadius: 12,
                  background: msg.role === 'user' ? '#f1f5f9' : '#eff6ff',
                }}
              >
                <Paragraph style={{ margin: 0, whiteSpace: 'pre-wrap' }}>
                  {msg.content}
                </Paragraph>
                {msg.codeBlocks?.map((block, idx) => (
                  <div key={idx} style={{ marginTop: 12 }}>
                    <div style={{
                      background: '#1e293b',
                      borderRadius: '8px 8px 0 0',
                      padding: '8px 12px',
                      display: 'flex',
                      justifyContent: 'space-between',
                      alignItems: 'center',
                    }}>
                      <Text style={{ color: '#94a3b8', fontSize: 12 }}>{block.language}</Text>
                      <Button
                        type="text"
                        size="small"
                        icon={<CopyOutlined />}
                        style={{ color: '#94a3b8' }}
                        onClick={() => copyToClipboard(block.code)}
                      />
                    </div>
                    <pre style={{
                      background: '#0f172a',
                      padding: 16,
                      borderRadius: '0 0 8px 8px',
                      margin: 0,
                      overflow: 'auto',
                      color: '#e2e8f0',
                      fontSize: 13,
                    }}>
                      {block.code}
                    </pre>
                  </div>
                ))}
              </div>
            </div>
          ))}
          {isLoading && (
            <div style={{ display: 'flex', gap: 12, marginBottom: 24 }}>
              <Avatar style={{ background: '#2563eb' }} icon={<RobotOutlined />} />
              <div style={{ padding: 16 }}>
                <Spin size="small" /> <Text type="secondary">Thinking...</Text>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Quick Prompts */}
        <div style={{ padding: '12px 24px', borderTop: '1px solid #f1f5f9' }}>
          <Space wrap>
            {quickPrompts.map((prompt, idx) => (
              <Button
                key={idx}
                size="small"
                icon={prompt.icon}
                onClick={() => handleQuickPrompt(prompt.prompt)}
              >
                {prompt.label}
              </Button>
            ))}
          </Space>
        </div>

        {/* Input Area */}
        <div style={{ padding: '16px 24px', borderTop: '1px solid #f1f5f9' }}>
          <div style={{ display: 'flex', gap: 12 }}>
            <TextArea
              value={inputValue}
              onChange={e => setInputValue(e.target.value)}
              placeholder="Ask me anything about your code... (Shift+Enter for new line)"
              autoSize={{ minRows: 1, maxRows: 6 }}
              onPressEnter={e => {
                if (!e.shiftKey) {
                  e.preventDefault();
                  handleSend();
                }
              }}
              style={{ flex: 1 }}
            />
            <Button
              type="primary"
              icon={<SendOutlined />}
              onClick={handleSend}
              loading={isLoading}
              style={{ alignSelf: 'flex-end' }}
            >
              Send
            </Button>
          </div>
        </div>
      </Card>

      <style>{`
        .conversation-item:hover {
          background: #f8fafc !important;
        }
      `}</style>
    </div>
  );
};

export default AIAssistant;

/**
 * Activity Feed Page
 * 娲诲姩鍔ㄦ€侀〉闈r
 * 
 * VSCode/GitHub-inspired activity timeline with:
 * - Real-time activity updates
 * - Filterable by type, user, project
 * - Rich activity cards with context
 */

import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Space,
  Avatar,
  Tag,
  Button,
  Select,
  Input,
  Tooltip,
  Badge,
  Empty,
} from 'antd';
import {
  ClockCircleOutlined,
  CodeOutlined,
  UserOutlined,
  TeamOutlined,
  BranchesOutlined,
  MergeOutlined,
  CommentOutlined,
  EyeOutlined,
  FilterOutlined,
  ReloadOutlined,
  BellOutlined,
  RocketOutlined,
  SafetyCertificateOutlined,
  SettingOutlined,
  StarOutlined,
  StarFilled,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';

const { Title, Text } = Typography;

interface Activity {
  id: string;
  type: 'analysis' | 'review' | 'comment' | 'merge' | 'deploy' | 'security' | 'config';
  action: string;
  description: string;
  user: {
    name: string;
    avatar?: string;
  };
  project?: string;
  branch?: string;
  file?: string;
  metadata?: Record<string, any>;
  timestamp: string;
  isNew?: boolean;
}

const mockActivities: Activity[] = [
  {
    id: 'act_1',
    type: 'analysis',
    action: 'completed',
    description: 'Code analysis completed for frontend/src/components',
    user: { name: 'AI Assistant' },
    project: 'Frontend',
    branch: 'feature/new-dashboard',
    metadata: { issues: 3, warnings: 12, duration: '2.3s' },
    timestamp: new Date(Date.now() - 5 * 60 * 1000).toISOString(),
    isNew: true,
  },
  {
    id: 'act_2',
    type: 'security',
    action: 'detected',
    description: 'Critical vulnerability detected: SQL Injection',
    user: { name: 'Security Scanner' },
    project: 'Backend',
    file: 'src/auth/login.py',
    metadata: { severity: 'critical', cve: 'CVE-2024-1234' },
    timestamp: new Date(Date.now() - 15 * 60 * 1000).toISOString(),
    isNew: true,
  },
  {
    id: 'act_3',
    type: 'review',
    action: 'approved',
    description: 'Approved pull request: Add user authentication',
    user: { name: 'John Doe' },
    project: 'Backend',
    branch: 'feature/auth',
    timestamp: new Date(Date.now() - 30 * 60 * 1000).toISOString(),
  },
  {
    id: 'act_4',
    type: 'merge',
    action: 'merged',
    description: 'Merged feature/auth into main',
    user: { name: 'Jane Smith' },
    project: 'Backend',
    branch: 'main',
    timestamp: new Date(Date.now() - 45 * 60 * 1000).toISOString(),
  },
  {
    id: 'act_5',
    type: 'comment',
    action: 'commented',
    description: 'Suggested using parameterized queries instead',
    user: { name: 'Bob Wilson' },
    project: 'Backend',
    file: 'src/api/users.py:45',
    timestamp: new Date(Date.now() - 60 * 60 * 1000).toISOString(),
  },
  {
    id: 'act_6',
    type: 'deploy',
    action: 'deployed',
    description: 'Deployed v2.1.0 to production',
    user: { name: 'CI/CD Pipeline' },
    project: 'Frontend',
    metadata: { version: 'v2.1.0', environment: 'production' },
    timestamp: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
  },
  {
    id: 'act_7',
    type: 'config',
    action: 'updated',
    description: 'Updated AI model configuration',
    user: { name: 'Admin' },
    metadata: { model: 'GPT-4 Turbo', temperature: 0.7 },
    timestamp: new Date(Date.now() - 3 * 60 * 60 * 1000).toISOString(),
  },
  {
    id: 'act_8',
    type: 'analysis',
    action: 'started',
    description: 'Started deep analysis for entire repository',
    user: { name: 'AI Assistant' },
    project: 'Mobile App',
    metadata: { files: 156, estimatedTime: '5 min' },
    timestamp: new Date(Date.now() - 4 * 60 * 60 * 1000).toISOString(),
  },
];

const activityConfig = {
  analysis: { 
    icon: <CodeOutlined />, 
    color: '#2563eb',
    bgColor: 'rgba(37, 99, 235, 0.1)',
  },
  review: { 
    icon: <EyeOutlined />, 
    color: '#8b5cf6',
    bgColor: 'rgba(139, 92, 246, 0.1)',
  },
  comment: { 
    icon: <CommentOutlined />, 
    color: '#06b6d4',
    bgColor: 'rgba(6, 182, 212, 0.1)',
  },
  merge: { 
    icon: <MergeOutlined />, 
    color: '#10b981',
    bgColor: 'rgba(16, 185, 129, 0.1)',
  },
  deploy: { 
    icon: <RocketOutlined />, 
    color: '#f59e0b',
    bgColor: 'rgba(245, 158, 11, 0.1)',
  },
  security: { 
    icon: <SafetyCertificateOutlined />, 
    color: '#ef4444',
    bgColor: 'rgba(239, 68, 68, 0.1)',
  },
  config: { 
    icon: <SettingOutlined />, 
    color: '#64748b',
    bgColor: 'rgba(100, 116, 139, 0.1)',
  },
};

const formatTimeAgo = (timestamp: string): string => {
  const now = Date.now();
  const time = new Date(timestamp).getTime();
  const diff = now - time;
  
  const minutes = Math.floor(diff / 60000);
  const hours = Math.floor(diff / 3600000);
  const days = Math.floor(diff / 86400000);
  
  if (minutes < 1) return 'Just now';
  if (minutes < 60) return `${minutes}m ago`;
  if (hours < 24) return `${hours}h ago`;
  return `${days}d ago`;
};

export const ActivityFeed: React.FC = () => {
  const { t } = useTranslation();
  const [activities, _setActivities] = useState<Activity[]>(mockActivities);
  const [filter, setFilter] = useState<string>('all');
  const [searchQuery, setSearchQuery] = useState('');
  const [starred, setStarred] = useState<Set<string>>(new Set());

  const filteredActivities = activities.filter(activity => {
    if (filter !== 'all' && activity.type !== filter) return false;
    if (searchQuery) {
      const query = searchQuery.toLowerCase();
      return (
        activity.description.toLowerCase().includes(query) ||
        activity.user.name.toLowerCase().includes(query) ||
        activity.project?.toLowerCase().includes(query)
      );
    }
    return true;
  });

  const toggleStar = (id: string) => {
    setStarred(prev => {
      const newSet = new Set(prev);
      if (newSet.has(id)) {
        newSet.delete(id);
      } else {
        newSet.add(id);
      }
      return newSet;
    });
  };

  const newCount = activities.filter(a => a.isNew).length;

  return (
    <div className="activity-feed-page" style={{ maxWidth: 1200, margin: '0 auto' }}>
      {/* Header */}
      <div className="page-header" style={{ marginBottom: 24 }}>
        <div>
          <Title level={3} style={{ margin: 0, display: 'flex', alignItems: 'center', gap: 12 }}>
            <BellOutlined style={{ color: '#2563eb' }} />
            {t('activity.title', 'Activity Feed')}
            {newCount > 0 && (
              <Badge count={newCount} style={{ marginLeft: 8 }} />
            )}
          </Title>
          <Text type="secondary">
            {t('activity.subtitle', 'Track all activities across your projects')}
          </Text>
        </div>
        <Space>
          <Input.Search
            placeholder="Search activities..."
            style={{ width: 250 }}
            value={searchQuery}
            onChange={e => setSearchQuery(e.target.value)}
            allowClear
          />
          <Select
            value={filter}
            onChange={setFilter}
            style={{ width: 150 }}
            options={[
              { value: 'all', label: 'All Types' },
              { value: 'analysis', label: 'Analysis' },
              { value: 'review', label: 'Reviews' },
              { value: 'security', label: 'Security' },
              { value: 'deploy', label: 'Deployments' },
              { value: 'merge', label: 'Merges' },
            ]}
          />
          <Button icon={<ReloadOutlined />}>Refresh</Button>
        </Space>
      </div>

      {/* Activity Timeline */}
      <Row gutter={24}>
        <Col xs={24} lg={16}>
          <Card 
            bodyStyle={{ padding: 0 }}
            style={{ 
              borderRadius: 16,
              overflow: 'hidden',
              border: '1px solid #e2e8f0',
            }}
          >
            {filteredActivities.length > 0 ? (
              <div>
                {filteredActivities.map((activity, index) => {
                  const config = activityConfig[activity.type];
                  return (
                    <div
                      key={activity.id}
                      style={{
                        padding: '20px 24px',
                        borderBottom: index < filteredActivities.length - 1 ? '1px solid #f1f5f9' : 'none',
                        background: activity.isNew ? 'rgba(37, 99, 235, 0.03)' : 'transparent',
                        transition: 'background 0.2s',
                      }}
                      className="activity-item"
                    >
                      <div style={{ display: 'flex', gap: 16 }}>
                        {/* Icon */}
                        <div
                          style={{
                            width: 42,
                            height: 42,
                            borderRadius: 12,
                            background: config.bgColor,
                            display: 'flex',
                            alignItems: 'center',
                            justifyContent: 'center',
                            color: config.color,
                            fontSize: 18,
                            flexShrink: 0,
                          }}
                        >
                          {config.icon}
                        </div>

                        {/* Content */}
                        <div style={{ flex: 1, minWidth: 0 }}>
                          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'flex-start' }}>
                            <div>
                              <Text strong style={{ fontSize: 14 }}>
                                {activity.description}
                              </Text>
                              {activity.isNew && (
                                <Tag color="blue" style={{ marginLeft: 8, fontSize: 11 }}>NEW</Tag>
                              )}
                            </div>
                            <Space size={4}>
                              <Tooltip title={starred.has(activity.id) ? 'Unstar' : 'Star'}>
                                <Button
                                  type="text"
                                  size="small"
                                  icon={starred.has(activity.id) ? 
                                    <StarFilled style={{ color: '#f59e0b' }} /> : 
                                    <StarOutlined />
                                  }
                                  onClick={() => toggleStar(activity.id)}
                                />
                              </Tooltip>
                              <Text type="secondary" style={{ fontSize: 12 }}>
                                {formatTimeAgo(activity.timestamp)}
                              </Text>
                            </Space>
                          </div>

                          {/* Metadata */}
                          <div style={{ marginTop: 8, display: 'flex', flexWrap: 'wrap', gap: 8 }}>
                            <Space size={4}>
                              <Avatar size={20} icon={<UserOutlined />} src={activity.user.avatar} />
                              <Text type="secondary" style={{ fontSize: 13 }}>
                                {activity.user.name}
                              </Text>
                            </Space>
                            
                            {activity.project && (
                              <Tag 
                                style={{ 
                                  borderRadius: 6,
                                  background: '#f1f5f9',
                                  border: 'none',
                                  color: '#475569',
                                }}
                              >
                                <BranchesOutlined style={{ marginRight: 4 }} />
                                {activity.project}
                              </Tag>
                            )}
                            
                            {activity.branch && (
                              <Tag
                                style={{ 
                                  borderRadius: 6,
                                  background: '#eff6ff',
                                  border: 'none',
                                  color: '#2563eb',
                                }}
                              >
                                {activity.branch}
                              </Tag>
                            )}

                            {activity.file && (
                              <Text code style={{ fontSize: 12 }}>{activity.file}</Text>
                            )}

                            {activity.metadata?.severity === 'critical' && (
                              <Tag color="red">CRITICAL</Tag>
                            )}

                            {activity.metadata?.version && (
                              <Tag color="green">{activity.metadata.version}</Tag>
                            )}
                          </div>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            ) : (
              <Empty 
                description="No activities found" 
                style={{ padding: 48 }}
              />
            )}
          </Card>
        </Col>

        {/* Sidebar Stats */}
        <Col xs={24} lg={8}>
          {/* Activity Summary */}
          <Card 
