import { UserOutlined } from '@ant-design/icons';

export default CodeReview;

/**
 * Deployments Page
 * 閮ㄧ讲绠＄悊椤甸潰
 * 
 * CI/CD Pipeline visualization with:
 * - Deployment history
 * - Pipeline status
 * - Rollback capability
 * - Environment management
 */

import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Table,
  Button,
  Tag,
  Space,
  Typography,
  Avatar,
  Badge,
  Tooltip,
  Select,
  Timeline,
  Progress,
  Modal,
  Statistic,
  Steps,
  Alert,
  Descriptions,
  message,
} from 'antd';
import type { TableProps } from 'antd';
import {
  RocketOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  SyncOutlined,
  ClockCircleOutlined,
  RollbackOutlined,
  EyeOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  EnvironmentOutlined,
  BranchesOutlined,
  UserOutlined,
  HistoryOutlined,
  CloudServerOutlined,
  ThunderboltOutlined,
  SafetyCertificateOutlined,
  CodeOutlined,
  BuildOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';

const { Title, Text, Paragraph } = Typography;

interface Deployment {
