/**
 * Evolution Cycle Dashboard
 * 
 * Monitors the three-version self-evolution cycle:
 * - V1 Experimentation: New technology testing
 * - V2 Production: Stable user-facing AI
 * - V3 Quarantine: Failed experiment archive
 */

import React, { useState, useEffect, useCallback } from 'react';
import {
  Card,
  Row,
  Col,
  Table,
  Tag,
  Button,
  Space,
  Progress,
  Tabs,
  Badge,
  Statistic,
  Timeline,
  Typography,
  Select,
  Spin,
  Tooltip,
  Descriptions,
  Alert,
  message,
  Divider,
} from 'antd';
import {
  ExperimentOutlined,
  RocketOutlined,
  StopOutlined,
  ArrowUpOutlined,
  ArrowDownOutlined,
  ReloadOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ClockCircleOutlined,
  SyncOutlined,
  ThunderboltOutlined,
  SafetyOutlined,
  BulbOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import { useTranslation } from 'react-i18next';

const { Title, Text, Paragraph } = Typography;
const { TabPane } = Tabs;

// Types
interface Technology {
  tech_id: string;
  name: string;
  category: string;
  description: string;
  source: string;
  version: 'v1' | 'v2' | 'v3';
  status: 'experimental' | 'promoted' | 'quarantined' | 're-evaluating';
  metrics: {
    accuracy?: number;
    error_rate?: number;
    latency_p95_ms?: number;
    sample_count?: number;
  };
  created_at: string;
}

interface Experiment {
  experiment_id: string;
  name: string;
  technology_type: string;
  category: string;
  status: 'pending' | 'running' | 'evaluating' | 'completed' | 'failed' | 'promoted' | 'quarantined';
  samples_collected: number;
  min_samples: number;
  accuracy: number;
  error_rate: number;
  latency_p95: number;
  started_at: string;
  recommendation?: string;
}

interface VersionMetrics {
  request_count: number;
  error_count: number;
  error_rate: number;
  avg_latency_ms: number;
  technology_count: number;
}

interface PromotionRecord {
  record_id: string;
  from_version: string;
  to_version: string;
  technology_name: string;
  reason: string;
  timestamp: string;
}

interface CycleStatus {
  running: boolean;
  cycle_count: number;
  last_cycle_at: string;
  promotions: number;
  degradations: number;
  v1_metrics: VersionMetrics;
  v2_metrics: VersionMetrics;
  v3_metrics: VersionMetrics;
}

// Version colors
const versionColors: Record<string, string> = {
  v1: 'orange',
  v2: 'green',
  v3: 'red',
};

const versionIcons: Record<string, React.ReactNode> = {
  v1: <ExperimentOutlined />,
  v2: <RocketOutlined />,
  v3: <StopOutlined />,
};

// Status colors
const statusColors: Record<string, string> = {
  pending: 'default',
  running: 'processing',
  evaluating: 'warning',
  completed: 'success',
  failed: 'error',
  promoted: 'green',
  quarantined: 'red',
  experimental: 'orange',
  're-evaluating': 'blue',
};

