const { Title, Text, Paragraph } = Typography;

interface Notification {
  id: string;
  type: 'security' | 'review' | 'deployment' | 'team' | 'system' | 'ai';
  priority: 'high' | 'medium' | 'low';
  title: string;
  message: string;
  read: boolean;
  timestamp: string;
  actionUrl?: string;
  actionLabel?: string;
}

const mockNotifications: Notification[] = [
