/**
 * Register Page Component
 * 娉ㄥ唽椤甸潰缁勪欢
 * 
 * This component handles new user registration with invitation code validation.
 * 姝ょ粍浠跺鐞嗘柊鐢ㄦ埛娉ㄥ唽锛屽寘鍚個璇风爜楠岃瘉鍔熻兘銆俓r
 * 
 * Features / 鍔熻兘:
 * - Email and password validation / 閭鍜屽瘑鐮侀獙璇乗r
 * - Invitation code requirement / 閭€璇风爜瑕佹眰
 * - Password strength indicator / 瀵嗙爜寮哄害鎸囩ず鍣╘r
 * - Form validation with i18n / 甯﹀浗闄呭寲鐨勮〃鍗曢獙璇乗r
 */

import React, { useState, useMemo } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { 
  Form, 
  Input, 
  Button, 
  Card, 
  Typography, 
  Alert, 
  Divider,
  Progress,
  Space
} from 'antd';
import { 
  UserOutlined, 
  LockOutlined, 
  KeyOutlined, 
  MailOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import { useAuth } from '../hooks/useAuth';
import './Register.css';

const { Title, Text } = Typography;

/**
 * Registration form values interface
 * 娉ㄥ唽琛ㄥ崟鍊兼帴鍙r
 */
interface RegisterFormValues {
  name: string;           // 鐢ㄦ埛鍚?/ Username
  email: string;          // 閭 / Email
  password: string;       // 瀵嗙爜 / Password
  confirmPassword: string; // 纭瀵嗙爜 / Confirm password
  invitation_code: string; // 閭€璇风爜 / Invitation code
}

/**
 * Password strength calculation
 * 瀵嗙爜寮哄害璁＄畻
 * 
 * @param password - The password to evaluate / 瑕佽瘎浼扮殑瀵嗙爜
 * @returns Strength score (0-100) and status / 寮哄害鍒嗘暟(0-100)鍜岀姸鎬乗r
 */
const calculatePasswordStrength = (password: string): { score: number; status: 'exception' | 'normal' | 'success' } => {
  let score = 0;
  
  // 闀垮害妫€鏌?/ Length check
  if (password.length >= 8) score += 25;
  if (password.length >= 12) score += 15;
  
  // 鍖呭惈灏忓啓瀛楁瘝 / Contains lowercase
  if (/[a-z]/.test(password)) score += 15;
  
  // 鍖呭惈澶у啓瀛楁瘝 / Contains uppercase
  if (/[A-Z]/.test(password)) score += 15;
  
  // 鍖呭惈鏁板瓧 / Contains number
  if (/[0-9]/.test(password)) score += 15;
  
  // 鍖呭惈鐗规畩瀛楃 / Contains special character
  if (/[!@#$%^&*(),.?":{}|<>]/.test(password)) score += 15;
  
  // 纭畾鐘舵€?/ Determine status
  const status = score < 40 ? 'exception' : score < 70 ? 'normal' : 'success';
  
  return { score: Math.min(score, 100), status };
};

/**
 * Register Component
 * 娉ㄥ唽缁勪欢
 */
export const Register: React.FC = () => {
  // 鍥介檯鍖栭挬瀛?/ Internationalization hook
  const { t } = useTranslation();
  
  // 璺敱瀵艰埅閽╁瓙 / Router navigation hook
  const navigate = useNavigate();
  
  // 璁よ瘉閽╁瓙 / Authentication hook
  const { register, isLoading, error } = useAuth();
  
  // 瀵嗙爜鐘舵€佺敤浜庡己搴﹁绠?/ Password state for strength calculation
  const [password, setPassword] = useState('');
  
  // 琛ㄥ崟瀹炰緥 / Form instance
  const [form] = Form.useForm();

  /**
   * Calculate password strength memo
   * 璁＄畻瀵嗙爜寮哄害鐨勭紦瀛樺€糪r
   */
  const passwordStrength = useMemo(() => {
    return calculatePasswordStrength(password);
  }, [password]);

  /**
   * Password validation rules
   * 瀵嗙爜楠岃瘉瑙勫垯鍒楄〃
   */
  const passwordRules = useMemo(() => [
    { 
      valid: password.length >= 8, 
      text: t('register.password_length', 'At least 8 characters / 鑷冲皯8涓瓧绗?) 
    },
    { 
      valid: /[a-z]/.test(password), 
      text: t('register.password_lowercase', 'One lowercase letter / 涓€涓皬鍐欏瓧姣?) 
    },
    { 
      valid: /[A-Z]/.test(password), 
      text: t('register.password_uppercase', 'One uppercase letter / 涓€涓ぇ鍐欏瓧姣?) 
    },
    { 
      valid: /[0-9]/.test(password), 
      text: t('register.password_number', 'One number / 涓€涓暟瀛?) 
    },
  ], [password, t]);

  /**
   * Handle form submission
   * 澶勭悊琛ㄥ崟鎻愪氦
   * 
   * @param values - Form values / 琛ㄥ崟鍊糪r
   */
  const onFinish = async (values: RegisterFormValues) => {
    // 璋冪敤娉ㄥ唽API / Call register API
    const success = await register({
      name: values.name,
      email: values.email,
      password: values.password,
      invitation_code: values.invitation_code,
    });
    
    // 娉ㄥ唽鎴愬姛鍚庤烦杞埌浠〃鏉?/ Navigate to dashboard on success
    if (success) {
      navigate('/dashboard');
    }
  };

  return (
    <div className="register-container">
      {/* 鑳屾櫙瑁呴グ / Background decoration */}
      <div className="register-background" />
      
      <Card className="register-card">
        {/* 椤甸潰鏍囬 / Page header */}
        <div className="register-header">
          <Title level={2}>{t('register.title', 'Create Account')}</Title>
          <Text type="secondary">
            {t('register.subtitle', 'Join the AI Code Review Platform')}
          </Text>
        </div>

        {/* 閿欒鎻愮ず / Error alert */}
        {error && (
          <Alert
            message={typeof error === 'string' ? error : (error as any)?.message || (error as any)?.msg || t('register.error', 'Registration failed')}
            type="error"
            showIcon
            closable
            className="register-error"
          />
        )}

        {/* 娉ㄥ唽琛ㄥ崟 / Registration form */}
        <Form
          form={form}
          name="register"
          onFinish={onFinish}
          layout="vertical"
          size="large"
          requiredMark={false}
        >
          {/* 鐢ㄦ埛鍚嶈緭鍏?/ Name input */}
          <Form.Item
            name="name"
            rules={[
              { required: true, message: t('register.name_required', 'Please enter your name') },
              { min: 2, message: t('register.name_min', 'Name must be at least 2 characters') },
              { max: 50, message: t('register.name_max', 'Name cannot exceed 50 characters') }
            ]}
          >
            <Input
              prefix={<UserOutlined />}
              placeholder={t('register.name_placeholder', 'Full Name / 濮撳悕')}
              autoComplete="name"
            />
          </Form.Item>

          {/* 閭杈撳叆 / Email input */}
          <Form.Item
            name="email"
            rules={[
              { required: true, message: t('register.email_required', 'Please enter your email') },
              { type: 'email', message: t('register.email_invalid', 'Please enter a valid email') }
            ]}
          >
            <Input
              prefix={<MailOutlined />}
              placeholder={t('register.email_placeholder', 'Email / 閭')}
              autoComplete="email"
            />
          </Form.Item>

          {/* 瀵嗙爜杈撳叆 / Password input */}
          <Form.Item
            name="password"
            rules={[
              { required: true, message: t('register.password_required', 'Please enter a password') },
              { min: 8, message: t('register.password_min', 'Password must be at least 8 characters') }
            ]}
          >
            <Input.Password
              prefix={<LockOutlined />}
              placeholder={t('register.password_placeholder', 'Password / 瀵嗙爜')}
              autoComplete="new-password"
              onChange={(e) => setPassword(e.target.value)}
            />
          </Form.Item>

          {/* 瀵嗙爜寮哄害鎸囩ず鍣?/ Password strength indicator */}
          {password && (
            <div className="password-strength">
              <Progress 
                percent={passwordStrength.score} 
                status={passwordStrength.status}
                showInfo={false}
                size="small"
              />
              <Space direction="vertical" size={4} className="password-rules">
                {passwordRules.map((rule, index) => (
                  <Text 
                    key={index} 
                    type={rule.valid ? 'success' : 'secondary'}
                    className="password-rule"
                  >
                    {rule.valid ? <CheckCircleOutlined /> : <CloseCircleOutlined />}
                    {' '}{rule.text}
                  </Text>
                ))}
              </Space>
            </div>
          )}

          {/* 纭瀵嗙爜杈撳叆 / Confirm password input */}
          <Form.Item
            name="confirmPassword"
            dependencies={['password']}
            rules={[
              { required: true, message: t('register.confirm_required', 'Please confirm your password') },
              ({ getFieldValue }) => ({
                validator(_, value) {
                  if (!value || getFieldValue('password') === value) {
                    return Promise.resolve();
                  }
                  return Promise.reject(new Error(t('register.password_mismatch', 'Passwords do not match')));
                },
              }),
            ]}
          >
            <Input.Password
              prefix={<LockOutlined />}
              placeholder={t('register.confirm_placeholder', 'Confirm Password / 纭瀵嗙爜')}
              autoComplete="new-password"
            />
          </Form.Item>

          {/* 閭€璇风爜杈撳叆 / Invitation code input */}
          <Form.Item
            name="invitation_code"
            rules={[
              { required: true, message: t('register.invitation_required', 'Invitation code is required') }
            ]}
            extra={
              <Text type="secondary" style={{ fontSize: 12 }}>
                {t('register.invitation_help', 'Contact admin to get an invitation code / 鑱旂郴绠＄悊鍛樿幏鍙栭個璇风爜')}
              </Text>
            }
          >
            <Input
              prefix={<KeyOutlined />}
              placeholder={t('register.invitation_placeholder', 'Invitation Code / 閭€璇风爜')}
            />
          </Form.Item>

          {/* 鎻愪氦鎸夐挳 / Submit button */}
          <Form.Item>
            <Button
              type="primary"
              htmlType="submit"
              loading={isLoading}
              block
            >
              {t('register.submit', 'Create Account / 鍒涘缓璐︽埛')}
            </Button>
          </Form.Item>
        </Form>

        {/* 椤佃剼閾炬帴 / Footer links */}
        <div className="register-footer">
          <Divider plain>{t('register.or', 'or')}</Divider>
          
          <Text>
            {t('register.have_account', 'Already have an account?')}{' '}
            <Link to="/login">{t('register.login', 'Sign In / 鐧诲綍')}</Link>
          </Text>
        </div>
      </Card>
    </div>
  );
};

export default Register;

/**
 * Reports Page
 * 鎶ュ憡椤甸潰
 * 
 * Features:
 * - Generate code review reports
 * - Security scan reports
 * - Compliance reports
 * - Export to PDF/CSV
 * - Scheduled reports
