import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Table,
  Button,
  Space,
  Typography,
  Modal,
  Form,
  Input,
  Select,
  DatePicker,
  Tag,
  Tooltip,
  message,
  Popconfirm,
  Progress,
  Tabs,
  List,
  Badge,
  Dropdown,
  Divider,
  Empty,
  Switch,
  Radio,
} from 'antd';
import type { TableProps, MenuProps } from 'antd';
import {
  FileTextOutlined,
  PlusOutlined,
  DownloadOutlined,
  DeleteOutlined,
  EyeOutlined,
  FilePdfOutlined,
  FileExcelOutlined,
  ScheduleOutlined,
  ClockCircleOutlined,
  CheckCircleOutlined,
  SyncOutlined,
  MailOutlined,
  MoreOutlined,
  BarChartOutlined,
  SafetyCertificateOutlined,
  BugOutlined,
  PlayCircleOutlined,
