import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Space,
  Tag,
  Input,
  Collapse,
  Divider,
  Alert,
} from 'antd';
import {
  ThunderboltOutlined,
  SearchOutlined,
  CodeOutlined,
  FileOutlined,
  SettingOutlined,
