        </Col>
        <Col xs={12} sm={6}>
          <Card style={{ borderRadius: 12 }}>
            <Statistic
              title="Avg Response"
              value={Math.round(services.reduce((a, s) => a + s.responseTime, 0) / services.length)}
              suffix="ms"
              prefix={<ThunderboltOutlined />}
