export const TeamManagement: React.FC = () => {
  const { t } = useTranslation();
  const [teams, setTeams] = useState<Team[]>(mockTeams);
  const [selectedTeam, setSelectedTeam] = useState<Team | null>(mockTeams[0]);
  const [loading, setLoading] = useState(false);
  const [createTeamModalOpen, setCreateTeamModalOpen] = useState(false);
  const [inviteModalOpen, setInviteModalOpen] = useState(false);
  const [editMemberModalOpen, setEditMemberModalOpen] = useState(false);
  const [selectedMember, setSelectedMember] = useState<TeamMember | null>(null);
  const [teamForm] = Form.useForm();
  const [inviteForm] = Form.useForm();
  const [memberForm] = Form.useForm();

  // Create team
  const handleCreateTeam = async (values: any) => {
    try {
      await api.post('/api/teams', values);
      message.success('Team created successfully');
    } catch (error) {
      message.success('Team created successfully (demo)');
      const newTeam: Team = {
        id: `team_${Date.now()}`,
        name: values.name,
        description: values.description,
        memberCount: 1,
        projectCount: 0,
        createdAt: new Date().toISOString(),
        members: [{
          id: 'current_user',
          name: 'You',
          email: 'you@example.com',
          role: 'owner',
          status: 'active',
          joinedAt: new Date().toISOString(),
          projectCount: 0,
        }],
      };
      setTeams(prev => [...prev, newTeam]);
    }
    setCreateTeamModalOpen(false);
    teamForm.resetFields();
  };

  // Invite member
  const handleInvite = async (values: any) => {
    if (!selectedTeam) return;
    try {
      await api.post(`/api/teams/${selectedTeam.id}/invite`, values);
      message.success('Invitation sent');
    } catch (error) {
      message.success('Invitation sent (demo)');
      const newMember: TeamMember = {
        id: `user_${Date.now()}`,
        name: values.email.split('@')[0],
        email: values.email,
        role: values.role,
        status: 'pending',
        joinedAt: new Date().toISOString(),
        projectCount: 0,
      };
      setTeams(prev => prev.map(t => 
        t.id === selectedTeam.id 
          ? { ...t, members: [...t.members, newMember], memberCount: t.memberCount + 1 }
          : t
      ));
      setSelectedTeam(prev => prev ? { ...prev, members: [...prev.members, newMember] } : null);
    }
    setInviteModalOpen(false);
    inviteForm.resetFields();
  };

  // Update member role
  const handleUpdateMember = async (values: any) => {
    if (!selectedTeam || !selectedMember) return;
    try {
      await api.put(`/api/teams/${selectedTeam.id}/members/${selectedMember.id}`, values);
      message.success('Member updated');
    } catch (error) {
      message.success('Member updated (demo)');
      setTeams(prev => prev.map(t => 
        t.id === selectedTeam.id 
          ? { 
              ...t, 
              members: t.members.map(m => 
                m.id === selectedMember.id ? { ...m, ...values } : m
              )
            }
          : t
      ));
    }
    setEditMemberModalOpen(false);
    setSelectedMember(null);
    memberForm.resetFields();
  };

  // Remove member
  const handleRemoveMember = async (memberId: string) => {
    if (!selectedTeam) return;
    try {
      await api.delete(`/api/teams/${selectedTeam.id}/members/${memberId}`);
      message.success('Member removed');
    } catch (error) {
      message.success('Member removed (demo)');
    }
    setTeams(prev => prev.map(t => 
      t.id === selectedTeam.id 
        ? { ...t, members: t.members.filter(m => m.id !== memberId), memberCount: t.memberCount - 1 }
        : t
    ));
    setSelectedTeam(prev => prev ? { ...prev, members: prev.members.filter(m => m.id !== memberId) } : null);
  };

  const memberColumns: TableProps<TeamMember>['columns'] = [
    {
      title: 'Member',
      key: 'member',
      render: (_, record) => (
        <Space>
          <Avatar src={record.avatar} icon={<UserOutlined />} />
          <div>
            <Text strong>{record.name}</Text>
            <br />
            <Text type="secondary" style={{ fontSize: 12 }}>{record.email}</Text>
          </div>
        </Space>
      ),
    },
    {
      title: 'Role',
      dataIndex: 'role',
      width: 120,
      render: (role) => (
        <Tag color={roleColors[role]} icon={roleIcons[role]}>
          {role.charAt(0).toUpperCase() + role.slice(1)}
        </Tag>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      width: 100,
      render: (status) => (
        <Badge
          status={status === 'active' ? 'success' : status === 'pending' ? 'warning' : 'default'}
          text={status.charAt(0).toUpperCase() + status.slice(1)}
        />
      ),
    },
    {
      title: 'Projects',
      dataIndex: 'projectCount',
      width: 100,
      render: (count) => <Text>{count} projects</Text>,
    },
    {
      title: 'Last Active',
      dataIndex: 'lastActive',
      width: 150,
      render: (date) => date 
        ? new Date(date).toLocaleDateString()
        : <Text type="secondary">Never</Text>,
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 120,
      render: (_, record) => {
        if (record.role === 'owner') return null;
        
        const items: MenuProps['items'] = [
          {
            key: 'edit',
            icon: <EditOutlined />,
            label: 'Edit Role',
            onClick: () => {
              setSelectedMember(record);
              memberForm.setFieldsValue({ role: record.role });
              setEditMemberModalOpen(true);
            },
          },
          { type: 'divider' },
          {
            key: 'remove',
            icon: <DeleteOutlined />,
            label: 'Remove',
            danger: true,
            onClick: () => handleRemoveMember(record.id),
          },
        ];
        
        return (
          <Dropdown menu={{ items }} trigger={['click']}>
            <Button icon={<MoreOutlined />} />
          </Dropdown>
        );
      },
    },
  ];

  return (
    <div className="team-management">
      {/* Header */}
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 24 }}>
        <div>
          <Title level={3}>
            <TeamOutlined /> {t('team.title', 'Team Management')}
          </Title>
          <Text type="secondary">
            {t('team.subtitle', 'Manage your teams and members')}
          </Text>
        </div>
        <Button type="primary" icon={<PlusOutlined />} onClick={() => setCreateTeamModalOpen(true)}>
          Create Team
        </Button>
      </div>

      <Row gutter={[16, 16]}>
        {/* Teams List */}
        <Col xs={24} md={8}>
          <Card title="Your Teams" bodyStyle={{ padding: 0 }}>
            <List
              dataSource={teams}
              renderItem={team => (
                <List.Item
                  style={{
                    padding: 16,
                    cursor: 'pointer',
                    backgroundColor: selectedTeam?.id === team.id ? '#f5f5f5' : 'transparent',
                  }}
                  onClick={() => setSelectedTeam(team)}
                >
                  <List.Item.Meta
                    avatar={<Avatar icon={<TeamOutlined />} style={{ backgroundColor: '#1890ff' }} />}
                    title={team.name}
                    description={
                      <Space split={<Divider type="vertical" />}>
                        <Text type="secondary">{team.memberCount} members</Text>
                        <Text type="secondary">{team.projectCount} projects</Text>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>

        {/* Team Details */}
        <Col xs={24} md={16}>
          {selectedTeam ? (
            <Card
              title={
                <Space>
                  <TeamOutlined />
                  {selectedTeam.name}
                </Space>
              }
              extra={
                <Space>
                  <Button icon={<UserAddOutlined />} onClick={() => setInviteModalOpen(true)}>
                    Invite Member
                  </Button>
                  <Button icon={<SettingOutlined />}>Settings</Button>
                </Space>
              }
            >
              {/* Team Stats */}
              <Row gutter={16} style={{ marginBottom: 24 }}>
                <Col span={8}>
                  <Statistic title="Members" value={selectedTeam.memberCount} prefix={<TeamOutlined />} />
                </Col>
                <Col span={8}>
                  <Statistic title="Projects" value={selectedTeam.projectCount} prefix={<ProjectOutlined />} />
                </Col>
                <Col span={8}>
                  <Statistic 
                    title="Created" 
                    value={new Date(selectedTeam.createdAt).toLocaleDateString()} 
                    prefix={<ClockCircleOutlined />}
                  />
                </Col>
              </Row>

              {selectedTeam.description && (
                <Paragraph type="secondary" style={{ marginBottom: 24 }}>
                  {selectedTeam.description}
                </Paragraph>
              )}

              {/* Members Table */}
              <Table
                columns={memberColumns}
                dataSource={selectedTeam.members}
                rowKey="id"
                pagination={false}
              />
            </Card>
          ) : (
            <Card>
              <Empty description="Select a team to view details" />
            </Card>
          )}
        </Col>
      </Row>

      {/* Create Team Modal */}
      <Modal
        title={<><TeamOutlined /> Create Team</>}
        open={createTeamModalOpen}
        onCancel={() => {
          setCreateTeamModalOpen(false);
          teamForm.resetFields();
        }}
        onOk={() => teamForm.submit()}
      >
        <Form form={teamForm} layout="vertical" onFinish={handleCreateTeam}>
          <Form.Item
            name="name"
            label="Team Name"
            rules={[{ required: true, message: 'Please enter a team name' }]}
          >
            <Input placeholder="e.g., Frontend Team" />
          </Form.Item>
          <Form.Item
            name="description"
            label="Description"
          >
            <Input.TextArea placeholder="Brief description of the team" rows={3} />
          </Form.Item>
        </Form>
      </Modal>

      {/* Invite Member Modal */}
      <Modal
        title={<><UserAddOutlined /> Invite Member</>}
        open={inviteModalOpen}
        onCancel={() => {
          setInviteModalOpen(false);
          inviteForm.resetFields();
        }}
        onOk={() => inviteForm.submit()}
      >
        <Form form={inviteForm} layout="vertical" onFinish={handleInvite}>
          <Form.Item
            name="email"
            label="Email Address"
            rules={[
              { required: true, message: 'Please enter an email' },
              { type: 'email', message: 'Please enter a valid email' },
            ]}
          >
            <Input prefix={<MailOutlined />} placeholder="member@example.com" />
          </Form.Item>
          <Form.Item
            name="role"
            label="Role"
            initialValue="member"
            rules={[{ required: true }]}
          >
            <Select
              options={[
                { value: 'admin', label: 'Admin - Full access to team settings' },
                { value: 'member', label: 'Member - Can create and analyze projects' },
                { value: 'viewer', label: 'Viewer - Read-only access' },
              ]}
            />
          </Form.Item>
        </Form>
      </Modal>

      {/* Edit Member Modal */}
      <Modal
        title={<><EditOutlined /> Edit Member Role</>}
        open={editMemberModalOpen}
        onCancel={() => {
          setEditMemberModalOpen(false);
          setSelectedMember(null);
          memberForm.resetFields();
        }}
        onOk={() => memberForm.submit()}
      >
        <Form form={memberForm} layout="vertical" onFinish={handleUpdateMember}>
          <Form.Item
            name="role"
            label="Role"
            rules={[{ required: true }]}
          >
            <Select
              options={[
                { value: 'admin', label: 'Admin' },
                { value: 'member', label: 'Member' },
                { value: 'viewer', label: 'Viewer' },
              ]}
            />
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

export default TeamManagement;

/**
 * Vulnerability Dashboard Page
 * 
 * Admin interface for monitoring and managing code vulnerabilities
 * detected by the Self-Evolving Bug Fixer AI.
 */

import React, { useState, useEffect, useCallback } from 'react';
import {
  Card,
  Row,
  Col,
  Table,
  Tag,
  Button,
  Space,
  Progress,
  Tabs,
  Badge,
  Statistic,
  Modal,
  Typography,
  Select,
  Spin,
  Tooltip,
  Timeline,
  message,
} from 'antd';
import {
  SafetyOutlined,
  WarningOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ReloadOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  EyeOutlined,
  CodeOutlined,
  BranchesOutlined,
  ClockCircleOutlined,
  SyncOutlined,
} from '@ant-design/icons';
import type { ColumnsType } from 'antd/es/table';
import { useTranslation } from 'react-i18next';

const { Title, Text, Paragraph } = Typography;
const { TabPane } = Tabs;

// Types
interface Vulnerability {
  vuln_id: string;
  pattern_id: string;
  file_path: string;
  line_number: number;
  code_snippet: string;
  severity: 'critical' | 'high' | 'medium' | 'low' | 'info';
  category: 'security' | 'performance' | 'reliability' | 'maintainability';
  description: string;
  detected_at: string;
  fix_suggestion: string;
  confidence: number;
}

interface CodeFix {
  fix_id: string;
  vuln_id: string;
  file_path: string;
  original_code: string;
  fixed_code: string;
  fix_description: string;
  status: 'pending' | 'in_progress' | 'applied' | 'verified' | 'failed' | 'rolled_back';
  applied_at: string | null;
  verified_at: string | null;
}

interface CycleResult {
  cycle: number;
  start_time: string;
  end_time: string;
  duration_seconds: number;
  vulnerabilities_found: number;
  fixes_applied: number;
  fixes_verified: number;
}

interface CycleStatus {
  running: boolean;
  cycle_count: number;
  scan_interval: number;
  summary: {
    total: number;
    by_severity: Record<string, number>;
    fixes_applied: number;
    fixes_verified: number;
  };
  recent_cycles: CycleResult[];
}

// Severity colors
const severityColors: Record<string, string> = {
  critical: 'red',
  high: 'volcano',
  medium: 'orange',
  low: 'blue',
  info: 'default',
};

// Status colors
const statusColors: Record<string, string> = {
  pending: 'default',
  in_progress: 'processing',
  applied: 'warning',
  verified: 'success',
  failed: 'error',
  rolled_back: 'error',
};

const VulnerabilityDashboard: React.FC = () => {
  const { t } = useTranslation();

  // State
  const [vulnerabilities, setVulnerabilities] = useState<Vulnerability[]>([]);
  const [fixes, setFixes] = useState<CodeFix[]>([]);
  const [cycleStatus, setCycleStatus] = useState<CycleStatus | null>(null);
  const [loading, setLoading] = useState(true);
  const [scanning, setScanning] = useState(false);
  const [selectedVuln, setSelectedVuln] = useState<Vulnerability | null>(null);
  const [selectedFix, setSelectedFix] = useState<CodeFix | null>(null);
  const [detailsModalOpen, setDetailsModalOpen] = useState(false);
  const [fixModalOpen, setFixModalOpen] = useState(false);
  const [severityFilter, setSeverityFilter] = useState<string>('all');
  const [categoryFilter, setCategoryFilter] = useState<string>('all');

  // Fetch data
  const fetchData = useCallback(async () => {
    try {
      setLoading(true);

      // Mock data - in production, call actual API
      const mockVulnerabilities: Vulnerability[] = [
        {
          vuln_id: 'vuln-001',
          pattern_id: 'SEC-001',
          file_path: 'backend/shared/security/auth.py',
          line_number: 19,
          code_snippet: 'SECRET_KEY = os.getenv("JWT_SECRET_KEY", "default")',
          severity: 'critical',
          category: 'security',
          description: 'Hardcoded default secret key',
          detected_at: new Date().toISOString(),
          fix_suggestion: 'Remove default fallback and raise error if not set',
          confidence: 0.95,
        },
        {
          vuln_id: 'vuln-002',
          pattern_id: 'REL-001',
          file_path: 'backend/shared/services/reliability.py',
          line_number: 41,
          code_snippet: 'expire = datetime.utcnow() + expires_delta',
          severity: 'medium',
          category: 'reliability',
          description: 'Using deprecated datetime.utcnow()',
          detected_at: new Date().toISOString(),
          fix_suggestion: 'Use datetime.now(timezone.utc) instead',
          confidence: 0.90,
        },
        {
          vuln_id: 'vuln-003',
          pattern_id: 'SEC-003',
          file_path: 'backend/shared/security/auth.py',
          line_number: 97,
          code_snippet: 'payload = jwt.decode(token, SECRET_KEY, algorithms=[ALGORITHM])',
          severity: 'high',
          category: 'security',
          description: 'JWT decode without proper validation options',
          detected_at: new Date().toISOString(),
          fix_suggestion: 'Add audience, issuer, and required claims validation',
          confidence: 0.88,
        },
      ];

      const mockFixes: CodeFix[] = [
        {
          fix_id: 'fix-001',
          vuln_id: 'vuln-001',
          file_path: 'backend/shared/security/auth.py',
          original_code: 'SECRET_KEY = os.getenv("JWT_SECRET_KEY", "default")',
          fixed_code: 'SECRET_KEY = os.getenv("JWT_SECRET_KEY")\nif not SECRET_KEY:\n    raise ValueError("JWT_SECRET_KEY must be set")',
          fix_description: 'Remove default fallback for secret key',
          status: 'verified',
          applied_at: new Date().toISOString(),
          verified_at: new Date().toISOString(),
        },
        {
          fix_id: 'fix-002',
          vuln_id: 'vuln-002',
          file_path: 'backend/shared/services/reliability.py',
          original_code: 'datetime.utcnow()',
          fixed_code: 'datetime.now(timezone.utc)',
          fix_description: 'Replace deprecated datetime.utcnow()',
          status: 'applied',
          applied_at: new Date().toISOString(),
          verified_at: null,
        },
      ];

      const mockCycleStatus: CycleStatus = {
        running: true,
        cycle_count: 15,
        scan_interval: 3600,
        summary: {
          total: 3,
          by_severity: { critical: 1, high: 1, medium: 1 },
          fixes_applied: 2,
          fixes_verified: 1,
        },
        recent_cycles: [
          {
            cycle: 15,
            start_time: new Date(Date.now() - 3600000).toISOString(),
            end_time: new Date().toISOString(),
            duration_seconds: 45,
            vulnerabilities_found: 3,
            fixes_applied: 1,
            fixes_verified: 1,
          },
          {
            cycle: 14,
            start_time: new Date(Date.now() - 7200000).toISOString(),
            end_time: new Date(Date.now() - 7200000 + 50000).toISOString(),
            duration_seconds: 50,
            vulnerabilities_found: 5,
            fixes_applied: 2,
            fixes_verified: 2,
          },
        ],
      };

      setVulnerabilities(mockVulnerabilities);
      setFixes(mockFixes);
      setCycleStatus(mockCycleStatus);
    } catch (error) {
      console.error('Failed to fetch data:', error);
      message.error('Failed to load vulnerability data');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 30000);
    return () => clearInterval(interval);
  }, [fetchData]);

  // Handlers
  const handleScan = async () => {
    setScanning(true);
    message.loading({ content: 'Scanning codebase...', key: 'scan' });
    await new Promise((resolve) => setTimeout(resolve, 2000));
    await fetchData();
    message.success({ content: 'Scan complete!', key: 'scan' });
    setScanning(false);
  };

  const handleApplyFix = async (vulnId: string) => {
    message.loading({ content: 'Applying fix...', key: 'fix' });
    await new Promise((resolve) => setTimeout(resolve, 1000));
    message.success({ content: 'Fix applied successfully!', key: 'fix' });
    await fetchData();
  };

  const handleViewDetails = (vuln: Vulnerability) => {
    setSelectedVuln(vuln);
    setDetailsModalOpen(true);
  };

  const handleViewFix = (fix: CodeFix) => {
    setSelectedFix(fix);
    setFixModalOpen(true);
  };

  // Filter vulnerabilities
  const filteredVulnerabilities = vulnerabilities.filter((vuln) => {
    if (severityFilter !== 'all' && vuln.severity !== severityFilter) return false;
    if (categoryFilter !== 'all' && vuln.category !== categoryFilter) return false;
    return true;
  });

  // Vulnerability table columns
  const vulnColumns: ColumnsType<Vulnerability> = [
    {
      title: 'Severity',
      dataIndex: 'severity',
      key: 'severity',
      width: 100,
      render: (severity: string) => (
        <Tag color={severityColors[severity]} icon={<WarningOutlined />}>
          {severity.toUpperCase()}
        </Tag>
      ),
    },
    {
      title: 'File',
      dataIndex: 'file_path',
      key: 'file_path',
      render: (path: string) => (
        <Text code style={{ fontSize: 12 }}>
          {path.split('/').pop()}
        </Text>
      ),
    },
    {
      title: 'Line',
      dataIndex: 'line_number',
      key: 'line_number',
      width: 80,
    },
    {
      title: 'Description',
      dataIndex: 'description',
      key: 'description',
      ellipsis: true,
    },
    {
      title: 'Confidence',
      dataIndex: 'confidence',
      key: 'confidence',
      width: 120,
      render: (confidence: number) => (
        <Progress percent={Math.round(confidence * 100)} size="small" />
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 120,
      render: (_: unknown, record: Vulnerability) => (
        <Space>
          <Tooltip title="View Details">
            <Button
              type="text"
              icon={<EyeOutlined />}
              onClick={() => handleViewDetails(record)}
            />
          </Tooltip>
          <Tooltip title="Apply Fix">
            <Button
              type="text"
              icon={<CodeOutlined />}
              onClick={() => handleApplyFix(record.vuln_id)}
            />
          </Tooltip>
        </Space>
      ),
    },
  ];

  // Fix table columns
  const fixColumns: ColumnsType<CodeFix> = [
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 120,
      render: (status: string) => (
        <Tag color={statusColors[status]}>{status.toUpperCase()}</Tag>
      ),
    },
    {
      title: 'File',
      dataIndex: 'file_path',
      key: 'file_path',
      render: (path: string) => (
        <Text code style={{ fontSize: 12 }}>
          {path.split('/').pop()}
        </Text>
      ),
    },
    {
      title: 'Description',
      dataIndex: 'fix_description',
      key: 'fix_description',
      ellipsis: true,
    },
    {
      title: 'Applied At',
      dataIndex: 'applied_at',
      key: 'applied_at',
      width: 180,
      render: (date: string | null) =>
        date ? new Date(date).toLocaleString() : '-',
    },
    {
      title: 'Verified',
      dataIndex: 'verified_at',
      key: 'verified_at',
      width: 100,
      render: (date: string | null) =>
        date ? (
          <CheckCircleOutlined style={{ color: 'green', fontSize: 18 }} />
        ) : (
          <ClockCircleOutlined style={{ color: 'orange', fontSize: 18 }} />
        ),
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 80,
      render: (_: unknown, record: CodeFix) => (
        <Tooltip title="View Fix">
          <Button
            type="text"
            icon={<BranchesOutlined />}
            onClick={() => handleViewFix(record)}
          />
        </Tooltip>
      ),
    },
  ];

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 400 }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: 24 }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3} style={{ margin: 0 }}>
            <SafetyOutlined style={{ marginRight: 8 }} />
            Vulnerability Dashboard
          </Title>
        </Col>
        <Col>
          <Space>
            <Tag color={cycleStatus?.running ? 'green' : 'default'}>
              {cycleStatus?.running ? (
                <>
                  <SyncOutlined spin /> Cycle #{cycleStatus.cycle_count}
                </>
              ) : (
                <>
                  <PauseCircleOutlined /> Stopped
                </>
              )}
            </Tag>
            <Button
              type="primary"
              icon={scanning ? <SyncOutlined spin /> : <ReloadOutlined />}
              onClick={handleScan}
              loading={scanning}
            >
              Scan Now
            </Button>
          </Space>
        </Col>
      </Row>

      {/* Summary Cards */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Vulnerabilities"
              value={cycleStatus?.summary.total || 0}
              prefix={<SafetyOutlined />}
              valueStyle={{ color: '#cf1322' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Fixes Applied"
              value={cycleStatus?.summary.fixes_applied || 0}
              prefix={<CodeOutlined />}
              valueStyle={{ color: '#faad14' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Fixes Verified"
              value={cycleStatus?.summary.fixes_verified || 0}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#52c41a' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Cycle Status"
              value={cycleStatus?.running ? 'Running' : 'Stopped'}
              prefix={
                cycleStatus?.running ? (
                  <PlayCircleOutlined />
                ) : (
                  <PauseCircleOutlined />
                )
              }
              valueStyle={{ color: cycleStatus?.running ? '#52c41a' : '#8c8c8c' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Severity Breakdown */}
      <Card title="Severity Breakdown" style={{ marginBottom: 24 }}>
        <Row gutter={16}>
          {Object.entries(cycleStatus?.summary.by_severity || {}).map(
            ([severity, count]) => (
              <Col key={severity} xs={12} sm={6}>
                <Space>
                  <Tag color={severityColors[severity]}>
                    {severity.toUpperCase()}
                  </Tag>
                  <Text strong>{count}</Text>
                </Space>
              </Col>
            )
          )}
        </Row>
      </Card>

      {/* Main Content Tabs */}
      <Card>
        <Tabs defaultActiveKey="vulnerabilities">
          <TabPane tab="Vulnerabilities" key="vulnerabilities">
            <Space style={{ marginBottom: 16 }}>
              <Select
                style={{ width: 120 }}
                value={severityFilter}
                onChange={(value: string) => setSeverityFilter(value)}
                options={[
                  { value: 'all', label: 'All Severity' },
                  { value: 'critical', label: 'Critical' },
                  { value: 'high', label: 'High' },
                  { value: 'medium', label: 'Medium' },
                  { value: 'low', label: 'Low' },
                ]}
              />
              <Select
                style={{ width: 140 }}
                value={categoryFilter}
                onChange={(value: string) => setCategoryFilter(value)}
                options={[
                  { value: 'all', label: 'All Categories' },
                  { value: 'security', label: 'Security' },
                  { value: 'reliability', label: 'Reliability' },
                  { value: 'performance', label: 'Performance' },
                ]}
              />
            </Space>
            <Table
              columns={vulnColumns}
              dataSource={filteredVulnerabilities}
              rowKey="vuln_id"
              pagination={{ pageSize: 10 }}
            />
          </TabPane>

          <TabPane tab="Applied Fixes" key="fixes">
            <Table
              columns={fixColumns}
              dataSource={fixes}
              rowKey="fix_id"
              pagination={{ pageSize: 10 }}
            />
          </TabPane>

          <TabPane tab="Cycle History" key="history">
            <Timeline mode="left">
              {cycleStatus?.recent_cycles.map((cycle) => (
                <Timeline.Item
                  key={cycle.cycle}
                  color={cycle.fixes_verified > 0 ? 'green' : 'blue'}
                  label={new Date(cycle.end_time).toLocaleTimeString()}
                >
                  <Text strong>Cycle #{cycle.cycle}</Text>
                  <br />
                  <Text type="secondary">
                    Duration: {cycle.duration_seconds}s | Found:{' '}
                    {cycle.vulnerabilities_found} | Fixed: {cycle.fixes_applied} |
                    Verified: {cycle.fixes_verified}
                  </Text>
                </Timeline.Item>
              ))}
            </Timeline>
          </TabPane>
        </Tabs>
      </Card>

      {/* Vulnerability Details Modal */}
      <Modal
        title={
          <Space>
            Vulnerability Details
            {selectedVuln && (
              <Tag color={severityColors[selectedVuln.severity]}>
                {selectedVuln.severity.toUpperCase()}
              </Tag>
            )}
          </Space>
        }
        open={detailsModalOpen}
        onCancel={() => setDetailsModalOpen(false)}
        footer={[
          <Button key="close" onClick={() => setDetailsModalOpen(false)}>
            Close
          </Button>,
          <Button
            key="fix"
            type="primary"
            onClick={() => {
              if (selectedVuln) handleApplyFix(selectedVuln.vuln_id);
              setDetailsModalOpen(false);
            }}
          >
            Apply Fix
          </Button>,
        ]}
        width={700}
      >
        {selectedVuln && (
          <div>
            <Paragraph>
              <Text strong>File: </Text>
              <Text code>
                {selectedVuln.file_path}:{selectedVuln.line_number}
              </Text>
            </Paragraph>

            <Paragraph>
              <Text strong>Description: </Text>
              {selectedVuln.description}
            </Paragraph>

            <Paragraph>
              <Text strong>Code Snippet:</Text>
            </Paragraph>
            <pre
              style={{
                background: '#1e1e1e',
                color: '#d4d4d4',
                padding: 16,
                borderRadius: 4,
                overflow: 'auto',
              }}
            >
              {selectedVuln.code_snippet}
            </pre>

            <Paragraph>
              <Text strong>Suggested Fix:</Text>
            </Paragraph>
            <pre
              style={{
                background: '#1e3a1e',
                color: '#4caf50',
                padding: 16,
                borderRadius: 4,
                overflow: 'auto',
              }}
            >
              {selectedVuln.fix_suggestion}
            </pre>

            <Row gutter={16}>
              <Col span={8}>
                <Text type="secondary">Pattern ID: </Text>
                <Text>{selectedVuln.pattern_id}</Text>
              </Col>
              <Col span={8}>
                <Text type="secondary">Category: </Text>
                <Text>{selectedVuln.category}</Text>
              </Col>
              <Col span={8}>
                <Text type="secondary">Confidence: </Text>
                <Text>{(selectedVuln.confidence * 100).toFixed(0)}%</Text>
              </Col>
            </Row>
          </div>
        )}
      </Modal>

      {/* Fix Details Modal */}
      <Modal
        title="Fix Details"
        open={fixModalOpen}
        onCancel={() => setFixModalOpen(false)}
        footer={[
          <Button key="close" onClick={() => setFixModalOpen(false)}>
            Close
          </Button>,
        ]}
        width={700}
      >
        {selectedFix && (
          <div>
            <Paragraph>
              <Text strong>File: </Text>
              <Text code>{selectedFix.file_path}</Text>
            </Paragraph>

            <Paragraph>
              <Text strong>Original Code:</Text>
            </Paragraph>
            <pre
              style={{
                background: '#3a1e1e',
                color: '#f44336',
                padding: 16,
                borderRadius: 4,
                overflow: 'auto',
              }}
            >
              - {selectedFix.original_code}
            </pre>

            <Paragraph>
              <Text strong>Fixed Code:</Text>
            </Paragraph>
            <pre
              style={{
                background: '#1e3a1e',
                color: '#4caf50',
                padding: 16,
                borderRadius: 4,
                overflow: 'auto',
              }}
            >
              + {selectedFix.fixed_code}
            </pre>

            <Paragraph>
              <Text strong>Status: </Text>
              <Tag color={statusColors[selectedFix.status]}>
                {selectedFix.status.toUpperCase()}
              </Tag>
            </Paragraph>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default VulnerabilityDashboard;

/**
 * Webhooks Management Page
 * Webhook绠＄悊椤甸潰
 * 
 * Features:
 * - Webhook configuration
 * - Event subscriptions
 * - Delivery logs
 * - Testing interface
 */

import React, { useState } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Space,
  Button,
  Table,
  Tag,
  Modal,
  Form,
  Input,
  Select,
  Switch,
  Tabs,
  Badge,
  Tooltip,
  Alert,
  Statistic,
  Timeline,
  message,
} from 'antd';
import type { TableProps } from 'antd';
import {
  ApiOutlined,
  PlusOutlined,
  EditOutlined,
  DeleteOutlined,
  PlayCircleOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  SyncOutlined,
  ClockCircleOutlined,
  LinkOutlined,
  KeyOutlined,
  BellOutlined,
  CodeOutlined,
  SecurityScanOutlined,
  RocketOutlined,
  ReloadOutlined,
  EyeOutlined,
  CopyOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';

const { Title, Text, Paragraph } = Typography;

interface Webhook {
  id: string;
  name: string;
  url: string;
  secret?: string;
  events: string[];
  status: 'active' | 'inactive' | 'failed';
  lastDelivery?: string;
  lastStatus?: number;
  successRate: number;
  createdAt: string;
}

interface DeliveryLog {
  id: string;
  webhookId: string;
  event: string;
  status: number;
  duration: number;
  timestamp: string;
  requestBody: string;
  responseBody: string;
}

const mockWebhooks: Webhook[] = [
  {
    id: 'wh_1',
    name: 'Slack Notifications',
    url: 'https://hooks.slack.com/services/xxx/yyy/zzz',
    events: ['analysis.completed', 'security.alert'],
    status: 'active',
    lastDelivery: new Date(Date.now() - 10 * 60 * 1000).toISOString(),
    lastStatus: 200,
    successRate: 99.5,
    createdAt: new Date(Date.now() - 30 * 24 * 60 * 60 * 1000).toISOString(),
  },
  {
    id: 'wh_2',
    name: 'CI/CD Pipeline Trigger',
    url: 'https://api.github.com/repos/myorg/myrepo/dispatches',
    events: ['deployment.success', 'deployment.failed'],
    status: 'active',
    lastDelivery: new Date(Date.now() - 2 * 60 * 60 * 1000).toISOString(),
    lastStatus: 204,
    successRate: 100,
    createdAt: new Date(Date.now() - 15 * 24 * 60 * 60 * 1000).toISOString(),
  },
  {
    id: 'wh_3',
    name: 'Security Alert System',
    url: 'https://security.example.com/api/alerts',
    events: ['security.vulnerability', 'security.alert'],
    status: 'failed',
    lastDelivery: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(),
    lastStatus: 500,
    successRate: 85.2,
    createdAt: new Date(Date.now() - 60 * 24 * 60 * 60 * 1000).toISOString(),
  },
];

const mockDeliveryLogs: DeliveryLog[] = [
  {
    id: 'dl_1',
    webhookId: 'wh_1',
    event: 'analysis.completed',
    status: 200,
    duration: 245,
    timestamp: new Date(Date.now() - 10 * 60 * 1000).toISOString(),
    requestBody: '{"event":"analysis.completed","project":"frontend"}',
    responseBody: '{"ok":true}',
  },
  {
    id: 'dl_2',
    webhookId: 'wh_1',
    event: 'security.alert',
    status: 200,
    duration: 312,
    timestamp: new Date(Date.now() - 30 * 60 * 1000).toISOString(),
    requestBody: '{"event":"security.alert","severity":"critical"}',
    responseBody: '{"ok":true}',
  },
  {
    id: 'dl_3',
    webhookId: 'wh_3',
    event: 'security.vulnerability',
    status: 500,
    duration: 5000,
    timestamp: new Date(Date.now() - 24 * 60 * 60 * 1000).toISOString(),
    requestBody: '{"event":"security.vulnerability"}',
    responseBody: '{"error":"Internal Server Error"}',
  },
];

const availableEvents = [
  { value: 'analysis.completed', label: 'Analysis Completed', icon: <CodeOutlined /> },
  { value: 'analysis.failed', label: 'Analysis Failed', icon: <CloseCircleOutlined /> },
  { value: 'security.alert', label: 'Security Alert', icon: <SecurityScanOutlined /> },
  { value: 'security.vulnerability', label: 'Vulnerability Found', icon: <SecurityScanOutlined /> },
  { value: 'deployment.success', label: 'Deployment Success', icon: <RocketOutlined /> },
  { value: 'deployment.failed', label: 'Deployment Failed', icon: <CloseCircleOutlined /> },
  { value: 'pr.opened', label: 'PR Opened', icon: <BellOutlined /> },
  { value: 'pr.merged', label: 'PR Merged', icon: <CheckCircleOutlined /> },
];

export const Webhooks: React.FC = () => {
  const { t } = useTranslation();
  const [webhooks, setWebhooks] = useState<Webhook[]>(mockWebhooks);
  const [createModalOpen, setCreateModalOpen] = useState(false);
  const [selectedWebhook, setSelectedWebhook] = useState<Webhook | null>(null);
  const [detailModalOpen, setDetailModalOpen] = useState(false);
  const [form] = Form.useForm();

  const handleCreate = (values: any) => {
    const newWebhook: Webhook = {
      id: `wh_${Date.now()}`,
      ...values,
      status: 'active',
      successRate: 100,
      createdAt: new Date().toISOString(),
    };
    setWebhooks(prev => [...prev, newWebhook]);
    setCreateModalOpen(false);
    form.resetFields();
    message.success('Webhook created successfully');
  };

  const handleTest = async (webhook: Webhook) => {
    message.loading('Sending test request...');
    setTimeout(() => {
      message.success('Test webhook delivered successfully');
    }, 1500);
  };

  const handleDelete = (webhookId: string) => {
    setWebhooks(prev => prev.filter(w => w.id !== webhookId));
    message.success('Webhook deleted');
  };

  const columns: TableProps<Webhook>['columns'] = [
    {
      title: 'Webhook',
      key: 'webhook',
      render: (_, record) => (
        <div>
          <Space>
            <ApiOutlined style={{ color: '#2563eb' }} />
            <Text strong>{record.name}</Text>
            <Badge
              status={record.status === 'active' ? 'success' : record.status === 'failed' ? 'error' : 'default'}
            />
          </Space>
          <div>
            <Text type="secondary" style={{ fontSize: 12 }} copyable={{ text: record.url }}>
              {record.url.length > 50 ? record.url.substring(0, 50) + '...' : record.url}
            </Text>
          </div>
        </div>
      ),
    },
    {
      title: 'Events',
      dataIndex: 'events',
      width: 200,
      render: (events: string[]) => (
        <Space wrap size={4}>
          {events.slice(0, 2).map(event => (
            <Tag key={event} style={{ fontSize: 11 }}>{event}</Tag>
          ))}
          {events.length > 2 && <Tag>+{events.length - 2}</Tag>}
        </Space>
      ),
    },
    {
      title: 'Last Delivery',
      key: 'lastDelivery',
      width: 150,
      render: (_, record) => (
        record.lastDelivery ? (
          <Space direction="vertical" size={0}>
            <Tag color={record.lastStatus === 200 || record.lastStatus === 204 ? 'green' : 'red'}>
              {record.lastStatus}
            </Tag>
            <Text type="secondary" style={{ fontSize: 11 }}>
              {new Date(record.lastDelivery).toLocaleString()}
            </Text>
          </Space>
        ) : (
          <Text type="secondary">Never</Text>
        )
      ),
    },
    {
      title: 'Success Rate',
      dataIndex: 'successRate',
      width: 120,
      render: (rate: number) => (
        <Text style={{ color: rate >= 95 ? '#22c55e' : rate >= 80 ? '#f59e0b' : '#ef4444' }}>
          {rate}%
        </Text>
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 180,
      render: (_, record) => (
        <Space>
          <Tooltip title="Test">
            <Button size="small" icon={<PlayCircleOutlined />} onClick={() => handleTest(record)} />
          </Tooltip>
          <Tooltip title="View Details">
            <Button
              size="small"
              icon={<EyeOutlined />}
              onClick={() => {
                setSelectedWebhook(record);
                setDetailModalOpen(true);
              }}
            />
          </Tooltip>
          <Tooltip title="Edit">
            <Button size="small" icon={<EditOutlined />} />
          </Tooltip>
          <Tooltip title="Delete">
            <Button size="small" danger icon={<DeleteOutlined />} onClick={() => handleDelete(record.id)} />
          </Tooltip>
        </Space>
      ),
    },
  ];

  const stats = {
    total: webhooks.length,
    active: webhooks.filter(w => w.status === 'active').length,
    failed: webhooks.filter(w => w.status === 'failed').length,
  };

  return (
    <div className="webhooks-page" style={{ maxWidth: 1400, margin: '0 auto' }}>
      {/* Header */}
      <div className="page-header" style={{ marginBottom: 24 }}>
        <div>
          <Title level={3} style={{ margin: 0 }}>
            <ApiOutlined style={{ color: '#2563eb' }} /> Webhooks
          </Title>
          <Text type="secondary">Configure webhooks to integrate with external services</Text>
        </div>
        <Button type="primary" icon={<PlusOutlined />} onClick={() => setCreateModalOpen(true)}>
          Create Webhook
        </Button>
      </div>

      {/* Stats */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={8}>
          <Card style={{ borderRadius: 12 }}>
            <Statistic title="Total Webhooks" value={stats.total} prefix={<ApiOutlined />} />
          </Card>
        </Col>
        <Col xs={8}>
          <Card style={{ borderRadius: 12 }}>
            <Statistic title="Active" value={stats.active} valueStyle={{ color: '#22c55e' }} prefix={<CheckCircleOutlined />} />
          </Card>
        </Col>
        <Col xs={8}>
          <Card style={{ borderRadius: 12 }}>
            <Statistic title="Failed" value={stats.failed} valueStyle={{ color: '#ef4444' }} prefix={<CloseCircleOutlined />} />
          </Card>
        </Col>
      </Row>

      {/* Failed Webhook Alert */}
      {stats.failed > 0 && (
        <Alert
          type="error"
          showIcon
          message="Webhook Delivery Failures"
          description={`${stats.failed} webhook(s) have failed recently. Check the delivery logs for details.`}
          style={{ marginBottom: 24, borderRadius: 12 }}
          action={<Button size="small" danger>View Failures</Button>}
        />
      )}

      {/* Webhooks Table */}
      <Card title="Configured Webhooks" style={{ borderRadius: 12 }}>
        <Table columns={columns} dataSource={webhooks} rowKey="id" pagination={false} />
      </Card>

      {/* Create Modal */}
      <Modal
        title={<><PlusOutlined /> Create Webhook</>}
        open={createModalOpen}
        onCancel={() => {
          setCreateModalOpen(false);
          form.resetFields();
        }}
        onOk={() => form.submit()}
        okText="Create"
        width={600}
      >
        <Form form={form} layout="vertical" onFinish={handleCreate}>
          <Form.Item name="name" label="Name" rules={[{ required: true }]}>
            <Input placeholder="e.g., Slack Notifications" />
          </Form.Item>
          <Form.Item name="url" label="Payload URL" rules={[{ required: true, type: 'url' }]}>
            <Input placeholder="https://example.com/webhook" prefix={<LinkOutlined />} />
          </Form.Item>
          <Form.Item name="secret" label="Secret (Optional)">
            <Input.Password placeholder="Webhook secret for signature verification" prefix={<KeyOutlined />} />
          </Form.Item>
          <Form.Item name="events" label="Events" rules={[{ required: true }]}>
            <Select
              mode="multiple"
              placeholder="Select events to subscribe"
              options={availableEvents.map(e => ({ value: e.value, label: e.label }))}
            />
          </Form.Item>
        </Form>
      </Modal>

      {/* Detail Modal */}
      <Modal
        title={<><ApiOutlined /> Webhook Details</>}
        open={detailModalOpen}
        onCancel={() => setDetailModalOpen(false)}
        footer={null}
        width={700}
      >
        {selectedWebhook && (
          <Tabs
            items={[
              {
                key: 'details',
                label: 'Details',
                children: (
                  <Space direction="vertical" style={{ width: '100%' }} size={16}>
                    <div>
                      <Text type="secondary">Name</Text>
                      <div><Text strong>{selectedWebhook.name}</Text></div>
                    </div>
                    <div>
                      <Text type="secondary">URL</Text>
                      <div><Text copyable>{selectedWebhook.url}</Text></div>
                    </div>
                    <div>
                      <Text type="secondary">Events</Text>
                      <div>
                        <Space wrap>
                          {selectedWebhook.events.map(e => <Tag key={e}>{e}</Tag>)}
                        </Space>
                      </div>
                    </div>
                    <div>
                      <Text type="secondary">Success Rate</Text>
                      <div><Text strong>{selectedWebhook.successRate}%</Text></div>
                    </div>
                  </Space>
                ),
              },
              {
                key: 'deliveries',
                label: 'Recent Deliveries',
                children: (
                  <Timeline
                    items={mockDeliveryLogs.filter(l => l.webhookId === selectedWebhook.id).map(log => ({
                      color: log.status < 300 ? 'green' : 'red',
                      children: (
                        <div>
                          <Space>
                            <Tag color={log.status < 300 ? 'green' : 'red'}>{log.status}</Tag>
                            <Text>{log.event}</Text>
                            <Text type="secondary">{log.duration}ms</Text>
                          </Space>
                          <div>
                            <Text type="secondary" style={{ fontSize: 12 }}>
                              {new Date(log.timestamp).toLocaleString()}
                            </Text>
                          </div>
                        </div>
                      ),
                    }))}
                  />
                ),
              },
            ]}
          />
        )}
      </Modal>
    </div>
  );
};

export default Webhooks;

/**
