const { Title, Text, Paragraph } = Typography;

// Types
interface AIModel {
  id: string;
  name: string;
  provider: string;
  model_id: string;
  version: string;
  zone: 'v1-experimentation' | 'v2-production' | 'v3-quarantine';
  status: 'active' | 'inactive' | 'testing' | 'deprecated' | 'quarantined';
  metrics: {
    accuracy: number;
    latency_p95: number;
    error_rate: number;
    cost_per_request: number;
    requests_today: number;
    success_rate: number;
  };
  config: {
    max_tokens: number;
    temperature: number;
    top_p: number;
  };
  created_at: string;
  updated_at: string;
  promoted_at?: string;
  last_evaluation?: string;
}

interface ModelVersion {
  id: string;
  model_id: string;
  version: string;
  zone: string;
  status: string;
  metrics: any;
  created_at: string;
  promoted_by?: string;
  promotion_reason?: string;
}

