export const AutoFix: React.FC = () => {
  const { t } = useTranslation();
  const [fixes, setFixes] = useState<AutoFix[]>(mockAutoFixes);
  const [cycles, setCycles] = useState<FixCycle[]>(mockCycles);
  const [autoFixEnabled, setAutoFixEnabled] = useState(true);
  const [selectedFix, setSelectedFix] = useState<AutoFix | null>(null);
  const [detailModalOpen, setDetailModalOpen] = useState(false);
  const [loading, setLoading] = useState(false);

  // Statistics
  const stats = {
    totalFixes: fixes.length,
    applied: fixes.filter(f => f.status === 'applied').length,
    pending: fixes.filter(f => ['pending', 'review'].includes(f.status)).length,
    critical: fixes.filter(f => f.severity === 'critical' && f.status !== 'applied').length,
  };

  const currentCycle = cycles.find(c => c.status === 'running');

  // Approve fix
  const handleApprove = async (fix: AutoFix) => {
    try {
      await api.post(`/api/admin/auto-fix/${fix.id}/approve`);
      message.success('Fix approved and applied');
    } catch (error) {
      message.success('Fix approved and applied (demo)');
    }
    setFixes(prev => prev.map(f =>
      f.id === fix.id ? { ...f, status: 'applied' as const, appliedAt: new Date().toISOString(), appliedBy: 'Admin' } : f
    ));
  };

  // Reject fix
  const handleReject = async (fix: AutoFix) => {
    try {
      await api.post(`/api/admin/auto-fix/${fix.id}/reject`);
      message.info('Fix rejected');
    } catch (error) {
      message.info('Fix rejected (demo)');
    }
    setFixes(prev => prev.map(f =>
      f.id === fix.id ? { ...f, status: 'rejected' as const } : f
    ));
  };

  // Rollback fix
  const handleRollback = async (fix: AutoFix) => {
    try {
      await api.post(`/api/admin/auto-fix/${fix.id}/rollback`);
      message.success('Fix rolled back');
    } catch (error) {
      message.success('Fix rolled back (demo)');
    }
    setFixes(prev => prev.map(f =>
      f.id === fix.id ? { ...f, status: 'pending' as const, appliedAt: undefined, appliedBy: undefined } : f
    ));
  };

  // Start new cycle
  const handleStartCycle = async () => {
    try {
      await api.post('/api/admin/auto-fix/cycle/start');
      message.success('New fix cycle started');
    } catch (error) {
      message.success('New fix cycle started (demo)');
      setCycles(prev => [{
        id: `cycle_${Date.now()}`,
        startedAt: new Date().toISOString(),
        status: 'running',
        issuesFound: 0,
        issuesFixed: 0,
        issuesPending: 0,
      }, ...prev]);
    }
  };

  const getTypeConfig = (type: string) => {
    const configs: Record<string, { color: string; icon: React.ReactNode }> = {
      vulnerability: { color: '#ef4444', icon: <SafetyCertificateOutlined /> },
      bug: { color: '#f59e0b', icon: <BugOutlined /> },
      quality: { color: '#3b82f6', icon: <CodeOutlined /> },
      performance: { color: '#8b5cf6', icon: <ThunderboltOutlined /> },
    };
    return configs[type] || { color: '#64748b', icon: <CodeOutlined /> };
  };

  const getSeverityColor = (severity: string) => {
    const colors: Record<string, string> = {
      critical: 'red',
      high: 'orange',
      medium: 'gold',
      low: 'blue',
    };
    return colors[severity] || 'default';
  };

  const getStatusBadge = (status: string) => {
    const configs: Record<string, { color: string; icon: React.ReactNode; text: string }> = {
      pending: { color: 'default', icon: <ClockCircleOutlined />, text: 'Pending' },
      analyzing: { color: 'processing', icon: <SyncOutlined spin />, text: 'Analyzing' },
      fixing: { color: 'processing', icon: <SyncOutlined spin />, text: 'Fixing' },
      review: { color: 'warning', icon: <ExclamationCircleOutlined />, text: 'Review' },
      approved: { color: 'success', icon: <CheckCircleOutlined />, text: 'Approved' },
      applied: { color: 'success', icon: <CheckCircleOutlined />, text: 'Applied' },
      rejected: { color: 'error', icon: <CloseCircleOutlined />, text: 'Rejected' },
      failed: { color: 'error', icon: <CloseCircleOutlined />, text: 'Failed' },
    };
    const config = configs[status] || { color: 'default', icon: null, text: status };
    return <Tag color={config.color} icon={config.icon}>{config.text}</Tag>;
  };

  const columns: TableProps<AutoFix>['columns'] = [
    {
      title: 'Type',
      key: 'type',
      width: 100,
      render: (_, record) => {
        const config = getTypeConfig(record.type);
        return (
          <Tooltip title={record.type.charAt(0).toUpperCase() + record.type.slice(1)}>
            <div style={{
              width: 36,
              height: 36,
              borderRadius: 8,
              background: `${config.color}15`,
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: config.color,
              fontSize: 18,
            }}>
              {config.icon}
            </div>
          </Tooltip>
        );
      },
    },
    {
      title: 'Issue',
      key: 'issue',
      render: (_, record) => (
        <div>
          <Space>
            <Text strong>{record.title}</Text>
            <Tag color={getSeverityColor(record.severity)}>{record.severity.toUpperCase()}</Tag>
          </Space>
          <div>
            <Text type="secondary" style={{ fontSize: 13 }}>{record.description}</Text>
          </div>
          <div style={{ marginTop: 4 }}>
            <Text code style={{ fontSize: 12 }}>{record.file}:{record.line}</Text>
          </div>
        </div>
      ),
    },
    {
      title: 'AI Model',
      key: 'ai',
      width: 150,
      render: (_, record) => (
        <Space direction="vertical" size={0}>
          <Text>{record.aiModel}</Text>
          <Progress
            percent={Math.round(record.confidence * 100)}
            size="small"
            strokeColor={record.confidence >= 0.9 ? '#52c41a' : record.confidence >= 0.7 ? '#faad14' : '#ff4d4f'}
            format={p => `${p}% conf.`}
            style={{ width: 100 }}
          />
        </Space>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      width: 120,
      render: (status) => getStatusBadge(status),
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 200,
      render: (_, record) => (
        <Space>
          <Tooltip title="View Details">
            <Button
              size="small"
              icon={<EyeOutlined />}
              onClick={() => {
                setSelectedFix(record);
                setDetailModalOpen(true);
              }}
            />
          </Tooltip>
          
          {record.status === 'review' && (
            <>
              <Popconfirm
                title="Apply this fix?"
                description="The AI-generated fix will be applied to the codebase."
                onConfirm={() => handleApprove(record)}
              >
                <Button size="small" type="primary" icon={<CheckOutlined />}>
                  Approve
                </Button>
              </Popconfirm>
              <Button size="small" danger icon={<CloseCircleOutlined />} onClick={() => handleReject(record)}>
                Reject
              </Button>
            </>
          )}

          {record.status === 'applied' && (
            <Popconfirm
              title="Rollback this fix?"
              onConfirm={() => handleRollback(record)}
            >
              <Button size="small" icon={<RollbackOutlined />}>
                Rollback
              </Button>
            </Popconfirm>
          )}
        </Space>
      ),
    },
  ];

  return (
    <div className="auto-fix-page">
      {/* Header */}
      <div className="page-header" style={{ marginBottom: 24 }}>
        <div>
          <Title level={3} style={{ margin: 0 }}>
            <RobotOutlined style={{ color: '#2563eb' }} /> AI Auto-Fix System
          </Title>
          <Text type="secondary">
            Automated vulnerability and bug detection with AI-powered fixes
          </Text>
        </div>
        <Space>
          <span>Auto-Fix:</span>
          <Switch
            checked={autoFixEnabled}
            onChange={setAutoFixEnabled}
            checkedChildren="ON"
            unCheckedChildren="OFF"
          />
          <Button icon={<ReloadOutlined />}>Refresh</Button>
          <Button
            type="primary"
            icon={currentCycle ? <SyncOutlined spin /> : <PlayCircleOutlined />}
            onClick={handleStartCycle}
            disabled={!!currentCycle}
          >
            {currentCycle ? 'Cycle Running' : 'Start Cycle'}
          </Button>
        </Space>
      </div>

      {/* Current Cycle Status */}
      {currentCycle && (
        <Alert
          type="info"
          showIcon
          icon={<SyncOutlined spin />}
          message="Fix Cycle in Progress"
          description={
            <Row gutter={24} align="middle">
              <Col>
                <Statistic title="Issues Found" value={currentCycle.issuesFound} />
              </Col>
              <Col>
                <Statistic title="Fixed" value={currentCycle.issuesFixed} valueStyle={{ color: '#52c41a' }} />
              </Col>
              <Col>
                <Statistic title="Pending" value={currentCycle.issuesPending} valueStyle={{ color: '#faad14' }} />
              </Col>
              <Col flex="1">
                <Progress
                  percent={Math.round((currentCycle.issuesFixed / currentCycle.issuesFound) * 100) || 0}
                  status="active"
                />
              </Col>
            </Row>
          }
          style={{ marginBottom: 24 }}
        />
      )}

      {/* Stats */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={12} sm={6}>
          <Card style={{ borderRadius: 12 }}>
            <Statistic
              title="Total Fixes"
              value={stats.totalFixes}
              prefix={<RobotOutlined />}
            />
          </Card>
        </Col>
        <Col xs={12} sm={6}>
          <Card style={{ borderRadius: 12 }}>
            <Statistic
              title="Applied"
              value={stats.applied}
              valueStyle={{ color: '#52c41a' }}
              prefix={<CheckCircleOutlined />}
            />
          </Card>
        </Col>
        <Col xs={12} sm={6}>
          <Card style={{ borderRadius: 12 }}>
            <Statistic
              title="Pending Review"
              value={stats.pending}
              valueStyle={{ color: '#faad14' }}
              prefix={<ExclamationCircleOutlined />}
            />
          </Card>
        </Col>
        <Col xs={12} sm={6}>
          <Card style={{ borderRadius: 12, background: stats.critical > 0 ? '#fff1f0' : undefined }}>
            <Statistic
              title="Critical Issues"
              value={stats.critical}
              valueStyle={{ color: stats.critical > 0 ? '#ff4d4f' : '#52c41a' }}
              prefix={<WarningOutlined />}
            />
          </Card>
        </Col>
      </Row>

      {/* Version Control Cycle Visualization */}
      <Card title={<><HistoryOutlined /> Fix Cycle Pipeline</>} style={{ marginBottom: 24, borderRadius: 12 }}>
        <Steps
          current={currentCycle ? 2 : -1}
          status={currentCycle ? 'process' : 'wait'}
          items={[
            {
              title: 'Scan',
              description: 'Detect issues',
              icon: <EyeOutlined />,
            },
            {
              title: 'Analyze',
              description: 'AI analysis',
              icon: <RobotOutlined />,
            },
            {
              title: 'Fix',
              description: 'Generate fixes',
              icon: <CodeOutlined />,
            },
            {
              title: 'Review',
              description: 'Human review',
              icon: <ExclamationCircleOutlined />,
            },
            {
              title: 'Apply',
              description: 'Deploy fixes',
              icon: <CheckCircleOutlined />,
            },
          ]}
        />
      </Card>

      {/* Fixes Table */}
      <Card title={<><BugOutlined /> Auto-Fix Queue</>} style={{ borderRadius: 12 }}>
        <Tabs
          defaultActiveKey="all"
          items={[
            { key: 'all', label: `All (${fixes.length})` },
            { key: 'review', label: `Needs Review (${fixes.filter(f => f.status === 'review').length})` },
            { key: 'applied', label: `Applied (${fixes.filter(f => f.status === 'applied').length})` },
            { key: 'rejected', label: `Rejected (${fixes.filter(f => f.status === 'rejected').length})` },
          ]}
        />
        <Table
          columns={columns}
          dataSource={fixes}
          rowKey="id"
          pagination={{ pageSize: 10 }}
        />
      </Card>

      {/* Fix Detail Modal */}
      <Modal
        title={<><RobotOutlined /> Fix Details</>}
        open={detailModalOpen}
        onCancel={() => setDetailModalOpen(false)}
        width={800}
        footer={selectedFix?.status === 'review' ? [
          <Button key="reject" danger onClick={() => {
            if (selectedFix) handleReject(selectedFix);
            setDetailModalOpen(false);
          }}>
            Reject
          </Button>,
          <Button key="approve" type="primary" onClick={() => {
            if (selectedFix) handleApprove(selectedFix);
            setDetailModalOpen(false);
          }}>
            Approve & Apply
          </Button>,
        ] : null}
      >
        {selectedFix && (
          <div>
            <Descriptions bordered column={2}>
              <Descriptions.Item label="Type">
                <Tag color={getTypeConfig(selectedFix.type).color}>
                  {selectedFix.type.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="Severity">
                <Tag color={getSeverityColor(selectedFix.severity)}>
                  {selectedFix.severity.toUpperCase()}
                </Tag>
              </Descriptions.Item>
              <Descriptions.Item label="File" span={2}>
                <Text code>{selectedFix.file}:{selectedFix.line}</Text>
              </Descriptions.Item>
              <Descriptions.Item label="AI Model">{selectedFix.aiModel}</Descriptions.Item>
              <Descriptions.Item label="Confidence">
                <Progress
                  percent={Math.round(selectedFix.confidence * 100)}
                  size="small"
                  style={{ width: 100 }}
                />
              </Descriptions.Item>
            </Descriptions>

            {selectedFix.fix && (
              <div style={{ marginTop: 24 }}>
                <Title level={5}>Proposed Fix</Title>
                
                <div style={{ display: 'flex', gap: 16, marginBottom: 16 }}>
                  <div style={{ flex: 1 }}>
                    <Text type="secondary" strong>Before:</Text>
                    <pre style={{
                      background: '#fee2e2',
                      padding: 12,
                      borderRadius: 8,
                      fontSize: 13,
                      overflow: 'auto',
                    }}>
                      {selectedFix.fix.before}
                    </pre>
                  </div>
                  <div style={{ display: 'flex', alignItems: 'center' }}>
                    <ArrowRightOutlined style={{ fontSize: 20, color: '#64748b' }} />
                  </div>
                  <div style={{ flex: 1 }}>
                    <Text type="secondary" strong>After:</Text>
                    <pre style={{
                      background: '#d1fae5',
                      padding: 12,
                      borderRadius: 8,
                      fontSize: 13,
                      overflow: 'auto',
                    }}>
                      {selectedFix.fix.after}
                    </pre>
                  </div>
                </div>

                <Alert
                  message="AI Explanation"
                  description={selectedFix.fix.explanation}
                  type="info"
                  showIcon
                  icon={<RobotOutlined />}
                />
              </div>
            )}
          </div>
        )}
      </Modal>
    </div>
  );
};

export default AutoFix;

/**
 * Auto-Fix Dashboard
 * 
 * Comprehensive dashboard for the AI auto-fix cycle:
 * - Cycle status and controls
 * - Vulnerability list
 * - Fix review and approval
 * - History and metrics
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Button,
  Table,
  Tag,
  Space,
  Typography,
  Statistic,
  Progress,
  Modal,
  Tabs,
  Timeline,
  Alert,
  Badge,
  Tooltip,
  Spin,
  message,
  Popconfirm,
} from 'antd';
import {
  PlayCircleOutlined,
  PauseCircleOutlined,
  SyncOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  WarningOutlined,
  SafetyOutlined,
  BugOutlined,
  ThunderboltOutlined,
  EyeOutlined,
  RollbackOutlined,
  CodeOutlined,
  HistoryOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import { useAutoFix } from '../../hooks/useAutoFix';

const { Title, Text, Paragraph } = Typography;
const { TabPane } = Tabs;

const AutoFixDashboard: React.FC = () => {
  const { t } = useTranslation();
  const {
    status,
    vulnerabilities,
    fixes,
    pendingFixes,
    loading,
    refresh,
    startCycle,
    stopCycle,
    approveFix,
    rejectFix,
    applyFix,
    rollbackFix,
  } = useAutoFix();

  const [selectedFix, setSelectedFix] = useState<any>(null);
  const [showFixModal, setShowFixModal] = useState(false);

  // Severity colors
  const severityColors: Record<string, string> = {
    critical: 'red',
    high: 'orange',
    medium: 'gold',
    low: 'blue',
    info: 'default',
  };

  // Status colors
  const statusColors: Record<string, string> = {
    pending: 'default',
    approved: 'processing',
    applied: 'cyan',
    verified: 'success',
    rejected: 'error',
    rolled_back: 'warning',
  };

  // Vulnerability columns
  const vulnColumns = [
    {
      title: 'ID',
      dataIndex: 'vuln_id',
      key: 'vuln_id',
      width: 100,
      render: (id: string) => <Text code>{id.slice(-6)}</Text>,
    },
    {
      title: 'Severity',
      dataIndex: 'severity',
      key: 'severity',
      width: 100,
      render: (severity: string) => (
        <Tag color={severityColors[severity]}>{severity.toUpperCase()}</Tag>
      ),
      filters: [
        { text: 'Critical', value: 'critical' },
        { text: 'High', value: 'high' },
        { text: 'Medium', value: 'medium' },
        { text: 'Low', value: 'low' },
      ],
      onFilter: (value: any, record: any) => record.severity === value,
    },
    {
      title: 'File',
      dataIndex: 'file_path',
      key: 'file_path',
      ellipsis: true,
      render: (path: string, record: any) => (
        <Tooltip title={path}>
          <Text code style={{ fontSize: 12 }}>
            {path.split('/').pop()}:{record.line_number}
          </Text>
        </Tooltip>
      ),
    },
    {
      title: 'Description',
      dataIndex: 'description',
      key: 'description',
      ellipsis: true,
    },
    {
      title: 'Confidence',
      dataIndex: 'confidence',
      key: 'confidence',
      width: 100,
      render: (confidence: number) => (
        <Progress
          percent={Math.round(confidence * 100)}
          size="small"
          status={confidence >= 0.9 ? 'success' : confidence >= 0.7 ? 'normal' : 'exception'}
        />
      ),
    },
  ];

  // Fix columns
  const fixColumns = [
    {
      title: 'ID',
      dataIndex: 'fix_id',
      key: 'fix_id',
      width: 100,
      render: (id: string) => <Text code>{id.slice(-6)}</Text>,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 100,
      render: (status: string) => (
        <Tag color={statusColors[status]}>{status.toUpperCase()}</Tag>
      ),
    },
    {
      title: 'File',
      dataIndex: 'file_path',
      key: 'file_path',
      ellipsis: true,
      render: (path: string) => (
        <Tooltip title={path}>
          <Text code style={{ fontSize: 12 }}>
            {path.split('/').pop()}
          </Text>
        </Tooltip>
      ),
    },
    {
      title: 'Confidence',
      dataIndex: 'confidence',
      key: 'confidence',
      width: 100,
      render: (confidence: number) => (
        <Progress
          percent={Math.round(confidence * 100)}
          size="small"
          status={confidence >= 0.9 ? 'success' : 'normal'}
        />
      ),
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 200,
      render: (_: any, record: any) => (
        <Space size="small">
          <Tooltip title="View Details">
            <Button
              type="text"
              icon={<EyeOutlined />}
              onClick={() => {
                setSelectedFix(record);
                setShowFixModal(true);
              }}
            />
          </Tooltip>
          {record.status === 'pending' && (
            <>
              <Tooltip title="Approve">
                <Button
                  type="text"
                  icon={<CheckCircleOutlined style={{ color: '#52c41a' }} />}
                  onClick={() => approveFix(record.fix_id)}
                />
              </Tooltip>
              <Tooltip title="Reject">
                <Button
                  type="text"
                  icon={<CloseCircleOutlined style={{ color: '#ff4d4f' }} />}
                  onClick={() => rejectFix(record.fix_id)}
                />
              </Tooltip>
            </>
          )}
          {record.status === 'approved' && (
            <Tooltip title="Apply Fix">
              <Button
                type="text"
                icon={<ThunderboltOutlined style={{ color: '#1890ff' }} />}
                onClick={() => applyFix(record.fix_id)}
              />
            </Tooltip>
          )}
          {(record.status === 'applied' || record.status === 'verified') && (
            <Popconfirm
              title="Rollback this fix?"
              onConfirm={() => rollbackFix(record.fix_id)}
            >
              <Button
                type="text"
                danger
                icon={<RollbackOutlined />}
              />
            </Popconfirm>
          )}
        </Space>
      ),
    },
  ];

  if (loading && !status) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 400 }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: 24 }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3} style={{ margin: 0 }}>
            <ThunderboltOutlined style={{ marginRight: 8 }} />
            AI Auto-Fix Dashboard
          </Title>
        </Col>
        <Col>
          <Space>
            <Badge
              status={status?.running ? 'processing' : 'default'}
              text={status?.running ? 'Running' : 'Stopped'}
            />
            {status?.running ? (
              <Button
                type="primary"
                danger
                icon={<PauseCircleOutlined />}
                onClick={stopCycle}
              >
                Stop Cycle
              </Button>
            ) : (
              <Button
                type="primary"
                icon={<PlayCircleOutlined />}
                onClick={startCycle}
              >
                Start Cycle
              </Button>
            )}
            <Button icon={<SyncOutlined />} onClick={refresh}>
              Refresh
            </Button>
          </Space>
        </Col>
      </Row>

      {/* Metrics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Cycles Completed"
              value={status?.metrics.cycles_completed || 0}
              prefix={<SyncOutlined />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Vulnerabilities Detected"
              value={status?.metrics.vulnerabilities_detected || 0}
              prefix={<BugOutlined />}
              valueStyle={{ color: '#cf1322' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Fixes Applied"
              value={status?.metrics.fixes_applied || 0}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#3f8600' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Success Rate"
              value={
                status?.metrics.fixes_applied
                  ? Math.round(
                      (status.metrics.fixes_verified /
                        status.metrics.fixes_applied) *
                        100
                    )
                  : 0
              }
              suffix="%"
              prefix={<SafetyOutlined />}
              valueStyle={{
                color:
                  status?.metrics.fixes_applied &&
                  status.metrics.fixes_verified / status.metrics.fixes_applied > 0.8
                    ? '#3f8600'
                    : '#faad14',
              }}
            />
          </Card>
        </Col>
      </Row>

      {/* Pending Fixes Alert */}
      {pendingFixes.length > 0 && (
        <Alert
          message={`${pendingFixes.length} fixes pending approval`}
          description="Review and approve fixes before they can be applied."
          type="warning"
          showIcon
          style={{ marginBottom: 24 }}
        />
      )}

      {/* Tabs */}
      <Card>
        <Tabs defaultActiveKey="vulnerabilities">
          <TabPane
            tab={
              <span>
                <BugOutlined />
                Vulnerabilities ({vulnerabilities.length})
              </span>
            }
            key="vulnerabilities"
          >
            <Table
              columns={vulnColumns}
              dataSource={vulnerabilities}
              rowKey="vuln_id"
              size="small"
              pagination={{ pageSize: 10 }}
            />
          </TabPane>
          <TabPane
            tab={
              <span>
                <ThunderboltOutlined />
                Fixes ({fixes.length})
              </span>
            }
            key="fixes"
          >
            <Table
              columns={fixColumns}
              dataSource={fixes}
              rowKey="fix_id"
              size="small"
              pagination={{ pageSize: 10 }}
            />
          </TabPane>
          <TabPane
            tab={
              <span>
                <HistoryOutlined />
                History
              </span>
            }
            key="history"
          >
            <Timeline>
              <Timeline.Item color="green">
                <Text strong>Fix verified</Text>
                <br />
                <Text type="secondary">auth.py - Hardcoded secret removed</Text>
                <br />
                <Text type="secondary" style={{ fontSize: 12 }}>
                  {new Date().toLocaleString()}
                </Text>
              </Timeline.Item>
              <Timeline.Item color="blue">
                <Text strong>Fix applied</Text>
                <br />
                <Text type="secondary">reliability.py - Deprecated API updated</Text>
                <br />
                <Text type="secondary" style={{ fontSize: 12 }}>
                  {new Date(Date.now() - 3600000).toLocaleString()}
                </Text>
              </Timeline.Item>
              <Timeline.Item color="orange">
                <Text strong>Vulnerability detected</Text>
                <br />
                <Text type="secondary">JWT decode without validation</Text>
                <br />
                <Text type="secondary" style={{ fontSize: 12 }}>
                  {new Date(Date.now() - 7200000).toLocaleString()}
                </Text>
              </Timeline.Item>
              <Timeline.Item color="gray">
                <Text strong>Cycle completed</Text>
                <br />
                <Text type="secondary">Cycle #15 finished successfully</Text>
                <br />
                <Text type="secondary" style={{ fontSize: 12 }}>
                  {new Date(Date.now() - 10800000).toLocaleString()}
                </Text>
              </Timeline.Item>
            </Timeline>
          </TabPane>
        </Tabs>
      </Card>

      {/* Fix Detail Modal */}
      <Modal
        title="Fix Details"
        open={showFixModal}
        onCancel={() => setShowFixModal(false)}
        width={800}
        footer={null}
      >
        {selectedFix && (
          <div>
            <Row gutter={16} style={{ marginBottom: 16 }}>
              <Col span={12}>
                <Text type="secondary">Fix ID:</Text>
                <br />
                <Text code>{selectedFix.fix_id}</Text>
              </Col>
              <Col span={12}>
                <Text type="secondary">Status:</Text>
                <br />
                <Tag color={statusColors[selectedFix.status]}>
                  {selectedFix.status.toUpperCase()}
                </Tag>
              </Col>
            </Row>
            <Row gutter={16} style={{ marginBottom: 16 }}>
              <Col span={24}>
                <Text type="secondary">File:</Text>
                <br />
                <Text code>{selectedFix.file_path}</Text>
              </Col>
            </Row>
            <Row gutter={16}>
              <Col span={12}>
                <Text type="secondary">Original Code:</Text>
                <pre
                  style={{
                    background: '#1a1a2e',
                    color: '#ff6b6b',
                    padding: 12,
                    borderRadius: 4,
                    overflow: 'auto',
                  }}
                >
                  {selectedFix.original_code || 'N/A'}
                </pre>
              </Col>
              <Col span={12}>
                <Text type="secondary">Fixed Code:</Text>
                <pre
                  style={{
                    background: '#1a1a2e',
                    color: '#00ff88',
                    padding: 12,
                    borderRadius: 4,
                    overflow: 'auto',
                  }}
                >
                  {selectedFix.fixed_code || 'N/A'}
                </pre>
              </Col>
            </Row>
          </div>
        )}
      </Modal>
    </div>
  );
};

export default AutoFixDashboard;

/**
 * Code Quality Dashboard
 * 
 * Comprehensive code quality metrics and analysis:
 * - Code coverage tracking
 * - Complexity metrics (cyclomatic, cognitive)
 * - Code duplication detection
 * - Technical debt estimation
 * - Quality trends over time
 */

import React, { useState, useMemo } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Statistic,
  Progress,
  Table,
  Tag,
  Space,
  Select,
  Tabs,
  List,
  Avatar,
  Tooltip,
} from 'antd';
import {
  CodeOutlined,
  CheckCircleOutlined,
  WarningOutlined,
  FileTextOutlined,
  BranchesOutlined,
  ClockCircleOutlined,
  RiseOutlined,
  FallOutlined,
  BugOutlined,
  ThunderboltOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip as RechartsTooltip,
  ResponsiveContainer,
  RadarChart,
  Radar,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  BarChart,
  Bar,
} from 'recharts';

const { Title, Text } = Typography;
const { Option } = Select;
const { TabPane } = Tabs;

interface QualityMetric {
  name: string;
  score: number;
  trend: number;
  status: 'good' | 'warning' | 'critical';
}

interface FileMetric {
  file: string;
  coverage: number;
  complexity: number;
  duplications: number;
  issues: number;
  lines: number;
}

const CodeQualityDashboard: React.FC = () => {
  const { t } = useTranslation();
  const [timeRange, setTimeRange] = useState<'7d' | '30d' | '90d'>('30d');
  const [selectedProject, setSelectedProject] = useState<string>('all');

  // Quality metrics
  const qualityMetrics: QualityMetric[] = [
    { name: 'Code Coverage', score: 78, trend: 5, status: 'warning' },
    { name: 'Maintainability', score: 85, trend: 3, status: 'good' },
    { name: 'Reliability', score: 92, trend: -2, status: 'good' },
    { name: 'Security', score: 88, trend: 8, status: 'good' },
    { name: 'Duplications', score: 94, trend: 1, status: 'good' },
  ];

  // Radar chart data
  const radarData = [
    { subject: 'Coverage', A: 78, fullMark: 100 },
    { subject: 'Maintainability', A: 85, fullMark: 100 },
    { subject: 'Reliability', A: 92, fullMark: 100 },
    { subject: 'Security', A: 88, fullMark: 100 },
    { subject: 'Performance', A: 82, fullMark: 100 },
    { subject: 'Documentation', A: 65, fullMark: 100 },
  ];

  // Trend data
  const trendData = useMemo(() => {
    const data = [];
    for (let i = 29; i >= 0; i--) {
      const date = new Date();
      date.setDate(date.getDate() - i);
      data.push({
        date: date.toLocaleDateString('en-US', { month: 'short', day: 'numeric' }),
        coverage: 70 + Math.random() * 15 + (29 - i) * 0.2,
        quality: 80 + Math.random() * 10 + (29 - i) * 0.15,
        debt: 120 - (29 - i) * 2 + Math.random() * 20,
      });
    }
    return data;
  }, []);

  // File metrics
  const fileMetrics: FileMetric[] = [
    { file: 'src/services/ai/orchestrator.py', coverage: 45, complexity: 28, duplications: 3, issues: 8, lines: 520 },
    { file: 'src/api/analysis.py', coverage: 62, complexity: 22, duplications: 1, issues: 5, lines: 380 },
    { file: 'src/components/CodeEditor.tsx', coverage: 78, complexity: 15, duplications: 0, issues: 2, lines: 290 },
    { file: 'src/hooks/useAuth.ts', coverage: 85, complexity: 12, duplications: 0, issues: 1, lines: 180 },
    { file: 'src/utils/parser.py', coverage: 52, complexity: 35, duplications: 5, issues: 12, lines: 650 },
    { file: 'src/models/experiment.py', coverage: 90, complexity: 8, duplications: 0, issues: 0, lines: 120 },
  ];

  // Technical debt items
  const debtItems = [
    { id: 1, type: 'Code Smell', title: 'Long method detected', file: 'orchestrator.py:145', effort: '2h', severity: 'major' },
    { id: 2, type: 'Bug', title: 'Null pointer dereference', file: 'parser.py:234', effort: '30m', severity: 'critical' },
    { id: 3, type: 'Vulnerability', title: 'SQL injection risk', file: 'api/users.py:89', effort: '1h', severity: 'blocker' },
    { id: 4, type: 'Code Smell', title: 'Duplicated code block', file: 'utils/helpers.py:45', effort: '45m', severity: 'minor' },
    { id: 5, type: 'Code Smell', title: 'Complex conditional', file: 'services/auth.py:78', effort: '1h', severity: 'major' },
  ];

  // Complexity distribution
  const complexityData = [
    { range: '1-5', count: 45, label: 'Simple' },
    { range: '6-10', count: 32, label: 'Low' },
    { range: '11-20', count: 18, label: 'Moderate' },
    { range: '21-50', count: 8, label: 'High' },
    { range: '50+', count: 3, label: 'Very High' },
  ];

  const fileColumns = [
    {
      title: 'File',
      dataIndex: 'file',
      key: 'file',
      render: (file: string) => (
        <Space>
          <FileTextOutlined />
          <Text code style={{ fontSize: 12 }}>{file}</Text>
        </Space>
      ),
    },
    {
      title: 'Coverage',
      dataIndex: 'coverage',
      key: 'coverage',
      sorter: (a: FileMetric, b: FileMetric) => a.coverage - b.coverage,
      render: (val: number) => (
        <Progress 
          percent={val} 
          size="small" 
          status={val < 50 ? 'exception' : val < 80 ? 'normal' : 'success'}
          style={{ width: 100 }}
        />
      ),
    },
    {
      title: 'Complexity',
      dataIndex: 'complexity',
      key: 'complexity',
      sorter: (a: FileMetric, b: FileMetric) => a.complexity - b.complexity,
      render: (val: number) => (
        <Tag color={val > 25 ? 'red' : val > 15 ? 'orange' : 'green'}>
          {val}
        </Tag>
      ),
    },
    {
      title: 'Duplications',
      dataIndex: 'duplications',
      key: 'duplications',
      render: (val: number) => (
        <Tag color={val > 3 ? 'red' : val > 0 ? 'orange' : 'green'}>
          {val} blocks
        </Tag>
      ),
    },
    {
      title: 'Issues',
      dataIndex: 'issues',
      key: 'issues',
      sorter: (a: FileMetric, b: FileMetric) => a.issues - b.issues,
      render: (val: number) => (
        <Tag color={val > 5 ? 'red' : val > 0 ? 'orange' : 'green'}>
          {val}
        </Tag>
      ),
    },
    {
      title: 'Lines',
      dataIndex: 'lines',
      key: 'lines',
      render: (val: number) => <Text type="secondary">{val}</Text>,
    },
  ];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'good': return '#52c41a';
      case 'warning': return '#faad14';
      case 'critical': return '#ff4d4f';
      default: return '#1890ff';
    }
  };

  const overallScore = Math.round(
    qualityMetrics.reduce((sum, m) => sum + m.score, 0) / qualityMetrics.length
  );

  return (
    <div style={{ padding: 24 }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3} style={{ margin: 0 }}>
            <CodeOutlined style={{ marginRight: 8 }} />
            Code Quality Dashboard
          </Title>
        </Col>
        <Col>
          <Space>
            <Select value={selectedProject} onChange={setSelectedProject} style={{ width: 150 }}>
              <Option value="all">All Projects</Option>
              <Option value="backend">Backend API</Option>
              <Option value="frontend">Frontend App</Option>
              <Option value="ai-core">AI Core</Option>
            </Select>
            <Select value={timeRange} onChange={setTimeRange} style={{ width: 100 }}>
              <Option value="7d">7 Days</Option>
              <Option value="30d">30 Days</Option>
              <Option value="90d">90 Days</Option>
            </Select>
          </Space>
        </Col>
      </Row>

      {/* Overall Score & Key Metrics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} md={8}>
          <Card>
            <div style={{ textAlign: 'center' }}>
              <Progress
                type="dashboard"
                percent={overallScore}
                width={150}
                strokeColor={{
                  '0%': '#ff4d4f',
                  '50%': '#faad14',
                  '100%': '#52c41a',
                }}
                format={(percent) => (
                  <div>
                    <div style={{ fontSize: 32, fontWeight: 700 }}>{percent}</div>
                    <div style={{ fontSize: 14, color: '#888' }}>Quality Score</div>
                  </div>
                )}
              />
              <div style={{ marginTop: 16 }}>
                <Tag color="green">Grade: B+</Tag>
              </div>
            </div>
          </Card>
        </Col>
        <Col xs={24} md={16}>
          <Card title="Quality Metrics">
            <Row gutter={[16, 16]}>
              {qualityMetrics.map((metric) => (
                <Col xs={12} sm={8} key={metric.name}>
                  <Statistic
                    title={metric.name}
                    value={metric.score}
                    suffix="%"
                    valueStyle={{ color: getStatusColor(metric.status) }}
                    prefix={metric.trend > 0 ? <RiseOutlined /> : <FallOutlined />}
                  />
                  <Text type="secondary" style={{ fontSize: 12 }}>
                    {metric.trend > 0 ? '+' : ''}{metric.trend}% vs last period
                  </Text>
                </Col>
              ))}
            </Row>
          </Card>
        </Col>
      </Row>

      {/* Charts Row */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} lg={12}>
          <Card title="Quality Radar">
            <ResponsiveContainer width="100%" height={300}>
              <RadarChart data={radarData}>
                <PolarGrid />
                <PolarAngleAxis dataKey="subject" tick={{ fontSize: 12 }} />
                <PolarRadiusAxis angle={30} domain={[0, 100]} />
                <Radar
                  name="Score"
                  dataKey="A"
                  stroke="#1890ff"
                  fill="#1890ff"
                  fillOpacity={0.5}
                />
                <RechartsTooltip />
              </RadarChart>
            </ResponsiveContainer>
          </Card>
        </Col>
        <Col xs={24} lg={12}>
          <Card title="Quality Trend">
            <ResponsiveContainer width="100%" height={300}>
              <LineChart data={trendData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="date" tick={{ fontSize: 10 }} />
                <YAxis domain={[0, 100]} />
                <RechartsTooltip />
                <Line type="monotone" dataKey="coverage" stroke="#52c41a" name="Coverage" />
                <Line type="monotone" dataKey="quality" stroke="#1890ff" name="Quality" />
              </LineChart>
            </ResponsiveContainer>
          </Card>
        </Col>
      </Row>

      {/* Tabs Section */}
      <Card>
        <Tabs defaultActiveKey="files">
          <TabPane tab={<span><FileTextOutlined /> Files ({fileMetrics.length})</span>} key="files">
            <Table
              columns={fileColumns}
              dataSource={fileMetrics}
              rowKey="file"
              pagination={{ pageSize: 10 }}
              size="middle"
            />
          </TabPane>
          
          <TabPane tab={<span><BugOutlined /> Technical Debt ({debtItems.length})</span>} key="debt">
            <Row gutter={16} style={{ marginBottom: 16 }}>
              <Col span={6}>
                <Statistic title="Total Debt" value="12h 45m" prefix={<ClockCircleOutlined />} />
              </Col>
              <Col span={6}>
                <Statistic title="Blockers" value={1} valueStyle={{ color: '#ff4d4f' }} />
              </Col>
              <Col span={6}>
                <Statistic title="Critical" value={1} valueStyle={{ color: '#fa8c16' }} />
              </Col>
              <Col span={6}>
                <Statistic title="Major" value={2} valueStyle={{ color: '#faad14' }} />
              </Col>
            </Row>
            <List
              dataSource={debtItems}
              renderItem={(item) => (
                <List.Item>
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        style={{
                          backgroundColor:
                            item.severity === 'blocker' ? '#ff4d4f' :
                            item.severity === 'critical' ? '#fa8c16' :
                            item.severity === 'major' ? '#faad14' : '#52c41a'
                        }}
                        icon={item.type === 'Bug' ? <BugOutlined /> : <WarningOutlined />}
                      />
                    }
                    title={
                      <Space>
                        <Text strong>{item.title}</Text>
                        <Tag>{item.type}</Tag>
                      </Space>
                    }
                    description={
                      <Space>
                        <Text code>{item.file}</Text>
                        <Text type="secondary">Est. effort: {item.effort}</Text>
                      </Space>
                    }
                  />
                  <Tag color={
                    item.severity === 'blocker' ? 'red' :
                    item.severity === 'critical' ? 'orange' :
                    item.severity === 'major' ? 'gold' : 'green'
                  }>
                    {item.severity.toUpperCase()}
                  </Tag>
                </List.Item>
              )}
            />
          </TabPane>

          <TabPane tab={<span><BranchesOutlined /> Complexity</span>} key="complexity">
            <Row gutter={16}>
              <Col xs={24} md={12}>
                <Title level={5}>Cyclomatic Complexity Distribution</Title>
                <ResponsiveContainer width="100%" height={250}>
                  <BarChart data={complexityData}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis dataKey="range" />
                    <YAxis />
                    <RechartsTooltip />
                    <Bar dataKey="count" fill="#1890ff" />
                  </BarChart>
                </ResponsiveContainer>
              </Col>
              <Col xs={24} md={12}>
                <Title level={5}>Complexity Summary</Title>
                <List
                  dataSource={complexityData}
                  renderItem={(item) => (
                    <List.Item>
                      <List.Item.Meta
                        title={`${item.label} (${item.range})`}
                        description={`${item.count} functions`}
                      />
                      <Progress
                        percent={Math.round((item.count / 106) * 100)}
                        size="small"
                        style={{ width: 100 }}
                        strokeColor={
                          item.range === '50+' ? '#ff4d4f' :
                          item.range === '21-50' ? '#fa8c16' :
                          item.range === '11-20' ? '#faad14' : '#52c41a'
                        }
                      />
                    </List.Item>
                  )}
                />
              </Col>
            </Row>
          </TabPane>
        </Tabs>
      </Card>
    </div>
  );
};

export default CodeQualityDashboard;

import React, { useState, useEffect } from 'react';
import {
  Card,
  Table,
  Button,
  Space,
  Tag,
  Modal,
  Form,
  Input,
  Select,
  Typography,
  Tooltip,
  Popconfirm,
  message,
  Progress,
  Descriptions
} from 'antd';
import {
  PlusOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ExperimentOutlined,
  ArrowUpOutlined,
  StopOutlined
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import { apiService } from '../../services/api';
import './ExperimentManagement.css';

const { Title, Text } = Typography;
const { TextArea } = Input;

interface Experiment {
  id: string;
  name: string;
  status: 'pending' | 'running' | 'completed' | 'failed' | 'promoted' | 'quarantined';
  config: {
    model: string;
    temperature: number;
    prompt_template: string;
  };
  dataset_id: string;
  created_at: string;
  started_at?: string;
  completed_at?: string;
  metrics?: {
    accuracy: number;
    precision: number;
    recall: number;
    f1_score: number;
    latency_p95: number;
    error_rate: number;
    cost_per_analysis: number;
  };
}

export const ExperimentManagement: React.FC = () => {
  const { t } = useTranslation();
  const [experiments, setExperiments] = useState<Experiment[]>([]);
  const [loading, setLoading] = useState(true);
  const [createModalVisible, setCreateModalVisible] = useState(false);
  const [detailModalVisible, setDetailModalVisible] = useState(false);
  const [selectedExperiment, setSelectedExperiment] = useState<Experiment | null>(null);
  const [form] = Form.useForm();

  // Fetch experiments
  useEffect(() => {
    fetchExperiments();
  }, []);

  const fetchExperiments = async () => {
    setLoading(true);
    try {
      const response = await apiService.experiments.list();
      setExperiments(response.data.items || []);
    } catch (error) {
      message.error(t('experiments.fetch_error', 'Failed to fetch experiments'));
    } finally {
      setLoading(false);
    }
  };

  // Create experiment
  const handleCreate = async (values: any) => {
    try {
      await apiService.experiments.create({
        name: values.name,
        config: {
          model: values.model,
          temperature: parseFloat(values.temperature),
          prompt_template: values.prompt_template
        },
        dataset_id: values.dataset_id
      });
      message.success(t('experiments.create_success', 'Experiment created'));
      setCreateModalVisible(false);
      form.resetFields();
      fetchExperiments();
    } catch (error) {
      message.error(t('experiments.create_error', 'Failed to create experiment'));
    }
  };

  // Start experiment
  const handleStart = async (id: string) => {
    try {
      await apiService.experiments.start(id);
      message.success(t('experiments.start_success', 'Experiment started'));
      fetchExperiments();
    } catch (error) {
      message.error(t('experiments.start_error', 'Failed to start experiment'));
    }
  };

  // Stop experiment
  const handleStop = async (id: string) => {
    try {
      await apiService.experiments.stop(id);
      message.success(t('experiments.stop_success', 'Experiment stopped'));
      fetchExperiments();
    } catch (error) {
      message.error(t('experiments.stop_error', 'Failed to stop experiment'));
    }
  };

  // Promote experiment
  const handlePromote = async (id: string) => {
    try {
      await apiService.experiments.promote(id);
      message.success(t('experiments.promote_success', 'Experiment promoted to v2'));
      fetchExperiments();
    } catch (error: any) {
      message.error(error.response?.data?.detail || t('experiments.promote_error', 'Failed to promote experiment'));
    }
  };

  // Quarantine experiment
  const handleQuarantine = async (id: string, reason: string) => {
    try {
      await apiService.experiments.quarantine(id, reason);
      message.success(t('experiments.quarantine_success', 'Experiment quarantined'));
      fetchExperiments();
    } catch (error) {
      message.error(t('experiments.quarantine_error', 'Failed to quarantine experiment'));
    }
  };

  // Get status tag
  const getStatusTag = (status: string) => {
    const statusConfig: Record<string, { color: string; icon: React.ReactNode }> = {
      pending: { color: 'default', icon: <ExperimentOutlined /> },
      running: { color: 'processing', icon: <PlayCircleOutlined /> },
      completed: { color: 'success', icon: <CheckCircleOutlined /> },
      failed: { color: 'error', icon: <CloseCircleOutlined /> },
      promoted: { color: 'green', icon: <ArrowUpOutlined /> },
      quarantined: { color: 'orange', icon: <StopOutlined /> }
    };
    const config = statusConfig[status] || statusConfig.pending;
    return (
      <Tag color={config.color} icon={config.icon}>
        {status.toUpperCase()}
      </Tag>
    );
  };

  // Table columns
  const columns = [
    {
      title: t('experiments.name', 'Name'),
      dataIndex: 'name',
      key: 'name',
      render: (name: string, record: Experiment) => (
        <a onClick={() => {
          setSelectedExperiment(record);
          setDetailModalVisible(true);
        }}>
          {name}
        </a>
      )
    },
    {
      title: t('experiments.model', 'Model'),
      dataIndex: ['config', 'model'],
      key: 'model',
      render: (model: string) => <Tag>{model}</Tag>
    },
    {
      title: t('experiments.status', 'Status'),
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => getStatusTag(status)
    },
    {
      title: t('experiments.accuracy', 'Accuracy'),
      dataIndex: ['metrics', 'accuracy'],
      key: 'accuracy',
      render: (accuracy: number) => accuracy ? (
        <Progress
          percent={Math.round(accuracy * 100)}
          size="small"
          status={accuracy >= 0.85 ? 'success' : accuracy >= 0.7 ? 'normal' : 'exception'}
        />
      ) : '-'
    },
    {
      title: t('experiments.error_rate', 'Error Rate'),
      dataIndex: ['metrics', 'error_rate'],
      key: 'error_rate',
      render: (rate: number) => rate !== undefined ? (
        <Tag color={rate <= 0.05 ? 'green' : rate <= 0.1 ? 'orange' : 'red'}>
          {(rate * 100).toFixed(1)}%
        </Tag>
      ) : '-'
    },
    {
      title: t('experiments.created', 'Created'),
      dataIndex: 'created_at',
      key: 'created_at',
      render: (date: string) => new Date(date).toLocaleString()
    },
    {
      title: t('experiments.actions', 'Actions'),
      key: 'actions',
      render: (_: any, record: Experiment) => (
        <Space>
          {record.status === 'pending' && (
            <Tooltip title={t('experiments.start', 'Start')}>
              <Button
                type="primary"
                size="small"
                icon={<PlayCircleOutlined />}
                onClick={() => handleStart(record.id)}
              />
            </Tooltip>
          )}
          {record.status === 'running' && (
            <Tooltip title={t('experiments.stop', 'Stop')}>
              <Button
                size="small"
                icon={<PauseCircleOutlined />}
                onClick={() => handleStop(record.id)}
              />
            </Tooltip>
          )}
          {record.status === 'completed' && record.metrics && (
            <>
              <Popconfirm
                title={t('experiments.promote_confirm', 'Promote to v2?')}
                onConfirm={() => handlePromote(record.id)}
                disabled={record.metrics.accuracy < 0.85 || record.metrics.error_rate > 0.05}
              >
                <Tooltip title={
                  record.metrics.accuracy >= 0.85 && record.metrics.error_rate <= 0.05
                    ? t('experiments.promote', 'Promote')
                    : t('experiments.promote_disabled', 'Does not meet criteria')
                }>
                  <Button
                    type="primary"
                    size="small"
                    icon={<ArrowUpOutlined />}
                    disabled={record.metrics.accuracy < 0.85 || record.metrics.error_rate > 0.05}
                  />
                </Tooltip>
              </Popconfirm>
              <Popconfirm
                title={t('experiments.quarantine_confirm', 'Quarantine this experiment?')}
                onConfirm={() => handleQuarantine(record.id, 'Manual quarantine')}
              >
                <Tooltip title={t('experiments.quarantine', 'Quarantine')}>
                  <Button
                    danger
                    size="small"
                    icon={<StopOutlined />}
                  />
                </Tooltip>
              </Popconfirm>
            </>
          )}
        </Space>
      )
    }
  ];

  return (
    <div className="experiment-management">
      <div className="page-header">
        <Title level={2}>{t('experiments.title', 'Experiment Management')}</Title>
        <Button
          type="primary"
          icon={<PlusOutlined />}
          onClick={() => setCreateModalVisible(true)}
        >
          {t('experiments.create', 'Create Experiment')}
        </Button>
      </div>

      <Card>
        <Table
          columns={columns}
          dataSource={experiments}
          rowKey="id"
          loading={loading}
          pagination={{ pageSize: 10 }}
        />
      </Card>

      {/* Create Modal */}
      <Modal
        title={t('experiments.create_title', 'Create New Experiment')}
        open={createModalVisible}
        onCancel={() => setCreateModalVisible(false)}
        footer={null}
      >
        <Form form={form} layout="vertical" onFinish={handleCreate}>
          <Form.Item
            name="name"
            label={t('experiments.name', 'Name')}
            rules={[{ required: true }]}
          >
            <Input placeholder="e.g., gpt-4-turbo-security-v2" />
          </Form.Item>
          <Form.Item
            name="model"
            label={t('experiments.model', 'Model')}
            rules={[{ required: true }]}
          >
            <Select>
              <Select.Option value="gpt-4-turbo">GPT-4 Turbo</Select.Option>
              <Select.Option value="gpt-4">GPT-4</Select.Option>
              <Select.Option value="claude-3-opus">Claude 3 Opus</Select.Option>
              <Select.Option value="claude-3-sonnet">Claude 3 Sonnet</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item
            name="temperature"
            label={t('experiments.temperature', 'Temperature')}
            rules={[{ required: true }]}
            initialValue="0.7"
          >
            <Input type="number" step="0.1" min="0" max="2" />
          </Form.Item>
          <Form.Item
            name="prompt_template"
            label={t('experiments.prompt_template', 'Prompt Template')}
            rules={[{ required: true }]}
          >
            <Select>
              <Select.Option value="security_expert_v1">Security Expert v1</Select.Option>
              <Select.Option value="security_expert_v2">Security Expert v2</Select.Option>
              <Select.Option value="code_quality_v1">Code Quality v1</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item
            name="dataset_id"
            label={t('experiments.dataset', 'Dataset')}
            rules={[{ required: true }]}
          >
            <Select>
              <Select.Option value="test-dataset-100">Test Dataset (100 samples)</Select.Option>
              <Select.Option value="test-dataset-500">Test Dataset (500 samples)</Select.Option>
              <Select.Option value="production-sample">Production Sample</Select.Option>
            </Select>
          </Form.Item>
          <Form.Item>
            <Space>
              <Button type="primary" htmlType="submit">
                {t('common.create', 'Create')}
              </Button>
              <Button onClick={() => setCreateModalVisible(false)}>
                {t('common.cancel', 'Cancel')}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>

      {/* Detail Modal */}
      <Modal
        title={selectedExperiment?.name}
        open={detailModalVisible}
        onCancel={() => setDetailModalVisible(false)}
        footer={null}
        width={700}
      >
        {selectedExperiment && (
          <Descriptions bordered column={2}>
            <Descriptions.Item label={t('experiments.status', 'Status')}>
              {getStatusTag(selectedExperiment.status)}
            </Descriptions.Item>
            <Descriptions.Item label={t('experiments.model', 'Model')}>
              {selectedExperiment.config.model}
            </Descriptions.Item>
            <Descriptions.Item label={t('experiments.temperature', 'Temperature')}>
              {selectedExperiment.config.temperature}
            </Descriptions.Item>
            <Descriptions.Item label={t('experiments.prompt_template', 'Prompt Template')}>
              {selectedExperiment.config.prompt_template}
            </Descriptions.Item>
            {selectedExperiment.metrics && (
              <>
                <Descriptions.Item label={t('experiments.accuracy', 'Accuracy')}>
                  <Progress
                    percent={Math.round(selectedExperiment.metrics.accuracy * 100)}
                    status={selectedExperiment.metrics.accuracy >= 0.85 ? 'success' : 'exception'}
                  />
                </Descriptions.Item>
                <Descriptions.Item label={t('experiments.error_rate', 'Error Rate')}>
                  {(selectedExperiment.metrics.error_rate * 100).toFixed(2)}%
                </Descriptions.Item>
                <Descriptions.Item label={t('experiments.latency', 'Latency (p95)')}>
                  {selectedExperiment.metrics.latency_p95.toFixed(2)}s
                </Descriptions.Item>
                <Descriptions.Item label={t('experiments.cost', 'Cost/Analysis')}>
                  ${selectedExperiment.metrics.cost_per_analysis.toFixed(4)}
                </Descriptions.Item>
              </>
            )}
          </Descriptions>
        )}
      </Modal>
    </div>
  );
};

export default ExperimentManagement;

/**
 * Learning Cycle Dashboard
 * 
 * Dashboard for monitoring the AI continuous learning cycle:
 * - Learning sources and channels
 * - Knowledge base updates
 * - Model fine-tuning status
 * - Learning metrics and trends
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Button,
  Table,
  Tag,
  Space,
  Typography,
  Statistic,
  Progress,
  Timeline,
  Alert,
  Badge,
  Tooltip,
  Spin,
  Switch,
  List,
  Avatar,
} from 'antd';
import {
  BookOutlined,
  CloudDownloadOutlined,
  SyncOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  DatabaseOutlined,
  BranchesOutlined,
  FileTextOutlined,
  GithubOutlined,
  GlobalOutlined,
  RocketOutlined,
  BarChartOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';

const { Title, Text, Paragraph } = Typography;

interface LearningSource {
  id: string;
  name: string;
  type: 'github' | 'papers' | 'blogs' | 'docs' | 'feedback';
  enabled: boolean;
  lastSync: string;
  itemsProcessed: number;
  status: 'active' | 'syncing' | 'error' | 'paused';
}

interface KnowledgeUpdate {
  id: string;
  source: string;
  title: string;
  type: string;
  timestamp: string;
  impact: 'high' | 'medium' | 'low';
}

interface LearningMetrics {
  totalKnowledgeItems: number;
  itemsToday: number;
  learningAccuracy: number;
  modelVersion: string;
  lastFineTune: string;
  nextScheduledTune: string;
}

const LearningCycleDashboard: React.FC = () => {
  const { t } = useTranslation();
  const [loading, setLoading] = useState(true);
  const [sources, setSources] = useState<LearningSource[]>([]);
  const [updates, setUpdates] = useState<KnowledgeUpdate[]>([]);
  const [metrics, setMetrics] = useState<LearningMetrics | null>(null);
  const [cycleRunning, setCycleRunning] = useState(true);

  useEffect(() => {
    // Simulate fetching data
    const timer = setTimeout(() => {
      setSources([
        {
          id: 'src-001',
          name: 'GitHub Repositories',
          type: 'github',
          enabled: true,
          lastSync: new Date().toISOString(),
          itemsProcessed: 15420,
          status: 'active',
        },
        {
          id: 'src-002',
          name: 'arXiv Papers',
          type: 'papers',
          enabled: true,
          lastSync: new Date(Date.now() - 3600000).toISOString(),
          itemsProcessed: 2340,
          status: 'active',
        },
        {
          id: 'src-003',
          name: 'Tech Blogs',
          type: 'blogs',
          enabled: true,
          lastSync: new Date(Date.now() - 7200000).toISOString(),
          itemsProcessed: 8750,
          status: 'syncing',
        },
        {
          id: 'src-004',
          name: 'Documentation',
          type: 'docs',
          enabled: true,
          lastSync: new Date(Date.now() - 1800000).toISOString(),
          itemsProcessed: 4280,
          status: 'active',
        },
        {
          id: 'src-005',
          name: 'User Feedback',
          type: 'feedback',
          enabled: true,
          lastSync: new Date().toISOString(),
          itemsProcessed: 1250,
          status: 'active',
        },
      ]);

      setUpdates([
        {
          id: 'upd-001',
          source: 'GitHub',
          title: 'New React 19 patterns detected',
          type: 'Pattern',
          timestamp: new Date().toISOString(),
          impact: 'high',
        },
        {
          id: 'upd-002',
          source: 'arXiv',
          title: 'GQA attention optimization paper',
          type: 'Research',
          timestamp: new Date(Date.now() - 1800000).toISOString(),
          impact: 'medium',
        },
        {
          id: 'upd-003',
          source: 'Feedback',
          title: 'Security pattern improvement',
          type: 'Improvement',
          timestamp: new Date(Date.now() - 3600000).toISOString(),
          impact: 'high',
        },
        {
          id: 'upd-004',
          source: 'Blogs',
          title: 'TypeScript 5.3 features',
          type: 'Pattern',
          timestamp: new Date(Date.now() - 7200000).toISOString(),
          impact: 'medium',
        },
      ]);

      setMetrics({
        totalKnowledgeItems: 32040,
        itemsToday: 245,
        learningAccuracy: 0.94,
        modelVersion: 'v2.3.1',
        lastFineTune: new Date(Date.now() - 86400000).toISOString(),
        nextScheduledTune: new Date(Date.now() + 43200000).toISOString(),
      });

      setLoading(false);
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  const getSourceIcon = (type: string) => {
    switch (type) {
      case 'github':
        return <GithubOutlined />;
      case 'papers':
        return <FileTextOutlined />;
      case 'blogs':
        return <GlobalOutlined />;
      case 'docs':
        return <BookOutlined />;
      case 'feedback':
        return <BranchesOutlined />;
      default:
        return <DatabaseOutlined />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active':
        return 'success';
      case 'syncing':
        return 'processing';
      case 'error':
        return 'error';
      case 'paused':
        return 'default';
      default:
        return 'default';
    }
  };

  const getImpactColor = (impact: string) => {
    switch (impact) {
      case 'high':
        return 'red';
      case 'medium':
        return 'orange';
      case 'low':
        return 'blue';
      default:
        return 'default';
    }
  };

  const sourceColumns = [
    {
      title: 'Source',
      dataIndex: 'name',
      key: 'name',
      render: (name: string, record: LearningSource) => (
        <Space>
          {getSourceIcon(record.type)}
          <Text strong>{name}</Text>
        </Space>
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Badge
          status={getStatusColor(status) as any}
          text={status.charAt(0).toUpperCase() + status.slice(1)}
        />
      ),
    },
    {
      title: 'Items Processed',
      dataIndex: 'itemsProcessed',
      key: 'itemsProcessed',
      render: (count: number) => count.toLocaleString(),
    },
    {
      title: 'Last Sync',
      dataIndex: 'lastSync',
      key: 'lastSync',
      render: (date: string) => new Date(date).toLocaleTimeString(),
    },
    {
      title: 'Enabled',
      dataIndex: 'enabled',
      key: 'enabled',
      render: (enabled: boolean, record: LearningSource) => (
        <Switch
          checked={enabled}
          size="small"
          onChange={(checked) => {
            setSources((prev) =>
              prev.map((s) =>
                s.id === record.id ? { ...s, enabled: checked } : s
              )
            );
          }}
        />
      ),
    },
  ];

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 400 }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: 24 }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3} style={{ margin: 0 }}>
            <BookOutlined style={{ marginRight: 8 }} />
            Learning Cycle Dashboard
          </Title>
        </Col>
        <Col>
          <Space>
            <Badge
              status={cycleRunning ? 'processing' : 'default'}
              text={cycleRunning ? 'Learning Active' : 'Paused'}
            />
            <Button
              type={cycleRunning ? 'default' : 'primary'}
              icon={cycleRunning ? <ClockCircleOutlined /> : <RocketOutlined />}
              onClick={() => setCycleRunning(!cycleRunning)}
            >
              {cycleRunning ? 'Pause Learning' : 'Resume Learning'}
            </Button>
            <Button icon={<SyncOutlined />}>Sync All</Button>
          </Space>
        </Col>
      </Row>

      {/* Metrics */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Knowledge Items"
              value={metrics?.totalKnowledgeItems || 0}
              prefix={<DatabaseOutlined />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Learned Today"
              value={metrics?.itemsToday || 0}
              prefix={<CloudDownloadOutlined />}
              valueStyle={{ color: '#3f8600' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Learning Accuracy"
              value={(metrics?.learningAccuracy || 0) * 100}
              precision={1}
              suffix="%"
              prefix={<BarChartOutlined />}
              valueStyle={{ color: '#1890ff' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Model Version"
              value={metrics?.modelVersion || 'N/A'}
              prefix={<BranchesOutlined />}
            />
          </Card>
        </Col>
      </Row>

      {/* Fine-tuning Status */}
      <Alert
        message="Next Fine-tuning Scheduled"
        description={
          <Space>
            <Text>
              Last fine-tune:{' '}
              {metrics?.lastFineTune
                ? new Date(metrics.lastFineTune).toLocaleString()
                : 'Never'}
            </Text>
            <Text type="secondary">|</Text>
            <Text>
              Next scheduled:{' '}
              {metrics?.nextScheduledTune
                ? new Date(metrics.nextScheduledTune).toLocaleString()
                : 'Not scheduled'}
            </Text>
          </Space>
        }
        type="info"
        showIcon
        icon={<ClockCircleOutlined />}
        style={{ marginBottom: 24 }}
      />

      <Row gutter={24}>
        {/* Learning Sources */}
        <Col xs={24} lg={14}>
          <Card title="Learning Sources" style={{ marginBottom: 24 }}>
            <Table
              columns={sourceColumns}
              dataSource={sources}
              rowKey="id"
              size="small"
              pagination={false}
            />
          </Card>
        </Col>

        {/* Recent Updates */}
        <Col xs={24} lg={10}>
          <Card title="Recent Knowledge Updates">
            <List
              itemLayout="horizontal"
              dataSource={updates}
              renderItem={(item) => (
                <List.Item>
                  <List.Item.Meta
                    avatar={
                      <Avatar
                        style={{
                          backgroundColor:
                            item.impact === 'high'
                              ? '#ff4d4f'
                              : item.impact === 'medium'
                              ? '#faad14'
                              : '#1890ff',
                        }}
                        icon={<BookOutlined />}
                      />
                    }
                    title={
                      <Space>
                        <Text strong>{item.title}</Text>
                        <Tag color={getImpactColor(item.impact)}>
                          {item.impact.toUpperCase()}
                        </Tag>
                      </Space>
                    }
                    description={
                      <Space>
                        <Tag>{item.source}</Tag>
                        <Text type="secondary">
                          {new Date(item.timestamp).toLocaleTimeString()}
                        </Text>
                      </Space>
                    }
                  />
                </List.Item>
              )}
            />
          </Card>
        </Col>
      </Row>

      {/* Learning Progress */}
      <Card title="Learning Progress by Category" style={{ marginTop: 24 }}>
        <Row gutter={[16, 16]}>
          {[
            { name: 'Security Patterns', progress: 92, color: '#ff4d4f' },
            { name: 'Code Quality', progress: 88, color: '#52c41a' },
            { name: 'Performance', progress: 75, color: '#1890ff' },
            { name: 'Best Practices', progress: 95, color: '#722ed1' },
            { name: 'New Frameworks', progress: 68, color: '#faad14' },
          ].map((category) => (
            <Col xs={24} sm={12} md={8} lg={4} key={category.name}>
              <div style={{ textAlign: 'center' }}>
                <Progress
                  type="circle"
                  percent={category.progress}
                  strokeColor={category.color}
                  width={80}
                />
                <div style={{ marginTop: 8 }}>
                  <Text>{category.name}</Text>
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </Card>
    </div>
  );
};

export default LearningCycleDashboard;

/**
 * Model Comparison Dashboard
 * 
 * Compare AI models across different metrics:
 * - Accuracy comparison
 * - Latency benchmarks
 * - Cost analysis
 * - A/B testing results
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Button,
  Table,
  Tag,
  Space,
  Typography,
  Statistic,
  Progress,
  Select,
  Radio,
  Tooltip,
  Spin,
  Divider,
  Badge,
} from 'antd';
import {
  BarChartOutlined,
  LineChartOutlined,
  SwapOutlined,
  ThunderboltOutlined,
  DollarOutlined,
  CheckCircleOutlined,
  ClockCircleOutlined,
  TrophyOutlined,
  ExperimentOutlined,
  RobotOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';

const { Title, Text } = Typography;
const { Option } = Select;

interface ModelMetrics {
  id: string;
  name: string;
  version: string;
  accuracy: number;
  precision: number;
  recall: number;
  f1Score: number;
  latencyP50: number;
  latencyP95: number;
  latencyP99: number;
  costPer1k: number;
  errorRate: number;
  throughput: number;
  requestsToday: number;
}

interface ComparisonResult {
  metric: string;
  model1Value: number | string;
  model2Value: number | string;
  winner: 'model1' | 'model2' | 'tie';
  difference: string;
}

const ModelComparison: React.FC = () => {
  const { t } = useTranslation();
  const [loading, setLoading] = useState(true);
  const [models, setModels] = useState<ModelMetrics[]>([]);
  const [selectedModel1, setSelectedModel1] = useState<string>('');
  const [selectedModel2, setSelectedModel2] = useState<string>('');
  const [comparisonType, setComparisonType] = useState<'accuracy' | 'performance' | 'cost'>('accuracy');

  useEffect(() => {
    const timer = setTimeout(() => {
      const mockModels: ModelMetrics[] = [
        {
          id: 'gpt4-review',
          name: 'GPT-4 Code Review',
          version: 'v2',
          accuracy: 0.94,
          precision: 0.92,
          recall: 0.95,
          f1Score: 0.935,
          latencyP50: 1800,
          latencyP95: 2500,
          latencyP99: 3200,
          costPer1k: 0.03,
          errorRate: 0.02,
          throughput: 150,
          requestsToday: 1250,
        },
        {
          id: 'claude3-security',
          name: 'Claude-3 Security',
          version: 'v2',
          accuracy: 0.92,
          precision: 0.94,
          recall: 0.90,
          f1Score: 0.92,
          latencyP50: 2100,
          latencyP95: 2800,
          latencyP99: 3500,
          costPer1k: 0.025,
          errorRate: 0.015,
          throughput: 120,
          requestsToday: 890,
        },
        {
          id: 'gqa-attention',
          name: 'GQA Attention Model',
          version: 'v1',
          accuracy: 0.87,
          precision: 0.85,
          recall: 0.88,
          f1Score: 0.865,
          latencyP50: 2500,
          latencyP95: 3200,
          latencyP99: 4000,
          costPer1k: 0.02,
          errorRate: 0.05,
          throughput: 100,
          requestsToday: 150,
        },
        {
          id: 'llama-quality',
          name: 'LLaMA Quality Analyzer',
          version: 'v1',
          accuracy: 0.85,
          precision: 0.83,
          recall: 0.86,
          f1Score: 0.845,
          latencyP50: 1500,
          latencyP95: 2000,
          latencyP99: 2500,
          costPer1k: 0.01,
          errorRate: 0.04,
          throughput: 200,
          requestsToday: 320,
        },
      ];
      setModels(mockModels);
      setSelectedModel1(mockModels[0].id);
      setSelectedModel2(mockModels[1].id);
      setLoading(false);
    }, 500);

    return () => clearTimeout(timer);
  }, []);

  const getModel = (id: string) => models.find((m) => m.id === id);

  const getComparisonResults = (): ComparisonResult[] => {
    const model1 = getModel(selectedModel1);
    const model2 = getModel(selectedModel2);
    if (!model1 || !model2) return [];

    const results: ComparisonResult[] = [];

    if (comparisonType === 'accuracy') {
      results.push(
        {
          metric: 'Accuracy',
          model1Value: `${(model1.accuracy * 100).toFixed(1)}%`,
          model2Value: `${(model2.accuracy * 100).toFixed(1)}%`,
          winner: model1.accuracy > model2.accuracy ? 'model1' : model1.accuracy < model2.accuracy ? 'model2' : 'tie',
          difference: `${Math.abs((model1.accuracy - model2.accuracy) * 100).toFixed(1)}%`,
        },
        {
          metric: 'Precision',
          model1Value: `${(model1.precision * 100).toFixed(1)}%`,
          model2Value: `${(model2.precision * 100).toFixed(1)}%`,
          winner: model1.precision > model2.precision ? 'model1' : model1.precision < model2.precision ? 'model2' : 'tie',
          difference: `${Math.abs((model1.precision - model2.precision) * 100).toFixed(1)}%`,
        },
        {
          metric: 'Recall',
          model1Value: `${(model1.recall * 100).toFixed(1)}%`,
          model2Value: `${(model2.recall * 100).toFixed(1)}%`,
          winner: model1.recall > model2.recall ? 'model1' : model1.recall < model2.recall ? 'model2' : 'tie',
          difference: `${Math.abs((model1.recall - model2.recall) * 100).toFixed(1)}%`,
        },
        {
          metric: 'F1 Score',
          model1Value: `${(model1.f1Score * 100).toFixed(1)}%`,
          model2Value: `${(model2.f1Score * 100).toFixed(1)}%`,
          winner: model1.f1Score > model2.f1Score ? 'model1' : model1.f1Score < model2.f1Score ? 'model2' : 'tie',
          difference: `${Math.abs((model1.f1Score - model2.f1Score) * 100).toFixed(1)}%`,
        },
        {
          metric: 'Error Rate',
          model1Value: `${(model1.errorRate * 100).toFixed(2)}%`,
          model2Value: `${(model2.errorRate * 100).toFixed(2)}%`,
          winner: model1.errorRate < model2.errorRate ? 'model1' : model1.errorRate > model2.errorRate ? 'model2' : 'tie',
          difference: `${Math.abs((model1.errorRate - model2.errorRate) * 100).toFixed(2)}%`,
        }
      );
    } else if (comparisonType === 'performance') {
      results.push(
        {
          metric: 'Latency P50',
          model1Value: `${model1.latencyP50}ms`,
          model2Value: `${model2.latencyP50}ms`,
          winner: model1.latencyP50 < model2.latencyP50 ? 'model1' : model1.latencyP50 > model2.latencyP50 ? 'model2' : 'tie',
          difference: `${Math.abs(model1.latencyP50 - model2.latencyP50)}ms`,
        },
        {
          metric: 'Latency P95',
          model1Value: `${model1.latencyP95}ms`,
          model2Value: `${model2.latencyP95}ms`,
          winner: model1.latencyP95 < model2.latencyP95 ? 'model1' : model1.latencyP95 > model2.latencyP95 ? 'model2' : 'tie',
          difference: `${Math.abs(model1.latencyP95 - model2.latencyP95)}ms`,
        },
        {
          metric: 'Latency P99',
          model1Value: `${model1.latencyP99}ms`,
          model2Value: `${model2.latencyP99}ms`,
          winner: model1.latencyP99 < model2.latencyP99 ? 'model1' : model1.latencyP99 > model2.latencyP99 ? 'model2' : 'tie',
          difference: `${Math.abs(model1.latencyP99 - model2.latencyP99)}ms`,
        },
        {
          metric: 'Throughput',
          model1Value: `${model1.throughput} req/s`,
          model2Value: `${model2.throughput} req/s`,
          winner: model1.throughput > model2.throughput ? 'model1' : model1.throughput < model2.throughput ? 'model2' : 'tie',
          difference: `${Math.abs(model1.throughput - model2.throughput)} req/s`,
        }
      );
    } else {
      results.push(
        {
          metric: 'Cost per 1K tokens',
          model1Value: `$${model1.costPer1k.toFixed(3)}`,
          model2Value: `$${model2.costPer1k.toFixed(3)}`,
          winner: model1.costPer1k < model2.costPer1k ? 'model1' : model1.costPer1k > model2.costPer1k ? 'model2' : 'tie',
          difference: `$${Math.abs(model1.costPer1k - model2.costPer1k).toFixed(3)}`,
        },
        {
          metric: 'Requests Today',
          model1Value: model1.requestsToday.toLocaleString(),
          model2Value: model2.requestsToday.toLocaleString(),
          winner: model1.requestsToday > model2.requestsToday ? 'model1' : model1.requestsToday < model2.requestsToday ? 'model2' : 'tie',
          difference: Math.abs(model1.requestsToday - model2.requestsToday).toLocaleString(),
        },
        {
          metric: 'Est. Daily Cost',
          model1Value: `$${((model1.requestsToday * model1.costPer1k) / 10).toFixed(2)}`,
          model2Value: `$${((model2.requestsToday * model2.costPer1k) / 10).toFixed(2)}`,
          winner: (model1.requestsToday * model1.costPer1k) < (model2.requestsToday * model2.costPer1k) ? 'model1' : 'model2',
          difference: `$${Math.abs((model1.requestsToday * model1.costPer1k - model2.requestsToday * model2.costPer1k) / 10).toFixed(2)}`,
        }
      );
    }

    return results;
  };

  const comparisonColumns = [
    {
      title: 'Metric',
      dataIndex: 'metric',
      key: 'metric',
      render: (text: string) => <Text strong>{text}</Text>,
    },
    {
      title: getModel(selectedModel1)?.name || 'Model 1',
      dataIndex: 'model1Value',
      key: 'model1Value',
      render: (value: string, record: ComparisonResult) => (
        <Space>
          <Text>{value}</Text>
          {record.winner === 'model1' && <TrophyOutlined style={{ color: '#52c41a' }} />}
        </Space>
      ),
    },
    {
      title: getModel(selectedModel2)?.name || 'Model 2',
      dataIndex: 'model2Value',
      key: 'model2Value',
      render: (value: string, record: ComparisonResult) => (
        <Space>
          <Text>{value}</Text>
          {record.winner === 'model2' && <TrophyOutlined style={{ color: '#52c41a' }} />}
        </Space>
      ),
    },
    {
      title: 'Difference',
      dataIndex: 'difference',
      key: 'difference',
      render: (text: string) => <Tag color="blue">{text}</Tag>,
    },
  ];

  const getWinnerCount = () => {
    const results = getComparisonResults();
    const model1Wins = results.filter((r) => r.winner === 'model1').length;
    const model2Wins = results.filter((r) => r.winner === 'model2').length;
    return { model1Wins, model2Wins };
  };

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 400 }}>
        <Spin size="large" />
      </div>
    );
  }

  const { model1Wins, model2Wins } = getWinnerCount();

  return (
    <div style={{ padding: 24 }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3} style={{ margin: 0 }}>
            <BarChartOutlined style={{ marginRight: 8 }} />
            Model Comparison
          </Title>
        </Col>
        <Col>
          <Button type="primary" icon={<ExperimentOutlined />}>
            Run A/B Test
          </Button>
        </Col>
      </Row>

      {/* Model Selection */}
      <Card style={{ marginBottom: 24 }}>
        <Row gutter={24} align="middle">
          <Col xs={24} md={10}>
            <Text type="secondary">Model 1</Text>
            <Select
              value={selectedModel1}
              onChange={setSelectedModel1}
              style={{ width: '100%', marginTop: 8 }}
              size="large"
            >
              {models.map((model) => (
                <Option key={model.id} value={model.id} disabled={model.id === selectedModel2}>
                  <Space>
                    <RobotOutlined />
                    {model.name}
                    <Tag color={model.version === 'v2' ? 'green' : 'orange'}>{model.version}</Tag>
                  </Space>
                </Option>
              ))}
            </Select>
          </Col>
          <Col xs={24} md={4} style={{ textAlign: 'center' }}>
            <SwapOutlined style={{ fontSize: 24, color: '#1890ff' }} />
          </Col>
          <Col xs={24} md={10}>
            <Text type="secondary">Model 2</Text>
            <Select
              value={selectedModel2}
              onChange={setSelectedModel2}
              style={{ width: '100%', marginTop: 8 }}
              size="large"
            >
              {models.map((model) => (
                <Option key={model.id} value={model.id} disabled={model.id === selectedModel1}>
                  <Space>
                    <RobotOutlined />
                    {model.name}
                    <Tag color={model.version === 'v2' ? 'green' : 'orange'}>{model.version}</Tag>
                  </Space>
                </Option>
              ))}
            </Select>
          </Col>
        </Row>
      </Card>

      {/* Winner Summary */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} md={8}>
          <Card>
            <Statistic
              title={getModel(selectedModel1)?.name}
              value={model1Wins}
              suffix={`/ ${getComparisonResults().length} wins`}
              prefix={<TrophyOutlined />}
              valueStyle={{ color: model1Wins > model2Wins ? '#52c41a' : '#888' }}
            />
          </Card>
        </Col>
        <Col xs={24} md={8}>
          <Card style={{ textAlign: 'center' }}>
            <Text type="secondary">Comparison Type</Text>
            <div style={{ marginTop: 12 }}>
              <Radio.Group
                value={comparisonType}
                onChange={(e) => setComparisonType(e.target.value)}
                buttonStyle="solid"
              >
                <Radio.Button value="accuracy">
                  <CheckCircleOutlined /> Accuracy
                </Radio.Button>
                <Radio.Button value="performance">
                  <ClockCircleOutlined /> Performance
                </Radio.Button>
                <Radio.Button value="cost">
                  <DollarOutlined /> Cost
                </Radio.Button>
              </Radio.Group>
            </div>
          </Card>
        </Col>
        <Col xs={24} md={8}>
          <Card>
            <Statistic
              title={getModel(selectedModel2)?.name}
              value={model2Wins}
              suffix={`/ ${getComparisonResults().length} wins`}
              prefix={<TrophyOutlined />}
              valueStyle={{ color: model2Wins > model1Wins ? '#52c41a' : '#888' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Comparison Table */}
      <Card title="Detailed Comparison">
        <Table
          columns={comparisonColumns}
          dataSource={getComparisonResults()}
          rowKey="metric"
          pagination={false}
          size="middle"
        />
      </Card>

      {/* Visual Comparison */}
      <Card title="Visual Comparison" style={{ marginTop: 24 }}>
        <Row gutter={[16, 16]}>
          {getComparisonResults().map((result) => (
            <Col xs={24} sm={12} md={8} key={result.metric}>
              <div style={{ textAlign: 'center', padding: 16 }}>
                <Text type="secondary">{result.metric}</Text>
                <div style={{ marginTop: 8, display: 'flex', justifyContent: 'center', gap: 16 }}>
                  <div>
                    <Progress
                      type="circle"
                      percent={typeof result.model1Value === 'string' && result.model1Value.includes('%')
                        ? parseFloat(result.model1Value)
                        : 75}
                      width={60}
                      strokeColor={result.winner === 'model1' ? '#52c41a' : '#1890ff'}
                      format={() => (
                        <Text style={{ fontSize: 10 }}>{String(result.model1Value).slice(0, 6)}</Text>
                      )}
                    />
                    <div style={{ marginTop: 4 }}>
                      <Text style={{ fontSize: 10 }}>
                        {getModel(selectedModel1)?.name.slice(0, 10)}
                      </Text>
                    </div>
                  </div>
                  <div>
                    <Progress
                      type="circle"
                      percent={typeof result.model2Value === 'string' && result.model2Value.includes('%')
                        ? parseFloat(result.model2Value)
                        : 75}
                      width={60}
                      strokeColor={result.winner === 'model2' ? '#52c41a' : '#1890ff'}
                      format={() => (
                        <Text style={{ fontSize: 10 }}>{String(result.model2Value).slice(0, 6)}</Text>
                      )}
                    />
                    <div style={{ marginTop: 4 }}>
                      <Text style={{ fontSize: 10 }}>
                        {getModel(selectedModel2)?.name.slice(0, 10)}
                      </Text>
                    </div>
                  </div>
                </div>
              </div>
            </Col>
          ))}
        </Row>
      </Card>
    </div>
  );
};

export default ModelComparison;

/**
 * Performance Monitor Dashboard
 * 
 * Real-time performance monitoring for the AI platform:
 * - Service latency metrics
 * - Request throughput
 * - Error rates
 * - Resource utilization
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Statistic,
  Progress,
  Table,
  Tag,
  Space,
  Select,
  Button,
  Alert,
  Badge,
} from 'antd';
import {
  DashboardOutlined,
  ThunderboltOutlined,
  ApiOutlined,
  ClockCircleOutlined,
  WarningOutlined,
  CheckCircleOutlined,
  SyncOutlined,
  ArrowUpOutlined,
  ArrowDownOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  AreaChart,
  Area,
} from 'recharts';

const { Title, Text } = Typography;
const { Option } = Select;

interface ServiceMetric {
  name: string;
  latencyP50: number;
  latencyP95: number;
  latencyP99: number;
  requestsPerSecond: number;
  errorRate: number;
  status: 'healthy' | 'degraded' | 'unhealthy';
}

interface TimeSeriesData {
  time: string;
  latency: number;
  requests: number;
  errors: number;
}

const PerformanceMonitor: React.FC = () => {
  const { t } = useTranslation();
  const [timeRange, setTimeRange] = useState<'1h' | '6h' | '24h' | '7d'>('1h');
  const [refreshing, setRefreshing] = useState(false);
  const [services, setServices] = useState<ServiceMetric[]>([]);
  const [timeSeriesData, setTimeSeriesData] = useState<TimeSeriesData[]>([]);

  // Generate mock data
  useEffect(() => {
    const generateMockData = () => {
      // Services
      setServices([
        { name: 'API Gateway', latencyP50: 45, latencyP95: 120, latencyP99: 250, requestsPerSecond: 1250, errorRate: 0.02, status: 'healthy' },
        { name: 'Auth Service', latencyP50: 32, latencyP95: 85, latencyP99: 150, requestsPerSecond: 450, errorRate: 0.01, status: 'healthy' },
        { name: 'Analysis Service', latencyP50: 1800, latencyP95: 2500, latencyP99: 3500, requestsPerSecond: 85, errorRate: 0.03, status: 'healthy' },
        { name: 'AI Orchestrator', latencyP50: 2200, latencyP95: 3000, latencyP99: 4500, requestsPerSecond: 45, errorRate: 0.05, status: 'degraded' },
        { name: 'Database', latencyP50: 5, latencyP95: 15, latencyP99: 35, requestsPerSecond: 2500, errorRate: 0.001, status: 'healthy' },
        { name: 'Redis Cache', latencyP50: 1, latencyP95: 3, latencyP99: 8, requestsPerSecond: 5000, errorRate: 0.0001, status: 'healthy' },
      ]);

      // Time series
      const now = Date.now();
      const data: TimeSeriesData[] = [];
      for (let i = 59; i >= 0; i--) {
        const time = new Date(now - i * 60000);
        data.push({
          time: time.toLocaleTimeString('en-US', { hour: '2-digit', minute: '2-digit' }),
          latency: 150 + Math.random() * 100 + Math.sin(i / 10) * 50,
          requests: 1000 + Math.random() * 500 + Math.sin(i / 5) * 200,
          errors: Math.floor(Math.random() * 5),
        });
      }
      setTimeSeriesData(data);
    };

    generateMockData();
    const interval = setInterval(generateMockData, 30000);
    return () => clearInterval(interval);
  }, [timeRange]);

  const handleRefresh = () => {
    setRefreshing(true);
    setTimeout(() => setRefreshing(false), 1000);
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'healthy': return 'success';
      case 'degraded': return 'warning';
      case 'unhealthy': return 'error';
      default: return 'default';
    }
  };

  const serviceColumns = [
    {
      title: 'Service',
      dataIndex: 'name',
      key: 'name',
      render: (name: string, record: ServiceMetric) => (
        <Space>
          <Badge status={getStatusColor(record.status) as any} />
          <Text strong>{name}</Text>
        </Space>
      ),
    },
    {
      title: 'P50 Latency',
      dataIndex: 'latencyP50',
      key: 'latencyP50',
      render: (val: number) => <Text>{val}ms</Text>,
      sorter: (a: ServiceMetric, b: ServiceMetric) => a.latencyP50 - b.latencyP50,
    },
    {
      title: 'P95 Latency',
      dataIndex: 'latencyP95',
      key: 'latencyP95',
      render: (val: number) => <Text>{val}ms</Text>,
      sorter: (a: ServiceMetric, b: ServiceMetric) => a.latencyP95 - b.latencyP95,
    },
    {
      title: 'P99 Latency',
      dataIndex: 'latencyP99',
      key: 'latencyP99',
      render: (val: number) => (
        <Text type={val > 3000 ? 'danger' : undefined}>{val}ms</Text>
      ),
      sorter: (a: ServiceMetric, b: ServiceMetric) => a.latencyP99 - b.latencyP99,
    },
    {
      title: 'RPS',
      dataIndex: 'requestsPerSecond',
      key: 'requestsPerSecond',
      render: (val: number) => <Text>{val.toLocaleString()}</Text>,
      sorter: (a: ServiceMetric, b: ServiceMetric) => a.requestsPerSecond - b.requestsPerSecond,
    },
    {
      title: 'Error Rate',
      dataIndex: 'errorRate',
      key: 'errorRate',
      render: (val: number) => (
        <Tag color={val > 0.05 ? 'red' : val > 0.02 ? 'orange' : 'green'}>
          {(val * 100).toFixed(2)}%
        </Tag>
      ),
      sorter: (a: ServiceMetric, b: ServiceMetric) => a.errorRate - b.errorRate,
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      render: (status: string) => (
        <Tag color={getStatusColor(status)}>
          {status.toUpperCase()}
        </Tag>
      ),
    },
  ];

  // Calculate aggregates
  const totalRequests = services.reduce((sum, s) => sum + s.requestsPerSecond, 0);
  const avgLatency = services.reduce((sum, s) => sum + s.latencyP50, 0) / services.length;
  const avgErrorRate = services.reduce((sum, s) => sum + s.errorRate, 0) / services.length;
  const healthyServices = services.filter(s => s.status === 'healthy').length;

  return (
    <div style={{ padding: 24 }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3} style={{ margin: 0 }}>
            <DashboardOutlined style={{ marginRight: 8 }} />
            Performance Monitor
          </Title>
        </Col>
        <Col>
          <Space>
            <Select value={timeRange} onChange={setTimeRange} style={{ width: 100 }}>
              <Option value="1h">1 Hour</Option>
              <Option value="6h">6 Hours</Option>
              <Option value="24h">24 Hours</Option>
              <Option value="7d">7 Days</Option>
            </Select>
            <Button 
              icon={<SyncOutlined spin={refreshing} />} 
              onClick={handleRefresh}
            >
              Refresh
            </Button>
          </Space>
        </Col>
      </Row>

      {/* Alert for degraded services */}
      {services.some(s => s.status !== 'healthy') && (
        <Alert
          message="Performance Degradation Detected"
          description={`${services.filter(s => s.status !== 'healthy').length} service(s) showing degraded performance.`}
          type="warning"
          showIcon
          icon={<WarningOutlined />}
          style={{ marginBottom: 24 }}
        />
      )}

      {/* Overview Stats */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Total Requests/sec"
              value={totalRequests}
              prefix={<ThunderboltOutlined />}
              suffix={
                <Text type="success" style={{ fontSize: 14 }}>
                  <ArrowUpOutlined /> 12%
                </Text>
              }
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Avg Latency (P50)"
              value={Math.round(avgLatency)}
              suffix="ms"
              prefix={<ClockCircleOutlined />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Avg Error Rate"
              value={(avgErrorRate * 100).toFixed(2)}
              suffix="%"
              prefix={<WarningOutlined />}
              valueStyle={{ color: avgErrorRate > 0.03 ? '#cf1322' : '#3f8600' }}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Healthy Services"
              value={healthyServices}
              suffix={`/ ${services.length}`}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: healthyServices === services.length ? '#3f8600' : '#faad14' }}
            />
          </Card>
        </Col>
      </Row>

      {/* Charts */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} lg={12}>
          <Card title="Latency Trend (ms)">
            <ResponsiveContainer width="100%" height={250}>
              <AreaChart data={timeSeriesData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="time" tick={{ fontSize: 10 }} />
                <YAxis />
                <Tooltip />
                <Area 
                  type="monotone" 
                  dataKey="latency" 
                  stroke="#1890ff" 
                  fill="#1890ff" 
                  fillOpacity={0.3} 
                />
              </AreaChart>
            </ResponsiveContainer>
          </Card>
        </Col>
        <Col xs={24} lg={12}>
          <Card title="Request Throughput">
            <ResponsiveContainer width="100%" height={250}>
              <LineChart data={timeSeriesData}>
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis dataKey="time" tick={{ fontSize: 10 }} />
                <YAxis />
                <Tooltip />
                <Line 
                  type="monotone" 
                  dataKey="requests" 
                  stroke="#52c41a" 
                  strokeWidth={2}
                  dot={false}
                />
              </LineChart>
            </ResponsiveContainer>
          </Card>
        </Col>
      </Row>

      {/* Service Table */}
      <Card title="Service Metrics">
        <Table
          columns={serviceColumns}
          dataSource={services}
          rowKey="name"
          pagination={false}
          size="middle"
        />
      </Card>

      {/* SLO Compliance */}
      <Card title="SLO Compliance" style={{ marginTop: 24 }}>
        <Row gutter={[24, 24]}>
          {[
            { name: 'Availability (99.9%)', current: 99.95, target: 99.9 },
            { name: 'P95 Latency (< 3s)', current: 2.5, target: 3, unit: 's', inverse: true },
            { name: 'Error Rate (< 2%)', current: 1.2, target: 2, unit: '%', inverse: true },
            { name: 'Throughput (> 1000 RPS)', current: 1250, target: 1000, unit: ' RPS' },
          ].map((slo) => {
            const met = slo.inverse 
              ? slo.current <= slo.target 
              : slo.current >= slo.target;
            const percent = slo.inverse
              ? Math.min(100, (slo.target / slo.current) * 100)
              : Math.min(100, (slo.current / slo.target) * 100);
            
            return (
              <Col xs={24} sm={12} md={6} key={slo.name}>
                <div style={{ textAlign: 'center' }}>
                  <Progress
                    type="dashboard"
                    percent={percent}
                    status={met ? 'success' : 'exception'}
                    format={() => (
                      <div>
                        <div style={{ fontSize: 18, fontWeight: 600 }}>
                          {slo.current}{slo.unit || '%'}
                        </div>
                        <div style={{ fontSize: 12, color: '#888' }}>
                          Target: {slo.target}{slo.unit || '%'}
                        </div>
                      </div>
                    )}
                  />
                  <div style={{ marginTop: 8 }}>
                    <Text>{slo.name}</Text>
                  </div>
                </div>
              </Col>
            );
          })}
        </Row>
      </Card>
    </div>
  );
};

export default PerformanceMonitor;

/**
 * Provider Management Page
 * 
 * Admin page for managing AI providers with:
 * - Provider list with status and metrics
 * - Provider configuration
 * - Model management
 * - Health monitoring
 * - Performance charts
 */

import React, { useState, useCallback, useMemo } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Button,
  Space,
  Tag,
  Badge,
  Progress,
  Statistic,
  List,
  Avatar,
  Modal,
  Form,
  Input,
  InputNumber,
  Select,
  Switch,
  Table,
  Tooltip,
  Alert,
  Divider,
  Tabs,
  Skeleton,
  Empty,
  Descriptions,
  message,
} from 'antd';
import type { TableProps, TabsProps } from 'antd';
import {
  ApiOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  ExclamationCircleOutlined,
  WarningOutlined,
  ThunderboltOutlined,
  DollarOutlined,
  ClockCircleOutlined,
  ReloadOutlined,
  SettingOutlined,
  EditOutlined,
  PlayCircleOutlined,
  PauseCircleOutlined,
  KeyOutlined,
  LinkOutlined,
  RobotOutlined,
  ExperimentOutlined,
  DatabaseOutlined,
  CloudOutlined,
  DesktopOutlined,
  LineChartOutlined,
  SyncOutlined,
  SafetyOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import {
  LineChart,
  Line,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip as RechartsTooltip,
  ResponsiveContainer,
  Legend,
} from 'recharts';
import {
  useAdminStore,
  type AIProvider,
  type ProviderModel,
  type ProviderType,
  type ProviderStatus,
} from '../../store/adminStore';
import {
  useProviders,
  useProvider,
  useUpdateProvider,
  useUpdateProviderApiKey,
  useTestProvider,
  useProviderHealth,
  useProviderMetrics,
  useProviderModels,
  useUpdateProviderModel,
  useSetFallbackOrder,
} from '../../hooks/useAdmin';
import './ProviderManagement.css';

const { Title, Text, Paragraph } = Typography;

/** Provider icons */
const providerIcons: Record<ProviderType, React.ReactNode> = {
  openai: <RobotOutlined />,
  anthropic: <ExperimentOutlined />,
  google: <CloudOutlined />,
  azure: <CloudOutlined />,
  huggingface: <DatabaseOutlined />,
  local: <DesktopOutlined />,
};

/** Provider colors */
const providerColors: Record<ProviderType, string> = {
  openai: '#10a37f',
  anthropic: '#cc785c',
  google: '#4285f4',
  azure: '#0078d4',
  huggingface: '#ff6b00',
  local: '#722ed1',
};

/** Status colors */
const statusColors: Record<ProviderStatus, string> = {
  active: 'green',
  inactive: 'default',
  error: 'red',
  rate_limited: 'orange',
};

/** Status icons */
const statusIcons: Record<ProviderStatus, React.ReactNode> = {
  active: <CheckCircleOutlined />,
  inactive: <PauseCircleOutlined />,
  error: <CloseCircleOutlined />,
  rate_limited: <ExclamationCircleOutlined />,
};

/**
 * Provider Card Component
 */
const ProviderCard: React.FC<{
  provider: AIProvider;
  selected: boolean;
  onSelect: () => void;
  onTest: () => void;
  testing: boolean;
}> = ({ provider, selected, onSelect, onTest, testing }) => {
  const { t } = useTranslation();

  return (
    <Card
      className={`provider-card ${selected ? 'selected' : ''}`}
      hoverable
      onClick={onSelect}
    >
      <div className="provider-card-header">
        <Avatar
          icon={providerIcons[provider.type]}
          style={{ backgroundColor: providerColors[provider.type] }}
          size={48}
        />
        <div className="provider-info">
          <Text strong>{provider.name}</Text>
          <Space>
            <Tag color={statusColors[provider.status]} icon={statusIcons[provider.status]}>
              {provider.status}
            </Tag>
            {provider.isDefault && (
              <Tag color="blue">{t('admin.providers.default', 'Default')}</Tag>
            )}
          </Space>
        </div>
        <Button
          size="small"
          icon={<ThunderboltOutlined />}
          onClick={(e) => {
            e.stopPropagation();
            onTest();
          }}
          loading={testing}
        >
          {t('admin.providers.test', 'Test')}
        </Button>
      </div>

      <Divider style={{ margin: '12px 0' }} />

      <Row gutter={16}>
        <Col span={8}>
          <Statistic
            title={t('admin.providers.requests', 'Requests')}
            value={provider.requestsToday}
            valueStyle={{ fontSize: 16 }}
          />
        </Col>
        <Col span={8}>
          <Statistic
            title={t('admin.providers.latency', 'Latency')}
            value={provider.avgResponseTime}
            suffix="ms"
            valueStyle={{ fontSize: 16 }}
          />
        </Col>
        <Col span={8}>
          <Statistic
            title={t('admin.providers.cost', 'Cost')}
            value={provider.costToday}
            prefix="$"
            precision={2}
            valueStyle={{ fontSize: 16 }}
          />
        </Col>
      </Row>

      <div className="provider-quota">
        <Text type="secondary">{t('admin.providers.quota', 'Quota Usage')}</Text>
        <Progress
          percent={Math.round((provider.quotaUsed / provider.quotaLimit) * 100)}
          size="small"
          status={provider.quotaUsed / provider.quotaLimit > 0.9 ? 'exception' : 'active'}
        />
      </div>

      {provider.lastError && (
        <Alert
          message={provider.lastError}
          type="error"
          showIcon
          style={{ marginTop: 12 }}
        />
      )}
    </Card>
  );
};

/**
 * Provider Configuration Panel
 */
const ProviderConfigPanel: React.FC<{
  providerId: string;
}> = ({ providerId }) => {
  const { t } = useTranslation();
  const [configForm] = Form.useForm();
  const [apiKeyForm] = Form.useForm();
  const [showApiKey, setShowApiKey] = useState(false);

  const { data: provider, isLoading } = useProvider(providerId);
  const { data: health } = useProviderHealth(providerId);
  const { data: metrics } = useProviderMetrics(providerId, 'day');
  const { data: models } = useProviderModels(providerId);
  
  const updateProvider = useUpdateProvider();
  const updateApiKey = useUpdateProviderApiKey();
  const updateModel = useUpdateProviderModel();

  const handleConfigSave = useCallback(async (values: any) => {
    await updateProvider.mutateAsync({ providerId, data: values });
  }, [providerId, updateProvider]);

  const handleApiKeySave = useCallback(async (values: any) => {
    await updateApiKey.mutateAsync({ providerId, apiKey: values.apiKey });
    apiKeyForm.resetFields();
    setShowApiKey(false);
  }, [providerId, updateApiKey, apiKeyForm]);

  const modelColumns: TableProps<ProviderModel>['columns'] = [
    {
      title: t('admin.providers.model', 'Model'),
      dataIndex: 'displayName',
      key: 'displayName',
    },
    {
      title: t('admin.providers.type', 'Type'),
      dataIndex: 'type',
      key: 'type',
      render: (type: string) => <Tag>{type}</Tag>,
    },
    {
      title: t('admin.providers.enabled', 'Enabled'),
      dataIndex: 'isEnabled',
      key: 'isEnabled',
      render: (enabled: boolean, record) => (
        <Switch
          checked={enabled}
          onChange={(checked) => updateModel.mutate({
            providerId,
            modelId: record.id,
            data: { isEnabled: checked },
          })}
          size="small"
        />
      ),
    },
    {
      title: t('admin.providers.default', 'Default'),
      dataIndex: 'isDefault',
      key: 'isDefault',
      render: (isDefault: boolean, record) => (
        <Switch
          checked={isDefault}
          onChange={(checked) => updateModel.mutate({
            providerId,
            modelId: record.id,
            data: { isDefault: checked },
          })}
          size="small"
          disabled={!record.isEnabled}
        />
      ),
    },
    {
      title: t('admin.providers.input_cost', 'Input $/1K'),
      dataIndex: 'costPerInputToken',
      key: 'costPerInputToken',
      render: (cost: number) => `$${(cost * 1000).toFixed(4)}`,
    },
    {
      title: t('admin.providers.output_cost', 'Output $/1K'),
      dataIndex: 'costPerOutputToken',
      key: 'costPerOutputToken',
      render: (cost: number) => `$${(cost * 1000).toFixed(4)}`,
    },
    {
      title: t('admin.providers.requests', 'Requests'),
      dataIndex: 'requestsToday',
      key: 'requestsToday',
    },
  ];

  if (isLoading) {
    return <Skeleton active paragraph={{ rows: 10 }} />;
  }

  if (!provider) {
    return <Empty description={t('admin.providers.select', 'Select a provider')} />;
  }

  const tabItems: TabsProps['items'] = [
    {
      key: 'overview',
      label: <><ApiOutlined /> {t('admin.providers.overview', 'Overview')}</>,
      children: (
        <div className="provider-overview">
          <Descriptions bordered column={2} size="small">
            <Descriptions.Item label={t('admin.providers.status', 'Status')}>
              <Badge status={provider.status === 'active' ? 'success' : 'error'} />
              {provider.status}
            </Descriptions.Item>
            <Descriptions.Item label={t('admin.providers.priority', 'Priority')}>
              {provider.priority}
            </Descriptions.Item>
            <Descriptions.Item label={t('admin.providers.api_key', 'API Key')}>
              {provider.apiKeyConfigured ? (
                <Tag color="green" icon={<CheckCircleOutlined />}>
                  {t('admin.providers.configured', 'Configured')}
                </Tag>
              ) : (
                <Tag color="red" icon={<CloseCircleOutlined />}>
                  {t('admin.providers.not_configured', 'Not Configured')}
                </Tag>
              )}
            </Descriptions.Item>
            <Descriptions.Item label={t('admin.providers.last_checked', 'Last Checked')}>
              {provider.lastChecked ? new Date(provider.lastChecked).toLocaleString() : '-'}
            </Descriptions.Item>
            <Descriptions.Item label={t('admin.providers.error_rate', 'Error Rate')}>
              <Progress 
                percent={provider.errorRate * 100} 
                size="small" 
                status={provider.errorRate > 0.05 ? 'exception' : 'normal'}
                format={(p) => `${p?.toFixed(1)}%`}
              />
            </Descriptions.Item>
            <Descriptions.Item label={t('admin.providers.monthly_cost', 'Monthly Cost')}>
              ${provider.costThisMonth.toFixed(2)}
            </Descriptions.Item>
          </Descriptions>

          {/* Health Status */}
          {health && (
            <Card title={t('admin.providers.health', 'Health Status')} size="small" className="health-card">
              <Row gutter={16}>
                <Col span={8}>
                  <Statistic
                    title={t('admin.providers.uptime', 'Uptime')}
                    value={health.uptime}
                    suffix="%"
                    precision={2}
                  />
                </Col>
                <Col span={8}>
                  <Statistic
                    title={t('admin.providers.latency', 'Latency')}
                    value={health.latency}
                    suffix="ms"
                  />
                </Col>
                <Col span={8}>
                  <Statistic
                    title={t('admin.providers.last_check', 'Last Check')}
                    value={new Date(health.lastCheck).toLocaleTimeString()}
                  />
                </Col>
              </Row>
              
              {health.recentErrors.length > 0 && (
                <div className="recent-errors">
                  <Text strong>{t('admin.providers.recent_errors', 'Recent Errors')}</Text>
                  <List
                    size="small"
                    dataSource={health.recentErrors.slice(0, 5)}
                    renderItem={(error) => (
                      <List.Item>
                        <Text type="danger">{error.message}</Text>
                        <Text type="secondary" style={{ fontSize: 12 }}>
                          {new Date(error.timestamp).toLocaleString()}
                        </Text>
                      </List.Item>
                    )}
                  />
                </div>
              )}
            </Card>
          )}
        </div>
      ),
    },
    {
      key: 'config',
      label: <><SettingOutlined /> {t('admin.providers.configuration', 'Configuration')}</>,
      children: (
        <div className="provider-config">
          <Form
            form={configForm}
            layout="vertical"
            initialValues={{
              name: provider.name,
              priority: provider.priority,
              isDefault: provider.isDefault,
              status: provider.status,
            }}
            onFinish={handleConfigSave}
          >
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item name="name" label={t('admin.providers.name', 'Name')}>
                  <Input />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item name="priority" label={t('admin.providers.priority', 'Priority')}>
                  <InputNumber min={1} max={10} style={{ width: '100%' }} />
                </Form.Item>
              </Col>
            </Row>
            <Row gutter={16}>
              <Col span={12}>
                <Form.Item name="status" label={t('admin.providers.status', 'Status')}>
                  <Select
                    options={[
                      { value: 'active', label: 'Active' },
                      { value: 'inactive', label: 'Inactive' },
                    ]}
                  />
                </Form.Item>
              </Col>
              <Col span={12}>
                <Form.Item name="isDefault" label={t('admin.providers.default', 'Default Provider')} valuePropName="checked">
                  <Switch />
                </Form.Item>
              </Col>
            </Row>
            <Form.Item>
              <Button type="primary" htmlType="submit" loading={updateProvider.isPending}>
                {t('common.save', 'Save')}
              </Button>
            </Form.Item>
          </Form>

          <Divider />

          {/* API Key */}
          <Title level={5}><KeyOutlined /> {t('admin.providers.api_key', 'API Key')}</Title>
          {!showApiKey ? (
            <Space>
              <Text>
                {provider.apiKeyConfigured ? '鈥⑩€⑩€⑩€⑩€⑩€⑩€⑩€⑩€⑩€⑩€⑩€⑩€⑩€⑩€⑩€? : t('admin.providers.no_api_key', 'No API key configured')}
              </Text>
              <Button icon={<EditOutlined />} onClick={() => setShowApiKey(true)}>
                {t('admin.providers.update_key', 'Update')}
              </Button>
            </Space>
          ) : (
            <Form form={apiKeyForm} layout="inline" onFinish={handleApiKeySave}>
              <Form.Item name="apiKey" rules={[{ required: true }]}>
                <Input.Password placeholder="Enter API key" style={{ width: 300 }} />
              </Form.Item>
              <Form.Item>
                <Space>
                  <Button type="primary" htmlType="submit" loading={updateApiKey.isPending}>
                    {t('common.save', 'Save')}
                  </Button>
                  <Button onClick={() => setShowApiKey(false)}>
                    {t('common.cancel', 'Cancel')}
                  </Button>
                </Space>
              </Form.Item>
            </Form>
          )}
        </div>
      ),
    },
    {
      key: 'models',
      label: <><RobotOutlined /> {t('admin.providers.models', 'Models')}</>,
      children: (
        <Table
          columns={modelColumns}
          dataSource={models || []}
          rowKey="id"
          size="small"
          pagination={false}
        />
      ),
    },
    {
      key: 'metrics',
      label: <><LineChartOutlined /> {t('admin.providers.metrics', 'Metrics')}</>,
      children: metrics ? (
        <Row gutter={[16, 16]}>
          <Col span={24}>
            <Card title={t('admin.providers.response_time', 'Response Time')} size="small">
              <ResponsiveContainer width="100%" height={200}>
                <LineChart data={metrics.responseTimeTrend}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="timestamp" fontSize={12} />
                  <YAxis fontSize={12} />
                  <RechartsTooltip />
                  <Line type="monotone" dataKey="value" stroke="#1890ff" strokeWidth={2} name="Response Time (ms)" />
                </LineChart>
              </ResponsiveContainer>
            </Card>
          </Col>
          <Col span={12}>
            <Card title={t('admin.providers.error_rate', 'Error Rate')} size="small">
              <ResponsiveContainer width="100%" height={150}>
                <AreaChart data={metrics.errorRateTrend}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="timestamp" fontSize={10} />
                  <YAxis fontSize={10} />
                  <RechartsTooltip />
                  <Area type="monotone" dataKey="value" stroke="#f5222d" fill="#fff1f0" name="Error Rate (%)" />
                </AreaChart>
              </ResponsiveContainer>
            </Card>
          </Col>
          <Col span={12}>
            <Card title={t('admin.providers.cost_trend', 'Cost Trend')} size="small">
              <ResponsiveContainer width="100%" height={150}>
                <AreaChart data={metrics.costTrend}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="timestamp" fontSize={10} />
                  <YAxis fontSize={10} />
                  <RechartsTooltip />
                  <Area type="monotone" dataKey="value" stroke="#52c41a" fill="#f6ffed" name="Cost ($)" />
                </AreaChart>
              </ResponsiveContainer>
            </Card>
          </Col>
        </Row>
      ) : (
        <Empty description={t('admin.providers.no_metrics', 'No metrics available')} />
      ),
    },
  ];

  return (
    <div className="provider-config-panel">
      <div className="panel-header">
        <Space>
          <Avatar
            icon={providerIcons[provider.type]}
            style={{ backgroundColor: providerColors[provider.type] }}
          />
          <div>
            <Title level={4} style={{ margin: 0 }}>{provider.name}</Title>
            <Text type="secondary">{provider.type}</Text>
          </div>
        </Space>
      </div>
      <Tabs items={tabItems} />
    </div>
  );
};

/**
 * Main Provider Management Component
 */
export const ProviderManagement: React.FC = () => {
  const { t } = useTranslation();
  const { selectedProviderId, selectProvider } = useAdminStore();
  
  const { data: providers, isLoading, refetch, isFetching } = useProviders();
  const testProvider = useTestProvider();

  const handleTest = useCallback((providerId: string) => {
    testProvider.mutate(providerId);
  }, [testProvider]);

  // Summary stats
  const stats = useMemo(() => {
    if (!providers) return null;
    return {
      total: providers.length,
      active: providers.filter(p => p.status === 'active').length,
      totalRequests: providers.reduce((sum, p) => sum + p.requestsToday, 0),
      totalCost: providers.reduce((sum, p) => sum + p.costToday, 0),
    };
  }, [providers]);

  return (
    <div className="provider-management" role="main" aria-label={t('admin.providers.title', 'Provider Management')}>
      <div className="page-header">
        <Title level={2}><ApiOutlined /> {t('admin.providers.title', 'AI Provider Management')}</Title>
        <Button 
          icon={<ReloadOutlined />} 
          onClick={() => refetch()}
          loading={isFetching}
        >
          {t('common.refresh', 'Refresh')}
        </Button>
      </div>

      {/* Summary Stats */}
      {stats && (
        <Row gutter={16} className="provider-stats">
          <Col xs={12} sm={6}>
            <Card>
              <Statistic
                title={t('admin.providers.total', 'Total Providers')}
                value={stats.total}
                prefix={<ApiOutlined />}
              />
            </Card>
          </Col>
          <Col xs={12} sm={6}>
            <Card>
              <Statistic
                title={t('admin.providers.active', 'Active')}
                value={stats.active}
                prefix={<CheckCircleOutlined />}
                valueStyle={{ color: '#52c41a' }}
              />
            </Card>
          </Col>
          <Col xs={12} sm={6}>
            <Card>
              <Statistic
                title={t('admin.providers.requests_today', 'Requests Today')}
                value={stats.totalRequests}
                prefix={<ThunderboltOutlined />}
              />
            </Card>
          </Col>
          <Col xs={12} sm={6}>
            <Card>
              <Statistic
                title={t('admin.providers.cost_today', 'Cost Today')}
                value={stats.totalCost}
                prefix={<DollarOutlined />}
                precision={2}
              />
            </Card>
          </Col>
        </Row>
      )}

      {/* Main Content */}
      <Row gutter={24}>
        {/* Provider List */}
        <Col xs={24} lg={8}>
          <Card 
            title={t('admin.providers.providers', 'Providers')} 
            className="providers-list-card"
            loading={isLoading}
          >
            {providers?.map(provider => (
              <ProviderCard
                key={provider.id}
                provider={provider}
                selected={selectedProviderId === provider.id}
                onSelect={() => selectProvider(provider.id)}
                onTest={() => handleTest(provider.id)}
                testing={testProvider.isPending}
              />
            ))}
            {!providers?.length && (
              <Empty description={t('admin.providers.no_providers', 'No providers configured')} />
            )}
          </Card>
        </Col>

        {/* Configuration Panel */}
        <Col xs={24} lg={16}>
          <Card className="config-panel-card">
            {selectedProviderId ? (
              <ProviderConfigPanel providerId={selectedProviderId} />
            ) : (
              <Empty description={t('admin.providers.select', 'Select a provider to configure')} />
            )}
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default ProviderManagement;

/**
 * AI Security Scanner Dashboard
 * 
 * Automated security vulnerability detection and remediation:
 * - Real-time scanning status
 * - Vulnerability categorization (OWASP Top 10)
 * - Auto-fix suggestions with AI confidence
 * - Compliance tracking (SOC2, GDPR, HIPAA)
 */

import React, { useState, useEffect } from 'react';
import {
  Card,
  Row,
  Col,
  Typography,
  Statistic,
  Progress,
  Table,
  Tag,
  Space,
  Button,
  Alert,
  Badge,
  Timeline,
  Tabs,
  Switch,
  Tooltip,
  Modal,
} from 'antd';
import {
  SafetyCertificateOutlined,
  BugOutlined,
  CheckCircleOutlined,
  ExclamationCircleOutlined,
  CloseCircleOutlined,
  SyncOutlined,
  ThunderboltOutlined,
  EyeOutlined,
  SecurityScanOutlined,
  FileProtectOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import {
  PieChart,
  Pie,
  Cell,
  ResponsiveContainer,
  Tooltip as RechartsTooltip,
  BarChart,
  Bar,
  XAxis,
  YAxis,
  CartesianGrid,
} from 'recharts';

const { Title, Text, Paragraph } = Typography;
const { TabPane } = Tabs;

interface Vulnerability {
  id: string;
  title: string;
  category: string;
  severity: 'critical' | 'high' | 'medium' | 'low';
  cweId: string;
  file: string;
  line: number;
  description: string;
  autoFixAvailable: boolean;
  aiConfidence: number;
  status: 'open' | 'fixing' | 'fixed' | 'ignored';
  discoveredAt: string;
}

interface ComplianceStatus {
  framework: string;
  status: 'compliant' | 'partial' | 'non-compliant';
  score: number;
  issues: number;
}

const SecurityScanner: React.FC = () => {
  const { t } = useTranslation();
  const [scanning, setScanning] = useState(false);
  const [autoFixEnabled, setAutoFixEnabled] = useState(true);
  const [vulnerabilities, setVulnerabilities] = useState<Vulnerability[]>([]);
  const [selectedVuln, setSelectedVuln] = useState<Vulnerability | null>(null);
  const [detailModalOpen, setDetailModalOpen] = useState(false);

  // Mock data
  useEffect(() => {
    setVulnerabilities([
      {
        id: 'vuln-001',
        title: 'SQL Injection Vulnerability',
        category: 'A03:2021-Injection',
        severity: 'critical',
        cweId: 'CWE-89',
        file: 'src/api/users.py',
        line: 145,
        description: 'User input directly concatenated into SQL query without sanitization.',
        autoFixAvailable: true,
        aiConfidence: 0.96,
        status: 'open',
        discoveredAt: new Date(Date.now() - 3600000).toISOString(),
      },
      {
        id: 'vuln-002',
        title: 'Cross-Site Scripting (XSS)',
        category: 'A03:2021-Injection',
        severity: 'high',
        cweId: 'CWE-79',
        file: 'src/components/Comment.tsx',
        line: 67,
        description: 'User-provided HTML rendered without escaping.',
        autoFixAvailable: true,
        aiConfidence: 0.92,
        status: 'fixing',
        discoveredAt: new Date(Date.now() - 7200000).toISOString(),
      },
      {
        id: 'vuln-003',
        title: 'Insecure Direct Object Reference',
        category: 'A01:2021-Broken Access Control',
        severity: 'high',
        cweId: 'CWE-639',
        file: 'src/api/documents.py',
        line: 89,
        description: 'Document access not properly authorized based on user permissions.',
        autoFixAvailable: true,
        aiConfidence: 0.88,
        status: 'open',
        discoveredAt: new Date(Date.now() - 14400000).toISOString(),
      },
      {
        id: 'vuln-004',
        title: 'Hardcoded Credentials',
        category: 'A07:2021-Identification Failures',
        severity: 'critical',
        cweId: 'CWE-798',
        file: 'src/config/database.py',
        line: 23,
        description: 'Database credentials hardcoded in configuration file.',
        autoFixAvailable: true,
        aiConfidence: 0.99,
        status: 'fixed',
        discoveredAt: new Date(Date.now() - 86400000).toISOString(),
      },
      {
        id: 'vuln-005',
        title: 'Missing Rate Limiting',
        category: 'A04:2021-Insecure Design',
        severity: 'medium',
        cweId: 'CWE-770',
        file: 'src/api/auth.py',
        line: 34,
        description: 'Login endpoint lacks rate limiting, vulnerable to brute force.',
        autoFixAvailable: false,
        aiConfidence: 0.75,
        status: 'open',
        discoveredAt: new Date(Date.now() - 172800000).toISOString(),
      },
      {
        id: 'vuln-006',
        title: 'Outdated Dependency',
        category: 'A06:2021-Vulnerable Components',
        severity: 'medium',
        cweId: 'CWE-1104',
        file: 'package.json',
        line: 15,
        description: 'lodash@4.17.20 has known prototype pollution vulnerability.',
        autoFixAvailable: true,
        aiConfidence: 0.95,
        status: 'open',
        discoveredAt: new Date(Date.now() - 259200000).toISOString(),
      },
    ]);
  }, []);

  const complianceData: ComplianceStatus[] = [
    { framework: 'SOC 2 Type II', status: 'compliant', score: 94, issues: 3 },
    { framework: 'GDPR', status: 'partial', score: 87, issues: 8 },
    { framework: 'HIPAA', status: 'partial', score: 82, issues: 12 },
    { framework: 'PCI DSS', status: 'compliant', score: 96, issues: 2 },
  ];

  const severityColors: Record<string, string> = {
    critical: '#ff4d4f',
    high: '#fa8c16',
    medium: '#faad14',
    low: '#52c41a',
  };

  const severityData = [
    { name: 'Critical', value: vulnerabilities.filter(v => v.severity === 'critical').length, color: severityColors.critical },
    { name: 'High', value: vulnerabilities.filter(v => v.severity === 'high').length, color: severityColors.high },
    { name: 'Medium', value: vulnerabilities.filter(v => v.severity === 'medium').length, color: severityColors.medium },
    { name: 'Low', value: vulnerabilities.filter(v => v.severity === 'low').length, color: severityColors.low },
  ];

  const categoryData = [
    { name: 'Injection', count: 2 },
    { name: 'Access Control', count: 1 },
    { name: 'Auth Failures', count: 1 },
    { name: 'Insecure Design', count: 1 },
    { name: 'Vuln Components', count: 1 },
  ];

  const handleScan = () => {
    setScanning(true);
    setTimeout(() => setScanning(false), 3000);
  };

  const handleAutoFix = (vuln: Vulnerability) => {
    setVulnerabilities(prev =>
      prev.map(v => v.id === vuln.id ? { ...v, status: 'fixing' } : v)
    );
    setTimeout(() => {
      setVulnerabilities(prev =>
        prev.map(v => v.id === vuln.id ? { ...v, status: 'fixed' } : v)
      );
    }, 2000);
  };

  const vulnColumns = [
    {
      title: 'Severity',
      dataIndex: 'severity',
      key: 'severity',
      width: 100,
      render: (severity: string) => (
        <Tag color={severityColors[severity]}>{severity.toUpperCase()}</Tag>
      ),
      sorter: (a: Vulnerability, b: Vulnerability) => {
        const order = { critical: 0, high: 1, medium: 2, low: 3 };
        return order[a.severity] - order[b.severity];
      },
    },
    {
      title: 'Vulnerability',
      dataIndex: 'title',
      key: 'title',
      render: (title: string, record: Vulnerability) => (
        <Space direction="vertical" size={0}>
          <Text strong>{title}</Text>
          <Text type="secondary" style={{ fontSize: 12 }}>{record.cweId}</Text>
        </Space>
      ),
    },
    {
      title: 'Category',
      dataIndex: 'category',
      key: 'category',
      render: (cat: string) => <Tag>{cat}</Tag>,
    },
    {
      title: 'Location',
      key: 'location',
      render: (_: any, record: Vulnerability) => (
        <Text code>{record.file}:{record.line}</Text>
      ),
    },
    {
      title: 'AI Fix',
      key: 'autoFix',
      width: 120,
      render: (_: any, record: Vulnerability) => (
        record.autoFixAvailable ? (
          <Tooltip title={`AI Confidence: ${(record.aiConfidence * 100).toFixed(0)}%`}>
            <Tag color="blue" icon={<ThunderboltOutlined />}>
              {(record.aiConfidence * 100).toFixed(0)}%
            </Tag>
          </Tooltip>
        ) : (
          <Tag>Manual</Tag>
        )
      ),
    },
    {
      title: 'Status',
      dataIndex: 'status',
      key: 'status',
      width: 100,
      render: (status: string) => {
        const config: Record<string, { color: string; icon: React.ReactNode }> = {
          open: { color: 'red', icon: <ExclamationCircleOutlined /> },
          fixing: { color: 'blue', icon: <SyncOutlined spin /> },
          fixed: { color: 'green', icon: <CheckCircleOutlined /> },
          ignored: { color: 'default', icon: <CloseCircleOutlined /> },
        };
        return (
          <Tag color={config[status]?.color} icon={config[status]?.icon}>
            {status.toUpperCase()}
          </Tag>
        );
      },
    },
    {
      title: 'Actions',
      key: 'actions',
      width: 150,
      render: (_: any, record: Vulnerability) => (
        <Space>
          <Button
            size="small"
            icon={<EyeOutlined />}
            onClick={() => {
              setSelectedVuln(record);
              setDetailModalOpen(true);
            }}
          />
          {record.autoFixAvailable && record.status === 'open' && (
            <Button
              size="small"
              type="primary"
              icon={<ThunderboltOutlined />}
              onClick={() => handleAutoFix(record)}
            >
              Fix
            </Button>
          )}
        </Space>
      ),
    },
  ];

  const openVulns = vulnerabilities.filter(v => v.status === 'open').length;
  const criticalVulns = vulnerabilities.filter(v => v.severity === 'critical' && v.status !== 'fixed').length;
  const fixedVulns = vulnerabilities.filter(v => v.status === 'fixed').length;
  const autoFixable = vulnerabilities.filter(v => v.autoFixAvailable && v.status === 'open').length;

  return (
    <div style={{ padding: 24 }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3} style={{ margin: 0 }}>
            <SecurityScanOutlined style={{ marginRight: 8 }} />
            AI Security Scanner
          </Title>
        </Col>
        <Col>
          <Space>
            <span>Auto-Fix:</span>
            <Switch checked={autoFixEnabled} onChange={setAutoFixEnabled} />
            <Button
              type="primary"
              icon={<SyncOutlined spin={scanning} />}
              onClick={handleScan}
              loading={scanning}
            >
              {scanning ? 'Scanning...' : 'Run Scan'}
            </Button>
          </Space>
        </Col>
      </Row>

      {/* Critical Alert */}
      {criticalVulns > 0 && (
        <Alert
          message={`${criticalVulns} Critical Vulnerabilities Detected`}
          description="Immediate attention required. AI auto-fix is available for most issues."
          type="error"
          showIcon
          icon={<BugOutlined />}
          style={{ marginBottom: 24 }}
          action={
            <Button size="small" danger onClick={() => {}}>
              Fix All Critical
            </Button>
          }
        />
      )}

      {/* Overview Stats */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Open Vulnerabilities"
              value={openVulns}
              valueStyle={{ color: openVulns > 0 ? '#cf1322' : '#3f8600' }}
              prefix={<BugOutlined />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Critical Issues"
              value={criticalVulns}
              valueStyle={{ color: criticalVulns > 0 ? '#cf1322' : '#3f8600' }}
              prefix={<ExclamationCircleOutlined />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Fixed This Week"
              value={fixedVulns}
              valueStyle={{ color: '#3f8600' }}
              prefix={<CheckCircleOutlined />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Auto-Fixable"
              value={autoFixable}
              valueStyle={{ color: '#1890ff' }}
              prefix={<ThunderboltOutlined />}
              suffix={
                <Button type="link" size="small" onClick={() => {}}>
                  Fix All
                </Button>
              }
            />
          </Card>
        </Col>
      </Row>

      {/* Charts Row */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} md={8}>
          <Card title="Severity Distribution">
            <ResponsiveContainer width="100%" height={200}>
              <PieChart>
                <Pie
                  data={severityData}
                  cx="50%"
                  cy="50%"
                  innerRadius={40}
                  outerRadius={80}
                  dataKey="value"
                  label={({ name, value }: any) => value > 0 ? `${name}: ${value}` : ''}
                >
                  {severityData.map((entry, index) => (
                    <Cell key={`cell-${index}`} fill={entry.color} />
                  ))}
                </Pie>
                <RechartsTooltip />
              </PieChart>
            </ResponsiveContainer>
          </Card>
        </Col>
        <Col xs={24} md={16}>
          <Card title="Vulnerabilities by Category (OWASP)">
            <ResponsiveContainer width="100%" height={200}>
              <BarChart data={categoryData} layout="vertical">
                <CartesianGrid strokeDasharray="3 3" />
                <XAxis type="number" />
                <YAxis dataKey="name" type="category" width={100} tick={{ fontSize: 12 }} />
                <RechartsTooltip />
                <Bar dataKey="count" fill="#1890ff" />
              </BarChart>
            </ResponsiveContainer>
          </Card>
        </Col>
      </Row>

      {/* Tabs for Vulnerabilities and Compliance */}
      <Card>
        <Tabs defaultActiveKey="vulnerabilities">
          <TabPane tab={<span><BugOutlined /> Vulnerabilities ({vulnerabilities.length})</span>} key="vulnerabilities">
            <Table
              columns={vulnColumns}
              dataSource={vulnerabilities}
              rowKey="id"
              pagination={{ pageSize: 10 }}
              size="middle"
            />
          </TabPane>
          <TabPane tab={<span><FileProtectOutlined /> Compliance</span>} key="compliance">
            <Row gutter={[16, 16]}>
              {complianceData.map((comp) => (
                <Col xs={24} sm={12} md={6} key={comp.framework}>
                  <Card size="small">
                    <div style={{ textAlign: 'center', marginBottom: 16 }}>
                      <Progress
                        type="circle"
                        percent={comp.score}
                        status={comp.status === 'compliant' ? 'success' : comp.status === 'partial' ? 'normal' : 'exception'}
                        width={100}
                      />
                    </div>
                    <div style={{ textAlign: 'center' }}>
                      <Text strong>{comp.framework}</Text>
                      <br />
                      <Tag color={
                        comp.status === 'compliant' ? 'green' : 
                        comp.status === 'partial' ? 'orange' : 'red'
                      }>
                        {comp.status.toUpperCase()}
                      </Tag>
                      <br />
                      <Text type="secondary">{comp.issues} issues</Text>
                    </div>
                  </Card>
                </Col>
              ))}
            </Row>
          </TabPane>
          <TabPane tab={<span><SafetyCertificateOutlined /> Scan History</span>} key="history">
            <Timeline>
              <Timeline.Item color="green">
                <Text strong>Full Security Scan Completed</Text>
                <br />
                <Text type="secondary">Today at 3:45 PM - Found 6 issues, 4 auto-fixed</Text>
              </Timeline.Item>
              <Timeline.Item color="blue">
                <Text strong>Dependency Audit</Text>
                <br />
                <Text type="secondary">Today at 2:30 PM - 2 outdated packages identified</Text>
              </Timeline.Item>
              <Timeline.Item color="green">
                <Text strong>SAST Analysis</Text>
                <br />
                <Text type="secondary">Yesterday at 11:00 AM - No new issues</Text>
              </Timeline.Item>
              <Timeline.Item color="orange">
                <Text strong>Container Scan</Text>
                <br />
                <Text type="secondary">Yesterday at 9:15 AM - 1 medium severity CVE</Text>
              </Timeline.Item>
            </Timeline>
          </TabPane>
        </Tabs>
      </Card>

      {/* Detail Modal */}
      <Modal
        title={selectedVuln?.title}
        open={detailModalOpen}
        onCancel={() => setDetailModalOpen(false)}
        footer={[
          <Button key="close" onClick={() => setDetailModalOpen(false)}>Close</Button>,
          selectedVuln?.autoFixAvailable && selectedVuln.status === 'open' && (
            <Button
              key="fix"
              type="primary"
              icon={<ThunderboltOutlined />}
              onClick={() => {
                if (selectedVuln) handleAutoFix(selectedVuln);
                setDetailModalOpen(false);
              }}
            >
              Apply AI Fix
            </Button>
          ),
        ]}
        width={700}
      >
        {selectedVuln && (
          <Space direction="vertical" style={{ width: '100%' }}>
            <Row gutter={16}>
              <Col span={12}>
                <Text type="secondary">Severity</Text>
                <br />
                <Tag color={severityColors[selectedVuln.severity]}>
                  {selectedVuln.severity.toUpperCase()}
                </Tag>
              </Col>
              <Col span={12}>
                <Text type="secondary">CWE ID</Text>
                <br />
                <Text strong>{selectedVuln.cweId}</Text>
              </Col>
            </Row>
            <div>
              <Text type="secondary">Category</Text>
              <br />
              <Tag>{selectedVuln.category}</Tag>
            </div>
            <div>
              <Text type="secondary">Location</Text>
              <br />
              <Text code>{selectedVuln.file}:{selectedVuln.line}</Text>
            </div>
            <div>
              <Text type="secondary">Description</Text>
              <Paragraph>{selectedVuln.description}</Paragraph>
            </div>
            {selectedVuln.autoFixAvailable && (
              <Alert
                message={`AI Auto-Fix Available (${(selectedVuln.aiConfidence * 100).toFixed(0)}% confidence)`}
                description="Our AI has analyzed this vulnerability and can automatically apply a secure fix."
                type="info"
                showIcon
                icon={<ThunderboltOutlined />}
              />
            )}
          </Space>
        )}
      </Modal>
    </div>
  );
};

export default SecurityScanner;

/**
 * System Health Dashboard
 * 
 * Monitor the health of all system components:
 * - Services (Backend, AI, Database)
 * - Evolution Cycle (V1/V2/V3)
 * - Auto-Fix Cycle
 * - Resource Usage
 */

import React, { useState, useEffect, useCallback } from 'react';
import {
  Card,
  Row,
  Col,
  Tag,
  Space,
  Progress,
  Statistic,
  Typography,
  Timeline,
  Alert,
  Badge,
  Tooltip,
  Spin,
  message,
} from 'antd';
import {
  CheckCircleOutlined,
  CloseCircleOutlined,
  WarningOutlined,
  SyncOutlined,
  CloudServerOutlined,
  DatabaseOutlined,
  ApiOutlined,
  RobotOutlined,
  ThunderboltOutlined,
  ClockCircleOutlined,
  DesktopOutlined,
  HddOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';

const { Title, Text } = Typography;

// Types
interface ServiceHealth {
  name: string;
  status: 'healthy' | 'degraded' | 'unhealthy';
  latency_ms: number;
  uptime_percent: number;
  last_check: string;
  error?: string;
}

interface ResourceUsage {
  cpu_percent: number;
  memory_percent: number;
  disk_percent: number;
  network_in_mbps: number;
  network_out_mbps: number;
}

interface CycleHealth {
  name: string;
  running: boolean;
  cycle_count: number;
  last_cycle: string;
  success_rate: number;
}

interface HealthEvent {
  timestamp: string;
  type: 'info' | 'warning' | 'error' | 'success';
  message: string;
  service: string;
}

// Status colors and icons
const statusConfig = {
  healthy: { color: 'green', icon: <CheckCircleOutlined /> },
  degraded: { color: 'orange', icon: <WarningOutlined /> },
  unhealthy: { color: 'red', icon: <CloseCircleOutlined /> },
};

const SystemHealth: React.FC = () => {
  const { t } = useTranslation();
  
  // State
  const [services, setServices] = useState<ServiceHealth[]>([]);
  const [resources, setResources] = useState<ResourceUsage | null>(null);
  const [cycles, setCycles] = useState<CycleHealth[]>([]);
  const [events, setEvents] = useState<HealthEvent[]>([]);
  const [loading, setLoading] = useState(true);

  // Fetch data
  const fetchData = useCallback(async () => {
    try {
      // Mock data
      const mockServices: ServiceHealth[] = [
        {
          name: 'API Gateway',
          status: 'healthy',
          latency_ms: 45,
          uptime_percent: 99.99,
          last_check: new Date().toISOString(),
        },
        {
          name: 'Auth Service',
          status: 'healthy',
          latency_ms: 32,
          uptime_percent: 99.98,
          last_check: new Date().toISOString(),
        },
        {
          name: 'Analysis Service',
          status: 'healthy',
          latency_ms: 180,
          uptime_percent: 99.95,
          last_check: new Date().toISOString(),
        },
        {
          name: 'AI Orchestrator',
          status: 'healthy',
          latency_ms: 250,
          uptime_percent: 99.90,
          last_check: new Date().toISOString(),
        },
        {
          name: 'PostgreSQL',
          status: 'healthy',
          latency_ms: 5,
          uptime_percent: 99.99,
          last_check: new Date().toISOString(),
        },
        {
          name: 'Redis',
          status: 'healthy',
          latency_ms: 1,
          uptime_percent: 99.99,
          last_check: new Date().toISOString(),
        },
        {
          name: 'Neo4j',
          status: 'degraded',
          latency_ms: 85,
          uptime_percent: 99.5,
          last_check: new Date().toISOString(),
          error: 'High memory usage detected',
        },
      ];

      const mockResources: ResourceUsage = {
        cpu_percent: 42,
        memory_percent: 68,
        disk_percent: 35,
        network_in_mbps: 125,
        network_out_mbps: 85,
      };

      const mockCycles: CycleHealth[] = [
        {
          name: 'Evolution Cycle',
          running: true,
          cycle_count: 24,
          last_cycle: new Date().toISOString(),
          success_rate: 0.95,
        },
        {
          name: 'Auto-Fix Cycle',
          running: true,
          cycle_count: 15,
          last_cycle: new Date().toISOString(),
          success_rate: 0.88,
        },
        {
          name: 'Learning Cycle',
          running: true,
          cycle_count: 120,
          last_cycle: new Date().toISOString(),
          success_rate: 0.92,
        },
      ];

      const mockEvents: HealthEvent[] = [
        {
          timestamp: new Date().toISOString(),
          type: 'success',
          message: 'Evolution cycle #24 completed successfully',
          service: 'Evolution',
        },
        {
          timestamp: new Date(Date.now() - 300000).toISOString(),
          type: 'warning',
          message: 'Neo4j memory usage above 80%',
          service: 'Neo4j',
        },
        {
          timestamp: new Date(Date.now() - 600000).toISOString(),
          type: 'info',
          message: 'Auto-fix applied 2 patches',
          service: 'Auto-Fix',
        },
        {
          timestamp: new Date(Date.now() - 900000).toISOString(),
          type: 'success',
          message: 'Model GPT-4 promoted to V2',
          service: 'AI Models',
        },
        {
          timestamp: new Date(Date.now() - 1200000).toISOString(),
          type: 'info',
          message: 'System backup completed',
          service: 'System',
        },
      ];

      setServices(mockServices);
      setResources(mockResources);
      setCycles(mockCycles);
      setEvents(mockEvents);
    } catch (error) {
      message.error('Failed to load health data');
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 30000);
    return () => clearInterval(interval);
  }, [fetchData]);

  // Calculate overall health
  const overallHealth = services.every((s) => s.status === 'healthy')
    ? 'healthy'
    : services.some((s) => s.status === 'unhealthy')
    ? 'unhealthy'
    : 'degraded';

  const healthyCount = services.filter((s) => s.status === 'healthy').length;

  // Get service icon
  const getServiceIcon = (name: string) => {
    if (name.includes('Gateway') || name.includes('API')) return <ApiOutlined />;
    if (name.includes('Database') || name.includes('SQL')) return <DatabaseOutlined />;
    if (name.includes('Redis')) return <HddOutlined />;
    if (name.includes('AI') || name.includes('Analysis')) return <RobotOutlined />;
    return <CloudServerOutlined />;
  };

  // Get event color
  const getEventColor = (type: string) => {
    switch (type) {
      case 'success':
        return 'green';
      case 'warning':
        return 'orange';
      case 'error':
        return 'red';
      default:
        return 'blue';
    }
  };

  if (loading) {
    return (
      <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: 400 }}>
        <Spin size="large" />
      </div>
    );
  }

  return (
    <div style={{ padding: 24 }}>
      {/* Header */}
      <Row justify="space-between" align="middle" style={{ marginBottom: 24 }}>
        <Col>
          <Title level={3} style={{ margin: 0 }}>
            <DesktopOutlined style={{ marginRight: 8 }} />
            System Health
          </Title>
        </Col>
        <Col>
          <Space>
            <Tag
              color={statusConfig[overallHealth].color}
              icon={statusConfig[overallHealth].icon}
              style={{ fontSize: 14, padding: '4px 12px' }}
            >
              {overallHealth.toUpperCase()}
            </Tag>
            <Text type="secondary">
              <SyncOutlined spin /> Auto-refreshing
            </Text>
          </Space>
        </Col>
      </Row>

      {/* Alert if degraded */}
      {overallHealth !== 'healthy' && (
        <Alert
          message="System Status Warning"
          description={
            services
              .filter((s) => s.status !== 'healthy')
              .map((s) => `${s.name}: ${s.error || s.status}`)
              .join('; ')
          }
          type={overallHealth === 'unhealthy' ? 'error' : 'warning'}
          showIcon
          style={{ marginBottom: 24 }}
        />
      )}

      {/* Summary Stats */}
      <Row gutter={16} style={{ marginBottom: 24 }}>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Services Healthy"
              value={healthyCount}
              suffix={`/ ${services.length}`}
              valueStyle={{
                color: healthyCount === services.length ? '#52c41a' : '#faad14',
              }}
              prefix={<CheckCircleOutlined />}
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="CPU Usage"
              value={resources?.cpu_percent || 0}
              suffix="%"
              valueStyle={{
                color: (resources?.cpu_percent || 0) > 80 ? '#ff4d4f' : '#52c41a',
              }}
            />
            <Progress
              percent={resources?.cpu_percent || 0}
              showInfo={false}
              strokeColor={(resources?.cpu_percent || 0) > 80 ? '#ff4d4f' : '#52c41a'}
              size="small"
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Memory Usage"
              value={resources?.memory_percent || 0}
              suffix="%"
              valueStyle={{
                color: (resources?.memory_percent || 0) > 80 ? '#ff4d4f' : '#52c41a',
              }}
            />
            <Progress
              percent={resources?.memory_percent || 0}
              showInfo={false}
              strokeColor={(resources?.memory_percent || 0) > 80 ? '#ff4d4f' : '#52c41a'}
              size="small"
            />
          </Card>
        </Col>
        <Col xs={24} sm={12} md={6}>
          <Card>
            <Statistic
              title="Disk Usage"
              value={resources?.disk_percent || 0}
              suffix="%"
              valueStyle={{
                color: (resources?.disk_percent || 0) > 80 ? '#ff4d4f' : '#52c41a',
              }}
            />
            <Progress
              percent={resources?.disk_percent || 0}
              showInfo={false}
              strokeColor={(resources?.disk_percent || 0) > 80 ? '#ff4d4f' : '#52c41a'}
              size="small"
            />
          </Card>
        </Col>
      </Row>

      <Row gutter={24}>
        {/* Services */}
        <Col xs={24} lg={12}>
          <Card title="Service Status" style={{ marginBottom: 24 }}>
            <Space direction="vertical" style={{ width: '100%' }}>
              {services.map((service) => (
                <div
                  key={service.name}
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '8px 0',
                    borderBottom: '1px solid #f0f0f0',
                  }}
                >
                  <Space>
                    {getServiceIcon(service.name)}
                    <Text strong>{service.name}</Text>
                  </Space>
                  <Space>
                    <Tooltip title={`Latency: ${service.latency_ms}ms`}>
                      <Text type="secondary">{service.latency_ms}ms</Text>
                    </Tooltip>
                    <Tooltip title={`Uptime: ${service.uptime_percent}%`}>
                      <Text type="secondary">{service.uptime_percent}%</Text>
                    </Tooltip>
                    <Badge
                      status={
                        service.status === 'healthy'
                          ? 'success'
                          : service.status === 'degraded'
                          ? 'warning'
                          : 'error'
                      }
                      text={service.status}
                    />
                  </Space>
                </div>
              ))}
            </Space>
          </Card>

          {/* AI Cycles */}
          <Card title="AI Cycles">
            <Space direction="vertical" style={{ width: '100%' }}>
              {cycles.map((cycle) => (
                <div
                  key={cycle.name}
                  style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '8px 0',
                    borderBottom: '1px solid #f0f0f0',
                  }}
                >
                  <Space>
                    <ThunderboltOutlined />
                    <Text strong>{cycle.name}</Text>
                  </Space>
                  <Space>
                    <Tag color={cycle.running ? 'green' : 'default'}>
                      {cycle.running ? (
                        <>
                          <SyncOutlined spin /> Running
                        </>
                      ) : (
                        'Stopped'
                      )}
                    </Tag>
                    <Text type="secondary">#{cycle.cycle_count}</Text>
                    <Progress
                      type="circle"
                      percent={Math.round(cycle.success_rate * 100)}
                      width={40}
                      strokeColor={cycle.success_rate >= 0.9 ? '#52c41a' : '#faad14'}
                    />
                  </Space>
                </div>
              ))}
            </Space>
          </Card>
        </Col>

        {/* Events Timeline */}
        <Col xs={24} lg={12}>
          <Card title="Recent Events">
            <Timeline>
              {events.map((event, index) => (
                <Timeline.Item
                  key={index}
                  color={getEventColor(event.type)}
                  dot={
                    event.type === 'success' ? (
                      <CheckCircleOutlined />
                    ) : event.type === 'warning' ? (
                      <WarningOutlined />
                    ) : event.type === 'error' ? (
                      <CloseCircleOutlined />
                    ) : (
                      <ClockCircleOutlined />
                    )
                  }
                >
                  <div>
                    <Text strong>{event.message}</Text>
                    <br />
                    <Space>
                      <Tag>{event.service}</Tag>
                      <Text type="secondary">
                        {new Date(event.timestamp).toLocaleTimeString()}
                      </Text>
                    </Space>
                  </div>
                </Timeline.Item>
              ))}
            </Timeline>
          </Card>
        </Col>
      </Row>
    </div>
  );
};

export default SystemHealth;

/**
 * User Management Page
 * 
 * Admin page for managing users with:
 * - User list table with filtering, sorting, pagination
 * - User statistics and charts
 * - Bulk operations
 * - User detail modal
 */

import React, { useState, useCallback, useMemo } from 'react';
import {
  Card,
  Table,
  Button,
  Space,
  Typography,
  Input,
  Select,
  Tag,
  Avatar,
  Modal,
  Form,
  Popconfirm,
  Dropdown,
  Statistic,
  Row,
  Col,
