/**
 * New Project Wizard Component
 */
export const NewProject: React.FC = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  
  // Store
  const { 
    draft, 
    setDraft, 
    updateDraft, 
    saveDraftToStorage, 
    loadDraftFromStorage, 
    clearDraft 
  } = useProjectStore();
  
  // Mutation
  const createProject = useCreateProject();
  
  // Local state
  const [currentStep, setCurrentStep] = useState(0);
  const [showExitModal, setShowExitModal] = useState(false);
  
  // Forms
  const [basicForm] = Form.useForm();
  const [analysisForm] = Form.useForm();
  const [notificationForm] = Form.useForm();
  
  // Initialize draft on mount
  useEffect(() => {
    loadDraftFromStorage();
    if (!draft) {
      setDraft({ ...defaultDraft });
    }
  }, []);
  
  // Set current step from draft
  useEffect(() => {
    if (draft?.step) {
      setCurrentStep(draft.step);
    }
  }, [draft?.step]);
  
  // Auto-save draft
  useEffect(() => {
    const autoSaveInterval = setInterval(() => {
      if (draft) {
        saveDraftToStorage();
      }
    }, 30000); // Auto-save every 30 seconds
    
    return () => clearInterval(autoSaveInterval);
  }, [draft, saveDraftToStorage]);
  
  // Current draft with fallback
  const currentDraft = useMemo(() => draft || defaultDraft, [draft]);
  
  // Handle values change
  const handleValuesChange = useCallback((updates: Partial<ProjectDraft>) => {
    updateDraft(updates);
  }, [updateDraft]);
  
  // Validate current step
  const validateCurrentStep = useCallback(async (): Promise<boolean> => {
    try {
      switch (currentStep) {
        case 0:
          await basicForm.validateFields();
          break;
        case 1:
          await analysisForm.validateFields();
          break;
        case 2:
          await notificationForm.validateFields();
          break;
      }
      return true;
    } catch {
      return false;
    }
  }, [currentStep, basicForm, analysisForm, notificationForm]);
  
  // Navigation
  const handleNext = useCallback(async () => {
    const isValid = await validateCurrentStep();
    if (isValid && currentStep < 3) {
      setCurrentStep(currentStep + 1);
      updateDraft({ step: currentStep + 1 });
    }
  }, [currentStep, validateCurrentStep, updateDraft]);
  
  const handlePrevious = useCallback(() => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
      updateDraft({ step: currentStep - 1 });
    }
  }, [currentStep, updateDraft]);
  
  // Save draft manually
  const handleSaveDraft = useCallback(() => {
    saveDraftToStorage();
    message.success(t('projects.wizard.draft_saved', 'Draft saved successfully'));
  }, [saveDraftToStorage, t]);
  
  // Create project
  const handleCreate = useCallback(async () => {
    const isValid = await validateCurrentStep();
    if (!isValid) return;
    
    const projectData = {
      name: currentDraft.basic_info.name,
      description: currentDraft.basic_info.description,
      repository_url: currentDraft.basic_info.repository_url,
      language: currentDraft.basic_info.language,
      settings: {
        auto_review: currentDraft.analysis_settings.analysis_frequency !== 'manual',
        review_on_push: currentDraft.analysis_settings.analysis_frequency === 'on_push',
        review_on_pr: currentDraft.analysis_settings.analysis_frequency === 'on_pr',
        severity_threshold: 'warning' as const,
        enabled_rules: [],
        ignored_paths: currentDraft.analysis_settings.excluded_patterns || [],
        analysis: currentDraft.analysis_settings,
        notifications: currentDraft.notification_settings,
      },
    };
    
    createProject.mutate(projectData, {
      onSuccess: (data) => {
        clearDraft();
        navigate(`/projects/${data.id}/settings`);
      },
    });
  }, [currentDraft, createProject, clearDraft, navigate, validateCurrentStep]);
  
  // Handle navigation away
  const handleBack = useCallback(() => {
    if (currentDraft.basic_info.name || currentDraft.basic_info.description) {
      setShowExitModal(true);
    } else {
      navigate('/projects');
    }
  }, [currentDraft, navigate]);
  
  const confirmExit = useCallback((saveDraft: boolean) => {
    if (saveDraft) {
      saveDraftToStorage();
    } else {
      clearDraft();
    }
    navigate('/projects');
  }, [saveDraftToStorage, clearDraft, navigate]);
  
  // Steps configuration
  const steps = [
    {
      title: t('projects.wizard.step_basic', 'Basic Info'),
      icon: <FolderOutlined />,
    },
    {
      title: t('projects.wizard.step_analysis', 'Analysis'),
      icon: <SettingOutlined />,
    },
    {
      title: t('projects.wizard.step_notifications', 'Notifications'),
      icon: <BellOutlined />,
    },
    {
      title: t('projects.wizard.step_review', 'Review'),
      icon: <FileSearchOutlined />,
    },
  ];
  
  // Show success result after creation
  if (createProject.isSuccess) {
    return (
      <div className="new-project-container">
        <Result
          status="success"
          title={t('projects.wizard.success_title', 'Project Created Successfully!')}
          subTitle={t('projects.wizard.success_subtitle', 'Your project is ready to use.')}
          extra={[
            <Button 
              type="primary" 
              key="settings"
              onClick={() => navigate(`/projects/${createProject.data.id}/settings`)}
            >
              {t('projects.wizard.go_to_settings', 'Go to Settings')}
            </Button>,
            <Button 
              key="list"
              onClick={() => navigate('/projects')}
            >
              {t('projects.wizard.go_to_list', 'View All Projects')}
            </Button>,
          ]}
        />
      </div>
    );
  }
  
  return (
    <div className="new-project-container" role="main" aria-label={t('projects.wizard.title', 'Create New Project')}>
      {/* Header */}
      <div className="new-project-header">
        <Space>
          <Button 
            icon={<ArrowLeftOutlined />} 
            onClick={handleBack}
            aria-label={t('common.back', 'Back')}
          />
          <div>
            <Title level={3} style={{ margin: 0 }}>
              {t('projects.wizard.title', 'Create New Project')}
            </Title>
            {currentDraft.last_saved_at && (
              <Text type="secondary" style={{ fontSize: 12 }}>
                {t('projects.wizard.last_saved', 'Last saved')}: {new Date(currentDraft.last_saved_at).toLocaleTimeString()}
              </Text>
            )}
          </div>
        </Space>
        <Button 
          icon={<SaveOutlined />} 
          onClick={handleSaveDraft}
        >
          {t('projects.wizard.save_draft', 'Save Draft')}
        </Button>
      </div>

      {/* Steps */}
      <Card className="steps-card">
        <Steps 
          current={currentStep} 
          items={steps}
          responsive
        />
      </Card>

      {/* Content */}
      <Card className="content-card">
        <Spin spinning={createProject.isPending}>
          {currentStep === 0 && (
            <BasicInfoStep
              form={basicForm}
              draft={currentDraft}
              onValuesChange={handleValuesChange}
            />
          )}
          {currentStep === 1 && (
            <AnalysisSettingsStep
              form={analysisForm}
              draft={currentDraft}
              onValuesChange={handleValuesChange}
            />
          )}
          {currentStep === 2 && (
            <NotificationSettingsStep
              form={notificationForm}
              draft={currentDraft}
              onValuesChange={handleValuesChange}
            />
          )}
          {currentStep === 3 && (
            <ReviewStep draft={currentDraft} />
          )}
        </Spin>
      </Card>

      {/* Navigation */}
      <Card className="navigation-card">
        <Row justify="space-between">
          <Col>
            {currentStep > 0 && (
              <Button 
                icon={<ArrowLeftOutlined />} 
                onClick={handlePrevious}
                disabled={createProject.isPending}
              >
                {t('common.previous', 'Previous')}
              </Button>
            )}
          </Col>
          <Col>
            <Space>
              <Button onClick={handleBack} disabled={createProject.isPending}>
                {t('common.cancel', 'Cancel')}
              </Button>
              {currentStep < 3 ? (
                <Button 
                  type="primary" 
                  icon={<ArrowRightOutlined />}
                  onClick={handleNext}
                  disabled={createProject.isPending}
                >
                  {t('common.next', 'Next')}
                </Button>
              ) : (
                <Button 
                  type="primary" 
                  icon={<CheckCircleOutlined />}
                  onClick={handleCreate}
                  loading={createProject.isPending}
                >
                  {t('projects.wizard.create', 'Create Project')}
                </Button>
              )}
            </Space>
          </Col>
        </Row>
      </Card>

      {/* Exit Confirmation Modal */}
      <Modal
        title={t('projects.wizard.exit_title', 'Save your progress?')}
        open={showExitModal}
        onCancel={() => setShowExitModal(false)}
        footer={[
          <Button key="discard" danger onClick={() => confirmExit(false)}>
            {t('projects.wizard.discard', 'Discard')}
          </Button>,
          <Button key="save" type="primary" onClick={() => confirmExit(true)}>
            {t('projects.wizard.save_and_exit', 'Save & Exit')}
          </Button>,
        ]}
      >
        <p>{t('projects.wizard.exit_message', 'You have unsaved changes. Would you like to save your draft before leaving?')}</p>
      </Modal>
    </div>
  );
};

export default NewProject;

/**
 * Projects List Page
 * 
 * Displays all user projects with filtering, sorting, search, and pagination.
 * Supports both table and card view modes.
 */

import React, { useState, useCallback, useMemo, memo } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Card,
  Table,
  Button,
  Space,
  Typography,
  Tag,
  Input,
  Select,
  Modal,
  Dropdown,
  Badge,
  Avatar,
  Tooltip,
  Empty,
  Row,
  Col,
  Statistic,
  Segmented,
  Skeleton,
  Alert,
} from 'antd';
import type { MenuProps, TableProps } from 'antd';
import {
  PlusOutlined,
  SearchOutlined,
  ReloadOutlined,
  MoreOutlined,
  FolderOutlined,
  CodeOutlined,
  ClockCircleOutlined,
  PlayCircleOutlined,
  EditOutlined,
  DeleteOutlined,
  SettingOutlined,
  AppstoreOutlined,
  UnorderedListOutlined,
  ExportOutlined,
  InboxOutlined,
  SortAscendingOutlined,
  SortDescendingOutlined,
  FilterOutlined,
  CheckCircleOutlined,
  PauseCircleOutlined,
  StopOutlined,
  BranchesOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import { useProjectStore, type Project, type ProjectStatus, type ViewMode } from '../../store/projectStore';
import { useProjects, useDeleteProject, useArchiveProject, useRestoreProject } from '../../hooks/useProjects';
import './ProjectList.css';

const { Title, Text, Paragraph } = Typography;
const { Search } = Input;

/** Language colors (GitHub-style) */
const languageColors: Record<string, string> = {
  python: '#3572A5',
  javascript: '#f1e05a',
  typescript: '#2b7489',
  java: '#b07219',
  go: '#00ADD8',
  rust: '#dea584',
  cpp: '#f34b7d',
  c: '#555555',
  csharp: '#178600',
  ruby: '#701516',
  php: '#4F5D95',
  swift: '#ffac45',
  kotlin: '#F18E33',
  scala: '#c22d40',
};

/** Status badge configuration */
const statusConfig: Record<ProjectStatus, { color: string; icon: React.ReactNode; text: string }> = {
  active: { color: 'processing', icon: <CheckCircleOutlined />, text: 'Active' },
  inactive: { color: 'default', icon: <PauseCircleOutlined />, text: 'Inactive' },
  archived: { color: 'warning', icon: <InboxOutlined />, text: 'Archived' },
};

/** Project Card Component */
const ProjectCard = memo(({ 
  project, 
  onView, 
  onSettings, 
  onAnalyze,
  onDelete,
  onArchive,
}: {
  project: Project;
  onView: (id: string) => void;
  onSettings: (id: string) => void;
  onAnalyze: (id: string) => void;
  onDelete: (project: Project) => void;
  onArchive: (project: Project) => void;
}) => {
  const { t } = useTranslation();
  const status = statusConfig[project.status] || statusConfig.active;
  
  return (
    <Card 
      className="project-card"
      hoverable
      actions={[
        <Tooltip title={t('projects.analyze', 'Analyze')} key="analyze">
          <Button type="text" icon={<PlayCircleOutlined />} onClick={() => onAnalyze(project.id)} />
        </Tooltip>,
        <Tooltip title={t('projects.settings', 'Settings')} key="settings">
          <Button type="text" icon={<SettingOutlined />} onClick={() => onSettings(project.id)} />
        </Tooltip>,
        <Dropdown
          key="more"
          menu={{
            items: [
              {
                key: 'archive',
                icon: <InboxOutlined />,
                label: project.status === 'archived' 
                  ? t('projects.restore', 'Restore') 
                  : t('projects.archive', 'Archive'),
                onClick: () => onArchive(project),
              },
              { type: 'divider' },
              {
                key: 'delete',
                icon: <DeleteOutlined />,
                label: t('common.delete', 'Delete'),
                danger: true,
                onClick: () => onDelete(project),
              },
            ],
          }}
          trigger={['click']}
        >
          <Button type="text" icon={<MoreOutlined />} />
        </Dropdown>,
      ]}
    >
      <Card.Meta
        avatar={
          <Avatar 
            shape="square" 
            size={48}
            style={{ backgroundColor: languageColors[project.language] || '#666' }}
            icon={<FolderOutlined />}
          />
        }
        title={
          <Space>
            <Text 
              strong 
              style={{ cursor: 'pointer' }} 
              onClick={() => onView(project.id)}
            >
              {project.name}
            </Text>
            <Badge status={status.color as any} />
          </Space>
        }
        description={
          <Paragraph 
            ellipsis={{ rows: 2 }} 
            type="secondary"
            style={{ marginBottom: 8 }}
          >
            {project.description || t('projects.no_description', 'No description')}
          </Paragraph>
        }
      />
      <div className="project-card-footer">
        <Space size="small" wrap>
          <Tag color={languageColors[project.language] || 'default'}>
            {project.language}
          </Tag>
          {project.framework && <Tag>{project.framework}</Tag>}
        </Space>
        <div className="project-card-meta">
          <Space>
            <ClockCircleOutlined />
            <Text type="secondary" style={{ fontSize: 12 }}>
              {new Date(project.updated_at).toLocaleDateString()}
            </Text>
          </Space>
        </div>
      </div>
      {project.stats && (
        <div className="project-card-stats">
          <Statistic 
            title={t('projects.analyses', 'Analyses')} 
            value={project.stats.total_analyses} 
            valueStyle={{ fontSize: 14 }}
          />
          <Statistic 
            title={t('projects.issues', 'Issues')} 
            value={project.stats.total_issues - project.stats.resolved_issues} 
            valueStyle={{ fontSize: 14, color: project.stats.total_issues - project.stats.resolved_issues > 0 ? '#faad14' : '#52c41a' }}
          />
        </div>
      )}
    </Card>
  );
});

ProjectCard.displayName = 'ProjectCard';

/**
 * Projects List Page Component
 */
export const ProjectList: React.FC = () => {
  const { t } = useTranslation();
  const navigate = useNavigate();
  
  // Store state
  const { 
    viewMode, 
    filters, 
    pagination,
    setViewMode,
    setFilters,
    setPagination,
    resetFilters,
  } = useProjectStore();
  
  // Local state for delete confirmation
  const [deleteModalVisible, setDeleteModalVisible] = useState(false);
  const [projectToDelete, setProjectToDelete] = useState<Project | null>(null);
  
  // Query hooks
  const { data, isLoading, isError, error, refetch, isFetching } = useProjects(filters, pagination);
  const deleteProject = useDeleteProject();
  const archiveProject = useArchiveProject();
  const restoreProject = useRestoreProject();
  
  // Computed values
  const projects = data?.items || [];
  const total = data?.total || 0;
  
  // Get unique languages for filter
  const availableLanguages = useMemo(() => {
    const langs = [...new Set(projects.map(p => p.language))];
    return langs.sort();
  }, [projects]);
  
  // Handlers
  const handleSearch = useCallback((value: string) => {
    setFilters({ search: value });
  }, [setFilters]);
  
  const handleStatusFilter = useCallback((value: string) => {
    setFilters({ status: value as ProjectStatus | 'all' });
  }, [setFilters]);
  
  const handleLanguageFilter = useCallback((value: string) => {
    setFilters({ language: value });
  }, [setFilters]);
  
  const handleSortChange = useCallback((field: string) => {
    const newOrder = filters.sortField === field && filters.sortOrder === 'asc' ? 'desc' : 'asc';
    setFilters({ sortField: field as any, sortOrder: newOrder });
  }, [filters.sortField, filters.sortOrder, setFilters]);
  
  const handlePageChange = useCallback((page: number, pageSize: number) => {
    setPagination({ page, pageSize });
  }, [setPagination]);
  
  const handleView = useCallback((id: string) => {
    navigate(`/review/${id}`);
  }, [navigate]);
  
  const handleSettings = useCallback((id: string) => {
    navigate(`/projects/${id}/settings`);
  }, [navigate]);
  
  const handleAnalyze = useCallback((id: string) => {
    navigate(`/review/${id}`);
  }, [navigate]);
  
  const handleDelete = useCallback((project: Project) => {
    setProjectToDelete(project);
    setDeleteModalVisible(true);
  }, []);
  
  const confirmDelete = useCallback(() => {
    if (projectToDelete) {
      deleteProject.mutate(projectToDelete.id);
      setDeleteModalVisible(false);
      setProjectToDelete(null);
    }
  }, [projectToDelete, deleteProject]);
  
  const handleArchive = useCallback((project: Project) => {
    if (project.status === 'archived') {
      restoreProject.mutate(project.id);
    } else {
      archiveProject.mutate(project.id);
    }
  }, [archiveProject, restoreProject]);
  
  // Project action menu for table
  const getProjectActions = useCallback((project: Project): MenuProps['items'] => [
    {
      key: 'analyze',
      icon: <PlayCircleOutlined />,
      label: t('projects.analyze', 'Analyze'),
      onClick: () => handleAnalyze(project.id),
    },
    {
      key: 'settings',
      icon: <SettingOutlined />,
      label: t('projects.settings', 'Settings'),
      onClick: () => handleSettings(project.id),
    },
    { type: 'divider' },
    {
      key: 'edit',
      icon: <EditOutlined />,
      label: t('common.edit', 'Edit'),
      onClick: () => handleSettings(project.id),
    },
    {
      key: 'archive',
      icon: <InboxOutlined />,
      label: project.status === 'archived' 
        ? t('projects.restore', 'Restore')
        : t('projects.archive', 'Archive'),
      onClick: () => handleArchive(project),
    },
    { type: 'divider' },
    {
      key: 'delete',
      icon: <DeleteOutlined />,
      label: t('common.delete', 'Delete'),
      danger: true,
      onClick: () => handleDelete(project),
    },
  ], [t, handleAnalyze, handleSettings, handleArchive, handleDelete]);
  
  // Table columns
  const columns: TableProps<Project>['columns'] = [
    {
      title: t('projects.name', 'Project'),
      dataIndex: 'name',
      key: 'name',
      sorter: true,
      sortOrder: filters.sortField === 'name' ? (filters.sortOrder === 'asc' ? 'ascend' : 'descend') : null,
      render: (name: string, record: Project) => (
        <Space>
          <Avatar
            shape="square"
            size="small"
            style={{ backgroundColor: languageColors[record.language] || '#666' }}
            icon={<FolderOutlined />}
          />
          <div>
            <Text 
              strong 
              style={{ cursor: 'pointer' }} 
              onClick={() => handleView(record.id)}
            >
              {name}
            </Text>
            {record.description && (
              <Paragraph 
                type="secondary" 
                ellipsis 
                style={{ margin: 0, fontSize: 12 }}
              >
                {record.description}
              </Paragraph>
            )}
          </div>
        </Space>
      ),
    },
    {
      title: t('projects.language', 'Language'),
      dataIndex: 'language',
      key: 'language',
      width: 120,
      render: (lang: string) => (
        <Tag color={languageColors[lang] || 'default'}>
          {lang}
        </Tag>
      ),
    },
    {
      title: t('projects.status', 'Status'),
      dataIndex: 'status',
      key: 'status',
      width: 100,
      render: (status: ProjectStatus) => {
        const config = statusConfig[status] || statusConfig.active;
        return (
          <Badge 
            status={config.color as any} 
            text={config.text}
          />
        );
      },
    },
    {
      title: t('projects.created', 'Created'),
      dataIndex: 'created_at',
      key: 'created_at',
      width: 120,
      sorter: true,
      sortOrder: filters.sortField === 'created_at' ? (filters.sortOrder === 'asc' ? 'ascend' : 'descend') : null,
      render: (date: string) => (
        <Tooltip title={new Date(date).toLocaleString()}>
          {new Date(date).toLocaleDateString()}
        </Tooltip>
      ),
    },
    {
      title: t('projects.last_analyzed', 'Last Analyzed'),
      dataIndex: 'last_analyzed_at',
      key: 'last_analyzed_at',
      width: 130,
      sorter: true,
      sortOrder: filters.sortField === 'last_analyzed_at' ? (filters.sortOrder === 'asc' ? 'ascend' : 'descend') : null,
      render: (date: string) => date ? (
        <Tooltip title={new Date(date).toLocaleString()}>
          <Space>
            <ClockCircleOutlined />
            {new Date(date).toLocaleDateString()}
          </Space>
        </Tooltip>
      ) : (
        <Text type="secondary">-</Text>
      ),
    },
    {
      title: '',
      key: 'actions',
      width: 50,
      fixed: 'right',
      render: (_: unknown, record: Project) => (
        <Dropdown menu={{ items: getProjectActions(record) }} trigger={['click']}>
          <Button type="text" icon={<MoreOutlined />} />
        </Dropdown>
      ),
    },
  ];
  
  // Table change handler
  const handleTableChange: TableProps<Project>['onChange'] = (_, __, sorter) => {
    if (!Array.isArray(sorter) && sorter.field) {
      const field = sorter.field as string;
      const order = sorter.order === 'ascend' ? 'asc' : 'desc';
      setFilters({ sortField: field as any, sortOrder: order });
    }
  };
  
  // Calculate stats
  const stats = useMemo(() => ({
    total: total,
    active: projects.filter(p => p.status === 'active').length,
    recentAnalyses: projects.filter(p => {
      if (!p.last_analyzed_at) return false;
      const weekAgo = Date.now() - 7 * 24 * 60 * 60 * 1000;
      return new Date(p.last_analyzed_at).getTime() > weekAgo;
    }).length,
  }), [projects, total]);
  
  return (
    <div className="project-list-container" role="main" aria-label={t('projects.title', 'Projects')}>
      {/* Header */}
      <div className="project-list-header">
        <Row justify="space-between" align="middle">
          <Col>
            <Title level={2} style={{ margin: 0 }}>
              {t('projects.title', 'Projects')}
            </Title>
            <Text type="secondary">
              {t('projects.subtitle', 'Manage your code review projects')}
            </Text>
          </Col>
          <Col>
            <Button
              type="primary"
              icon={<PlusOutlined />}
              onClick={() => navigate('/projects/new')}
              size="large"
            >
              {t('projects.new', 'New Project')}
            </Button>
          </Col>
        </Row>
      </div>

      {/* Stats */}
      <Row gutter={16} className="project-stats">
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title={t('projects.total', 'Total Projects')}
              value={stats.total}
              prefix={<FolderOutlined />}
              loading={isLoading}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title={t('projects.active', 'Active Projects')}
              value={stats.active}
              prefix={<CheckCircleOutlined />}
              valueStyle={{ color: '#3f8600' }}
              loading={isLoading}
            />
          </Card>
        </Col>
        <Col xs={24} sm={8}>
          <Card>
            <Statistic
              title={t('projects.recent_analyses', 'Recent Analyses')}
              value={stats.recentAnalyses}
              prefix={<PlayCircleOutlined />}
              suffix={t('projects.this_week', 'this week')}
              loading={isLoading}
            />
          </Card>
        </Col>
      </Row>

      {/* Filters */}
      <Card className="project-filters">
        <Row gutter={[16, 16]} align="middle">
          <Col xs={24} sm={12} md={6}>
            <Search
              placeholder={t('projects.search', 'Search projects...')}
              prefix={<SearchOutlined />}
              value={filters.search}
              onChange={(e) => handleSearch(e.target.value)}
              allowClear
              aria-label={t('projects.search', 'Search projects')}
            />
          </Col>
          <Col xs={12} sm={6} md={4}>
            <Select
              style={{ width: '100%' }}
              placeholder={t('projects.filter_status', 'Status')}
              value={filters.status}
              onChange={handleStatusFilter}
              options={[
                { value: 'all', label: t('projects.all_status', 'All Status') },
                { value: 'active', label: t('projects.status_active', 'Active') },
                { value: 'inactive', label: t('projects.status_inactive', 'Inactive') },
                { value: 'archived', label: t('projects.status_archived', 'Archived') },
              ]}
              aria-label={t('projects.filter_status', 'Filter by status')}
            />
          </Col>
          <Col xs={12} sm={6} md={4}>
            <Select
              style={{ width: '100%' }}
              placeholder={t('projects.filter_language', 'Language')}
              value={filters.language}
              onChange={handleLanguageFilter}
              options={[
                { value: 'all', label: t('projects.all_languages', 'All Languages') },
                ...availableLanguages.map(lang => ({ value: lang, label: lang })),
              ]}
              aria-label={t('projects.filter_language', 'Filter by language')}
            />
          </Col>
          <Col flex="auto">
            <Row justify="end" gutter={8}>
              <Col>
                <Tooltip title={t('projects.reset_filters', 'Reset filters')}>
                  <Button 
                    icon={<FilterOutlined />} 
                    onClick={resetFilters}
                    disabled={filters.search === '' && filters.status === 'all' && filters.language === 'all'}
                  >
                    {t('projects.reset', 'Reset')}
                  </Button>
                </Tooltip>
              </Col>
              <Col>
                <Tooltip title={t('common.refresh', 'Refresh')}>
                  <Button 
                    icon={<ReloadOutlined spin={isFetching} />} 
                    onClick={() => refetch()}
                    loading={isFetching}
                  />
                </Tooltip>
              </Col>
              <Col>
                <Segmented
                  value={viewMode}
                  onChange={(value) => setViewMode(value as ViewMode)}
                  options={[
                    { value: 'table', icon: <UnorderedListOutlined />, label: '' },
                    { value: 'card', icon: <AppstoreOutlined />, label: '' },
                  ]}
                  aria-label={t('projects.view_mode', 'View mode')}
                />
              </Col>
            </Row>
          </Col>
        </Row>
      </Card>

      {/* Error State */}
      {isError && (
        <Alert
          message={t('projects.error_loading', 'Error loading projects')}
          description={(error as Error)?.message || t('common.unknown_error', 'An unknown error occurred')}
          type="error"
          showIcon
          action={
            <Button size="small" onClick={() => refetch()}>
              {t('common.retry', 'Retry')}
            </Button>
          }
          style={{ marginBottom: 16 }}
        />
      )}

      {/* Projects List */}
      <Card className="project-content">
        {isLoading ? (
          <Skeleton active paragraph={{ rows: 10 }} />
        ) : projects.length === 0 ? (
          <Empty
            image={Empty.PRESENTED_IMAGE_SIMPLE}
            description={
              filters.search || filters.status !== 'all' || filters.language !== 'all'
                ? t('projects.no_results', 'No projects match your filters')
                : t('projects.no_projects', 'No projects yet')
            }
          >
            {!filters.search && filters.status === 'all' && filters.language === 'all' && (
              <Button type="primary" onClick={() => navigate('/projects/new')}>
                {t('projects.create_first', 'Create your first project')}
              </Button>
            )}
          </Empty>
        ) : viewMode === 'table' ? (
          <Table
            columns={columns}
            dataSource={projects}
            rowKey="id"
            loading={isFetching}
            onChange={handleTableChange}
            pagination={{
              current: pagination.page,
              pageSize: pagination.pageSize,
              total: total,
              showSizeChanger: true,
              showTotal: (total) => t('projects.showing', `Showing ${total} projects`),
              onChange: handlePageChange,
            }}
            scroll={{ x: 800 }}
          />
        ) : (
          <>
            <Row gutter={[16, 16]}>
              {projects.map((project) => (
                <Col key={project.id} xs={24} sm={12} lg={8} xl={6}>
                  <ProjectCard
                    project={project}
                    onView={handleView}
                    onSettings={handleSettings}
                    onAnalyze={handleAnalyze}
                    onDelete={handleDelete}
                    onArchive={handleArchive}
                  />
                </Col>
              ))}
            </Row>
            <Row justify="center" style={{ marginTop: 24 }}>
              <Col>
                <Table
                  dataSource={[]}
                  columns={[]}
                  pagination={{
                    current: pagination.page,
                    pageSize: pagination.pageSize,
                    total: total,
                    showSizeChanger: true,
                    showTotal: (total) => t('projects.showing', `Showing ${total} projects`),
                    onChange: handlePageChange,
                  }}
                />
              </Col>
            </Row>
          </>
        )}
      </Card>

      {/* Delete Confirmation Modal */}
      <Modal
        title={t('projects.delete_confirm', 'Delete Project?')}
        open={deleteModalVisible}
        onCancel={() => setDeleteModalVisible(false)}
        onOk={confirmDelete}
        okText={t('common.delete', 'Delete')}
        okType="danger"
        cancelText={t('common.cancel', 'Cancel')}
        confirmLoading={deleteProject.isPending}
      >
        <p>
          {t(
            'projects.delete_warning',
            `Are you sure you want to delete "${projectToDelete?.name}"? This action cannot be undone.`
          )}
        </p>
      </Modal>
    </div>
  );
};

export default ProjectList;

/**
 * Project Settings Page
 * 
 * Comprehensive settings page for managing project configuration including:
 * - Project information
 * - Analysis settings
 * - Team members
 * - Webhooks
 * - API keys
 * - Notification preferences
 * - Danger zone (archive/delete)
 */

import React, { useState, useCallback, useEffect, useMemo } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import {
  Card,
  Tabs,
  Form,
  Input,
  Select,
  Button,
  Space,
  Typography,
  Row,
  Col,
  Switch,
  InputNumber,
  Table,
  Tag,
  Modal,
  Popconfirm,
  Avatar,
  Skeleton,
  Alert,
  Tooltip,
  Divider,
  Timeline,
  Badge,
  message,
  Descriptions,
  Empty,
  List,
  Spin,
  Collapse,
} from 'antd';
import type { TabsProps, TableProps } from 'antd';
import {
  ArrowLeftOutlined,
  SaveOutlined,
  UserOutlined,
  SettingOutlined,
  BellOutlined,
  LinkOutlined,
  KeyOutlined,
  DeleteOutlined,
  PlusOutlined,
  EditOutlined,
  CopyOutlined,
  ReloadOutlined,
  ExclamationCircleOutlined,
  CheckCircleOutlined,
  CloseCircleOutlined,
  HistoryOutlined,
  WarningOutlined,
  InfoCircleOutlined,
  TeamOutlined,
  ApiOutlined,
  InboxOutlined,
  SendOutlined,
  EyeOutlined,
  EyeInvisibleOutlined,
} from '@ant-design/icons';
import { useTranslation } from 'react-i18next';
import { useProjectStore, type TeamMember, type Webhook, type APIKey, type ActivityLog } from '../../store/projectStore';
import {
  useProject,
  useProjectActivity,
  useProjectTeam,
  useProjectWebhooks,
  useProjectApiKeys,
  useUpdateProject,
  useDeleteProject,
  useArchiveProject,
  useInviteTeamMember,
  useUpdateMemberRole,
  useRemoveTeamMember,
  useCreateWebhook,
  useUpdateWebhook,
  useDeleteWebhook,
  useTestWebhook,
  useCreateApiKey,
  useRevokeApiKey,
} from '../../hooks/useProjects';
import './ProjectSettings.css';

const { Title, Text, Paragraph } = Typography;
const { TextArea } = Input;
const { Panel } = Collapse;

/** Role options */
const roleOptions = [
  { value: 'owner', label: 'Owner', color: 'gold' },
  { value: 'admin', label: 'Admin', color: 'purple' },
  { value: 'member', label: 'Member', color: 'blue' },
  { value: 'viewer', label: 'Viewer', color: 'default' },
];

/** Webhook event options */
const webhookEventOptions = [
  { value: 'analysis.started', label: 'Analysis Started' },
  { value: 'analysis.completed', label: 'Analysis Completed' },
  { value: 'analysis.failed', label: 'Analysis Failed' },
  { value: 'issue.created', label: 'Issue Created' },
  { value: 'issue.resolved', label: 'Issue Resolved' },
];

/** API permission options */
const apiPermissionOptions = [
  { value: 'read:project', label: 'Read Project' },
  { value: 'write:project', label: 'Write Project' },
  { value: 'read:analysis', label: 'Read Analysis' },
  { value: 'write:analysis', label: 'Trigger Analysis' },
  { value: 'read:issues', label: 'Read Issues' },
  { value: 'write:issues', label: 'Manage Issues' },
];

/**
 * Project Information Section
 */
const ProjectInfoSection: React.FC<{
  project: any;
  onSave: (values: any) => void;
  isSaving: boolean;
}> = ({ project, onSave, isSaving }) => {
  const { t } = useTranslation();
  const [form] = Form.useForm();
  const { setUnsavedChanges } = useProjectStore();

  useEffect(() => {
    if (project) {
      form.setFieldsValue({
        name: project.name,
        description: project.description,
        repository_url: project.repository_url,
      });
    }
  }, [project, form]);

  return (
    <Form
      form={form}
      layout="vertical"
      onFinish={onSave}
      onValuesChange={() => setUnsavedChanges(true)}
    >
      <Row gutter={16}>
        <Col xs={24} md={12}>
          <Form.Item
            name="name"
            label={t('projects.form.name', 'Project Name')}
            rules={[{ required: true, message: t('projects.form.name_required', 'Required') }]}
          >
            <Input prefix={<SettingOutlined />} />
          </Form.Item>
        </Col>
        <Col xs={24} md={12}>
          <Form.Item
            name="repository_url"
            label={t('projects.form.repo', 'Repository URL')}
          >
            <Input prefix={<LinkOutlined />} />
          </Form.Item>
        </Col>
      </Row>

      <Form.Item
        name="description"
        label={t('projects.form.description', 'Description')}
      >
        <TextArea rows={3} maxLength={500} showCount />
      </Form.Item>

      <Descriptions bordered size="small" column={{ xs: 1, sm: 2 }}>
        <Descriptions.Item label={t('projects.settings.owner', 'Owner')}>
          {project?.owner_name || '-'}
        </Descriptions.Item>
        <Descriptions.Item label={t('projects.settings.created', 'Created')}>
          {project?.created_at ? new Date(project.created_at).toLocaleDateString() : '-'}
        </Descriptions.Item>
        <Descriptions.Item label={t('projects.settings.language', 'Language')}>
          <Tag>{project?.language}</Tag>
        </Descriptions.Item>
        <Descriptions.Item label={t('projects.settings.status', 'Status')}>
          <Badge status={project?.status === 'active' ? 'processing' : 'default'} text={project?.status} />
        </Descriptions.Item>
      </Descriptions>

      <Divider />

      <Form.Item>
        <Button type="primary" htmlType="submit" icon={<SaveOutlined />} loading={isSaving}>
          {t('common.save', 'Save Changes')}
        </Button>
      </Form.Item>
    </Form>
  );
};

/**
 * Team Management Section
 */
const TeamSection: React.FC<{ projectId: string }> = ({ projectId }) => {
  const { t } = useTranslation();
  const [inviteModalOpen, setInviteModalOpen] = useState(false);
  const [inviteForm] = Form.useForm();

  const { data: team, isLoading } = useProjectTeam(projectId);
  const inviteMember = useInviteTeamMember(projectId);
  const updateRole = useUpdateMemberRole(projectId);
  const removeMember = useRemoveTeamMember(projectId);

  const handleInvite = useCallback(async (values: { email: string; role: string }) => {
    await inviteMember.mutateAsync(values);
    setInviteModalOpen(false);
    inviteForm.resetFields();
  }, [inviteMember, inviteForm]);

  const columns: TableProps<TeamMember>['columns'] = [
    {
      title: t('projects.team.member', 'Member'),
      key: 'member',
      render: (_, record) => (
        <Space>
          <Avatar src={record.avatar} icon={<UserOutlined />} />
          <div>
            <Text strong>{record.name}</Text>
            <br />
            <Text type="secondary" style={{ fontSize: 12 }}>{record.email}</Text>
          </div>
        </Space>
      ),
    },
    {
      title: t('projects.team.role', 'Role'),
      dataIndex: 'role',
      key: 'role',
      render: (role: string, record) => (
        <Select
          value={role}
          onChange={(newRole) => updateRole.mutate({ memberId: record.id, role: newRole })}
          options={roleOptions}
          disabled={role === 'owner'}
          style={{ width: 120 }}
        />
      ),
    },
    {
      title: t('projects.team.joined', 'Joined'),
      dataIndex: 'accepted_at',
      key: 'joined',
      render: (date: string) => date ? new Date(date).toLocaleDateString() : 
        <Tag color="orange">{t('projects.team.pending', 'Pending')}</Tag>,
    },
    {
      title: '',
      key: 'actions',
      width: 60,
      render: (_, record) => (
        record.role !== 'owner' && (
          <Popconfirm
            title={t('projects.team.remove_confirm', 'Remove this member?')}
            onConfirm={() => removeMember.mutate(record.id)}
            okText={t('common.yes', 'Yes')}
            cancelText={t('common.no', 'No')}
          >
            <Button type="text" danger icon={<DeleteOutlined />} />
          </Popconfirm>
        )
      ),
    },
  ];

  return (
    <div>
      <Row justify="space-between" align="middle" style={{ marginBottom: 16 }}>
        <Col>
          <Text strong>{t('projects.team.title', 'Team Members')}</Text>
        </Col>
        <Col>
          <Button type="primary" icon={<PlusOutlined />} onClick={() => setInviteModalOpen(true)}>
            {t('projects.team.invite', 'Invite Member')}
          </Button>
        </Col>
      </Row>

      <Table
        columns={columns}
        dataSource={team || []}
        rowKey="id"
        loading={isLoading}
        pagination={false}
        locale={{ emptyText: <Empty description={t('projects.team.no_members', 'No team members yet')} /> }}
      />

      <Modal
        title={t('projects.team.invite_title', 'Invite Team Member')}
        open={inviteModalOpen}
        onCancel={() => setInviteModalOpen(false)}
        footer={null}
      >
        <Form form={inviteForm} layout="vertical" onFinish={handleInvite}>
          <Form.Item
            name="email"
            label={t('projects.team.email', 'Email Address')}
            rules={[
              { required: true, message: t('common.required', 'Required') },
              { type: 'email', message: t('common.invalid_email', 'Invalid email') },
            ]}
          >
            <Input prefix={<UserOutlined />} placeholder="user@example.com" />
          </Form.Item>
          <Form.Item
            name="role"
            label={t('projects.team.role', 'Role')}
            initialValue="member"
          >
            <Select options={roleOptions.filter(r => r.value !== 'owner')} />
          </Form.Item>
          <Form.Item>
            <Space>
              <Button onClick={() => setInviteModalOpen(false)}>
                {t('common.cancel', 'Cancel')}
              </Button>
              <Button type="primary" htmlType="submit" loading={inviteMember.isPending}>
                {t('projects.team.send_invite', 'Send Invite')}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

/**
 * Webhooks Section
 */
const WebhooksSection: React.FC<{ projectId: string }> = ({ projectId }) => {
  const { t } = useTranslation();
  const [modalOpen, setModalOpen] = useState(false);
  const [editingWebhook, setEditingWebhook] = useState<Webhook | null>(null);
  const [form] = Form.useForm();

  const { data: webhooks, isLoading } = useProjectWebhooks(projectId);
  const createWebhook = useCreateWebhook(projectId);
  const updateWebhook = useUpdateWebhook(projectId);
  const deleteWebhook = useDeleteWebhook(projectId);
  const testWebhook = useTestWebhook(projectId);

  const handleSubmit = useCallback(async (values: any) => {
    if (editingWebhook) {
      await updateWebhook.mutateAsync({ webhookId: editingWebhook.id, data: values });
    } else {
      await createWebhook.mutateAsync(values);
    }
    setModalOpen(false);
    setEditingWebhook(null);
    form.resetFields();
  }, [editingWebhook, createWebhook, updateWebhook, form]);

  const handleEdit = useCallback((webhook: Webhook) => {
    setEditingWebhook(webhook);
    form.setFieldsValue(webhook);
    setModalOpen(true);
  }, [form]);

  const columns: TableProps<Webhook>['columns'] = [
    {
      title: t('projects.webhooks.url', 'URL'),
      dataIndex: 'url',
      key: 'url',
      ellipsis: true,
      render: (url: string) => (
        <Tooltip title={url}>
          <Text copyable={{ text: url }}>{url}</Text>
        </Tooltip>
      ),
    },
    {
      title: t('projects.webhooks.events', 'Events'),
      dataIndex: 'events',
      key: 'events',
      render: (events: string[]) => (
        <Space wrap>
          {events.slice(0, 2).map(e => <Tag key={e}>{e}</Tag>)}
          {events.length > 2 && <Tag>+{events.length - 2}</Tag>}
        </Space>
      ),
    },
    {
      title: t('projects.webhooks.status', 'Status'),
      key: 'status',
      render: (_, record) => (
        <Space>
          <Badge status={record.is_active ? 'success' : 'default'} />
          {record.last_status === 'success' ? 
            <CheckCircleOutlined style={{ color: '#52c41a' }} /> :
            record.last_status === 'failure' ?
            <CloseCircleOutlined style={{ color: '#ff4d4f' }} /> : null
          }
        </Space>
      ),
    },
    {
      title: '',
      key: 'actions',
      width: 150,
      render: (_, record) => (
        <Space>
          <Tooltip title={t('projects.webhooks.test', 'Test')}>
            <Button 
              type="text" 
              icon={<SendOutlined />} 
              onClick={() => testWebhook.mutate(record.id)}
              loading={testWebhook.isPending}
            />
          </Tooltip>
          <Tooltip title={t('common.edit', 'Edit')}>
            <Button type="text" icon={<EditOutlined />} onClick={() => handleEdit(record)} />
          </Tooltip>
          <Popconfirm
            title={t('projects.webhooks.delete_confirm', 'Delete this webhook?')}
            onConfirm={() => deleteWebhook.mutate(record.id)}
          >
            <Button type="text" danger icon={<DeleteOutlined />} />
          </Popconfirm>
        </Space>
      ),
    },
  ];

  return (
    <div>
      <Row justify="space-between" align="middle" style={{ marginBottom: 16 }}>
        <Col>
          <Text strong>{t('projects.webhooks.title', 'Webhooks')}</Text>
          <br />
          <Text type="secondary" style={{ fontSize: 12 }}>
            {t('projects.webhooks.description', 'Receive real-time notifications about project events')}
          </Text>
        </Col>
        <Col>
          <Button type="primary" icon={<PlusOutlined />} onClick={() => setModalOpen(true)}>
            {t('projects.webhooks.add', 'Add Webhook')}
          </Button>
        </Col>
      </Row>

      <Table
        columns={columns}
        dataSource={webhooks || []}
        rowKey="id"
        loading={isLoading}
        pagination={false}
        locale={{ emptyText: <Empty description={t('projects.webhooks.no_webhooks', 'No webhooks configured')} /> }}
      />

      <Modal
        title={editingWebhook ? t('projects.webhooks.edit', 'Edit Webhook') : t('projects.webhooks.add', 'Add Webhook')}
        open={modalOpen}
        onCancel={() => { setModalOpen(false); setEditingWebhook(null); form.resetFields(); }}
        footer={null}
        width={600}
      >
        <Form form={form} layout="vertical" onFinish={handleSubmit}>
          <Form.Item
            name="url"
            label={t('projects.webhooks.url', 'Webhook URL')}
            rules={[
              { required: true, message: t('common.required', 'Required') },
              { type: 'url', message: t('common.invalid_url', 'Invalid URL') },
            ]}
          >
            <Input placeholder="https://example.com/webhook" />
          </Form.Item>
          <Form.Item
            name="events"
            label={t('projects.webhooks.events', 'Events')}
            rules={[{ required: true, message: t('common.required', 'Required') }]}
          >
            <Select mode="multiple" options={webhookEventOptions} placeholder={t('projects.webhooks.select_events', 'Select events')} />
          </Form.Item>
          <Form.Item name="secret" label={t('projects.webhooks.secret', 'Secret (optional)')}>
            <Input.Password placeholder="Optional signing secret" />
          </Form.Item>
          {editingWebhook && (
            <Form.Item name="is_active" label={t('projects.webhooks.active', 'Active')} valuePropName="checked">
              <Switch />
            </Form.Item>
          )}
          <Form.Item>
            <Space>
              <Button onClick={() => { setModalOpen(false); setEditingWebhook(null); }}>
                {t('common.cancel', 'Cancel')}
              </Button>
              <Button type="primary" htmlType="submit" loading={createWebhook.isPending || updateWebhook.isPending}>
                {editingWebhook ? t('common.save', 'Save') : t('common.create', 'Create')}
              </Button>
            </Space>
          </Form.Item>
        </Form>
      </Modal>
    </div>
  );
};

/**
 * API Keys Section
 */
const ApiKeysSection: React.FC<{ projectId: string }> = ({ projectId }) => {
  const { t } = useTranslation();
  const [modalOpen, setModalOpen] = useState(false);
  const [newKey, setNewKey] = useState<string | null>(null);
  const [form] = Form.useForm();

  const { data: apiKeys, isLoading } = useProjectApiKeys(projectId);
  const createApiKey = useCreateApiKey(projectId);
  const revokeApiKey = useRevokeApiKey(projectId);

  const handleCreate = useCallback(async (values: any) => {
    const result = await createApiKey.mutateAsync(values);
    setNewKey(result.key);
    form.resetFields();
  }, [createApiKey, form]);

  const handleCopy = useCallback((key: string) => {
    navigator.clipboard.writeText(key);
    message.success(t('common.copied', 'Copied to clipboard'));
  }, [t]);

  const columns: TableProps<APIKey>['columns'] = [
    {
      title: t('projects.api_keys.name', 'Name'),
      dataIndex: 'name',
      key: 'name',
    },
    {
      title: t('projects.api_keys.key', 'Key'),
      dataIndex: 'key_prefix',
      key: 'key',
      render: (prefix: string) => (
        <Text code>{prefix}...</Text>
      ),
    },
    {
      title: t('projects.api_keys.permissions', 'Permissions'),
      dataIndex: 'permissions',
      key: 'permissions',
      render: (perms: string[]) => (
        <Space wrap>
          {perms.slice(0, 2).map(p => <Tag key={p}>{p}</Tag>)}
          {perms.length > 2 && <Tag>+{perms.length - 2}</Tag>}
        </Space>
      ),
    },
    {
      title: t('projects.api_keys.usage', 'Usage'),
      dataIndex: 'usage_count',
      key: 'usage',
      render: (count: number) => count.toLocaleString(),
    },
    {
      title: t('projects.api_keys.last_used', 'Last Used'),
      dataIndex: 'last_used_at',
      key: 'last_used',
      render: (date: string) => date ? new Date(date).toLocaleDateString() : '-',
    },
    {
      title: '',
      key: 'actions',
      width: 80,
      render: (_, record) => (
        <Popconfirm
          title={t('projects.api_keys.revoke_confirm', 'Revoke this API key?')}
          description={t('projects.api_keys.revoke_warning', 'This action cannot be undone.')}
          onConfirm={() => revokeApiKey.mutate(record.id)}
          okText={t('common.revoke', 'Revoke')}
          okType="danger"
        >
          <Button type="text" danger icon={<DeleteOutlined />} />
        </Popconfirm>
      ),
    },
  ];

  return (
    <div>
      <Row justify="space-between" align="middle" style={{ marginBottom: 16 }}>
        <Col>
          <Text strong>{t('projects.api_keys.title', 'API Keys')}</Text>
          <br />
          <Text type="secondary" style={{ fontSize: 12 }}>
            {t('projects.api_keys.description', 'Manage API keys for programmatic access')}
          </Text>
        </Col>
        <Col>
          <Button type="primary" icon={<PlusOutlined />} onClick={() => setModalOpen(true)}>
            {t('projects.api_keys.create', 'Create API Key')}
          </Button>
        </Col>
      </Row>

      <Table
        columns={columns}
        dataSource={apiKeys || []}
        rowKey="id"
        loading={isLoading}
        pagination={false}
        locale={{ emptyText: <Empty description={t('projects.api_keys.no_keys', 'No API keys')} /> }}
      />

      <Modal
        title={t('projects.api_keys.create', 'Create API Key')}
        open={modalOpen}
        onCancel={() => { setModalOpen(false); setNewKey(null); form.resetFields(); }}
        footer={null}
      >
        {newKey ? (
          <div>
            <Alert
              message={t('projects.api_keys.copy_warning', 'Copy your API key now')}
              description={t('projects.api_keys.copy_warning_desc', 'This is the only time you will see this key.')}
              type="warning"
              showIcon
              style={{ marginBottom: 16 }}
            />
            <Input.Group compact style={{ marginBottom: 16 }}>
              <Input value={newKey} readOnly style={{ width: 'calc(100% - 80px)' }} />
              <Button icon={<CopyOutlined />} onClick={() => handleCopy(newKey)}>
                {t('common.copy', 'Copy')}
              </Button>
            </Input.Group>
            <Button type="primary" block onClick={() => { setModalOpen(false); setNewKey(null); }}>
              {t('common.done', 'Done')}
            </Button>
          </div>
        ) : (
          <Form form={form} layout="vertical" onFinish={handleCreate}>
            <Form.Item
              name="name"
              label={t('projects.api_keys.name', 'Key Name')}
              rules={[{ required: true, message: t('common.required', 'Required') }]}
            >
              <Input placeholder="e.g., CI/CD Pipeline" />
            </Form.Item>
            <Form.Item
              name="permissions"
